css:
@-moz-document domain("radio-everypony-ru.chatovod.ru"), domain("tabun-everypony.chatovod.ru") {
body {
  background-color: #252525;
  color: #D8D8D8;
}
.chatMain, .chatEventDiv {
  background: inherit;
}
.chatHeader {
  background: #202020;
  color: #202020;
  height: 35px;
}
.chatFooter {
  background: #202020;
  color: #808080;
}
.chatHeader .chatTitle {
  text-shadow: 0px 0px 0px #202020;
}

.chatHeader .chatTopLine {
  top: 7px;
  background-color: #252525; /*profile&chat btns */
  border-color: #303030
}
/*menu btns text*/
.chatTopLineWrapper > ul > li > a
{
    color: #a67859;
    text-shadow: 1px 1px 0 #252525;
}
/*tabs menu*/
.chatTopLineWrapper ul li ul {
    border-color: #ccc #666 #666 #ccc;
    border-style: solid;
    border-width: 1px;
    background-color: #555;
}
/*tabs menu text*/
.chatTopLineWrapper > ul > li > ul > li > a {
    color: #d99126;
}
.chatMain .chatPeople {
  background: #202020;
}
/*chat people list*/
.chatUserFrom, .chatPeople ul.chatPeopleList li a.chatUser {
    color: #664800;
}
.chatMain .chatMessages {
  top: 0px
}
.chatMain .chatMessages .chatMessage {
  color: #f4ca16
}
.chatMain .chatMessages .chatMessage .chatUserFrom {
  color: #664800
}
.chatTabs {
  top: 0px
}
.chatEventDiv {
  
}
#scrollBottomBox {
  display: none;
}
a {
  color: #D8D8FF;
}
.chatFooter .chatFooterWrapper .chatPanel div a span {
  border-bottom: none;
}
.chatTabs ul {
  position: fixed;
  top: 7px;
  right: 15%;
  z-index: 100;
  left: 1%;
}
.chatHeader .chatTopLine {
  /*color: #000000;*/
  background-color: #252525;
}
/*chat tab inactive*/
.chatTabs > ul > li.chatTab > a
{
    background-color: #303030;
    color:#a67859;
}
/*chat tabs active*/
.chatTabs > ul > li.chatTab.chatTabActive > a, .chatTabs > ul > li.chatTab > a:hover, .chatTabs > ul > li.chatTab > a.active
{
    background-color:#505050;
    color:#ff9a56;
}

#aevents.data a.obl {
  opacity: 0;
  visibility: hidden !important;
  display: none !important;
}
.chat.withEvents #events {
  position: fixed;
  bottom: 32px;
  left: 50%;
  z-index: 100;
}
}
