css:
@-moz-document domain("tvtropes.org") {

/* Wide Load fix - No longer needed */
/* .main-body {padding: 10px;} #header-new {padding: 0 10px;} */

/* Narrower sidebar */  
.sidebar-right {width: 230px;}
.sidebar-item li a {font-size: 0.98em !important;}
.display-options li {width: 50px;}
.display-options ul.selector, .display-options .display-btn ul.selector li a {width: 44px !important; height: 24px !important;}
.display-options .display-btn .is-off.selector li.on {left: -44px;}
.display-options .display-btn ul.selector li a i {font-size: 14px !important; top: 6px;}
.display-options .display-btn ul.selector .off a i {right: 6px !important;}
.selector, .selector li a::before {border-radius: 12px !important;}
.showcase .sidebar-title .sub {margin-right: 0 !important;}
.showcase .sidebar-title .sub a {font-size: 0;} .showcase .sidebar-title .sub a:after {content:"(More)"; font-size: 10px !important;}
.showcase-inner .item a {width: 100%; height: auto !important;}
.sidebar-item.sb-ad-unit {background: none !important; box-shadow: none !important; padding-left: 2px;}
.sidebar-item.sb-ad-unit > .sidebar-section {background: none;} /* Mainly for the sake of sub-wikis */
.square_ad {min-height: 0;} .proper-ad-unit {display: block;}
.sidebar-item.sb-ad-unit .proper-ad-unit  > div > div {display: block !important; height: 190px !important;}
.square_ad iframe {transform: translate(-12.25%, -12.25%)  scale(0.75, 0.75);}

/* Color schemes */
.main-full-width {background-color: #CCDDFF;}
.sidebar-title, .secondary-nav, .social-box {background-color: #336 !important;}
.secondary-nav .watch-page:not(:hover) {background-color: rgba(255,255,255,0.9);}
.banner, .item-title-bar {background-color: #667788 !important;}
.folderlabelopen, .floatboxleft, .floatboxright {background-color: #E5E5E5 !important; color: black !important;}
.footer-top {background-color: #369;}
.footer-left.footer-column::before, .footer-left.footer-column::after,
.footer-mid-right.footer-column::before, .footer-right.footer-column::before {
  background: transparent linear-gradient(to bottom, #369 0%, #666 50%, #369 100%) repeat scroll 0% 0%;
}
.footer-bottom {background-color: #336; border-top: 1px solid #558;}
.item .newmsg {background: #FE6;}

/* Shadows */
.main-left {border-radius: 5px;} .secondary-nav {border-radius: 0;}
.sidebar-item, .main-left {box-shadow: 2px 2px 2px 0px rgba(0,0,0,0.2) !important;}
#header-new {box-shadow: 0px 2px 8px 0px rgba(0,0,0,0.2) !important;}
.main-left .btn,
.forumpagebutton, .forumpagebuttons, .forumbutton,
.main-body-links > li > a,
.folderlabel, .folderlabelopen,
.alphalist > a,
.section-links .section-link-row,
.filter-sort > li > a, .filter-sort > li > div > a,
.post-reply, .voting > li, .toggle-child,
.selector {box-shadow: 2px 2px 2px 0 rgba(0,0,0,0.2) !important;}
.voting li {border-radius: 5px;}
 
/* Rounded buttons */
.btn {border-radius: 2em;}

/* "Code font" in editor and history */
textarea, .article-history .diffadd, .article-history .diffdel {font-family: monospace; white-space: pre-wrap;}
  
/* Fix wrapping issues with page images */
.quoteleft, .quoteright {background: white;}
  
/* Styles for sub-wikis */
@-moz-document regexp("^https?:\\/\\/tvtropes.org\\/pmwiki\\/(pmwiki\\.php\\/|relatedsearch\\.php\\?term=|remarks\\.php\\?trope=|article_history\\.php\\?article=)(DarthWiki|Horrible|NightmareFuel).*") {
  .main-full-width {background-color: #666666;}
  .sidebar-title, .secondary-nav, .social-box {background-color: #850000 !important;} /* Reverting to original colour */
  .secondary-nav .watch-page:not(:hover) {background-color: #660000 !important;}
  a.urllink:link {color: #850000;}
  .main-left > .inner, #header-new, .spoiler, .quoteleft, .quoteright {background-color: #FFFFF0;}
  .sidebar-section {background-color: #FFFFF0; border-radius: 5px;}
  #main-nav a, .breadcrumbs a {font-family: "Special Elite"; color: #850000 !important;}
  #header-new .page-btns li a:hover {background-color: #850000;}
  #header-new .page-btns li a i {color: #993333;}
  .footer-top {background-color: #444 !important;}
  .footer-left.footer-column::before, .footer-left.footer-column::after, .footer-mid-right.footer-column::before, .footer-right.footer-column::before {
    background: transparent linear-gradient(to bottom, #444 0%, #666 50%, #444 100%) repeat scroll 0% 0% !important;
  }
  .footer-bottom {background-color: #333 !important; border-top: 1px solid #555 !important;}
  .page-content li {list-style:outside none;}
  .page-content li:before {content:"?"; color: #633; margin-left: -1em;}
  .page-content li li:before {content:"?"; color: #444;}
  .page-content li li li:before {content:"◉"; color: #666;}
}
@-moz-document regexp("^https?:\\/\\/tvtropes.org\\/pmwiki\\/(pmwiki\\.php\\/|relatedsearch\\.php\\?term=|remarks\\.php\\?trope=|article_history\\.php\\?article=)(SugarWiki|Awesome|Heartwarming).*") {
  .main-full-width {background-color: #FF9999;}
  .sidebar-title, .secondary-nav, .social-box {background-color: #088 !important;} /* Reverting to original colour */
  .secondary-nav .watch-page:not(:hover) {background: #FF9 !important;}
  #main-nav a, .breadcrumbs a {font-family: "Delius Swash Caps";}
  .footer-top {background-color: #088 !important; border-top-color: #007979;}
  .footer-left.footer-column::before, .footer-left.footer-column::after, .footer-mid-right.footer-column::before, .footer-right.footer-column::before {
    background: transparent linear-gradient(to bottom, #088 0%, #0EE 50%, #0EE 50%, #088 100%) repeat scroll 0% 0%;
  }
  .footer-bottom {background-color: #006E6F !important; border-top: medium none !important;}
  .page-content li {list-style:outside none;}
  .page-content li:before {content:"♥"; color: #C33; margin-left: -1em;}
  .page-content li li:before {content:"?"; color: #900;}
  .page-content li li li:before {content:"❥"; color: #600;}
  a:not(:hover) {color: #088;}
}
@-moz-document regexp("^https?:\\/\\/tvtropes.org\\/pmwiki\\/(pmwiki\\.php\\/|relatedsearch\\.php\\?term=|remarks\\.php\\?trope=|article_history\\.php\\?article=)YMMV.*") {
  .main-full-width {background-color: #006633;}
  .sidebar-title, .secondary-nav, .social-box {background-color: #DCA029 !important;} /* Reverting to original colour */
  .sidebar-title {font-size: 0.95em !important;}
  .secondary-nav .watch-page {color: white !important;}
  .footer-top {background-color: #041 !important; border-top-color: #052;}
  .footer-left.footer-column::before, .footer-left.footer-column::after, .footer-mid-right.footer-column::before, .footer-right.footer-column::before {
    background: transparent linear-gradient(to bottom, #041 0%, #063 50%, #063 50%, #041 100%) repeat scroll 0% 0% !important;
  }
  .footer-bottom {background-color: #030 !important; border-top: medium none !important;}
  a:not(:hover) {color: #062;}
}
/* Grey background for editor and preview */
@-moz-document regexp("^https?:\\/\\/tvtropes.org\\/pmwiki\\/(preview|wysiwyg_source_editor)\\.php.*") {
  .main-full-width {background-color: #CCC;}
  .footer-top {background-color: #444 !important;}
  .footer-left.footer-column::before, .footer-left.footer-column::after, .footer-mid-right.footer-column::before, .footer-right.footer-column::before {
    background: transparent linear-gradient(to bottom, #444 0%, #666 50%, #444 100%) repeat scroll 0% 0% !important;
  }
  .footer-bottom {background-color: #333 !important; border-top: 1px solid #555 !important;}
}
  
}
