@namespace url(http://www.w3.org/1999/xhtml);

/****************************************/
/* STYLE CREATED BY: SAMUEL HÉL  © 2016 */
/****************************************/

@-moz-document domain("spsoafm.cz") {
  /* Dark Theme */
  body, head{
    background: url("http://i.imgur.com/poEG69C.jpg") repeat-x scroll -40px 0px !important;
    background-color: black !important;
  }
  .submenu li a, .submenu li span.separator{
    color: white;
  }
  /* partner right */
  .moduletable img{
    background: white;
  }
  #right .moduletable .content-module{
    background-color: transparent;
  }
  /* slideshow */ 
  #top{
    display: none;
  }
  #ais_36_wrapper, #ais_36{
    width: 980px;
    border-radius: 5px;
  }
  /* logo  header */
  fieldset, img{
    border-radius: 5px;
  }
  /* příspěvky */
  #content{
    position: relative;
    overflow: hidden;
    background-color: white;
    border-radius: 20px;
    padding: 8px;
    color: white;
    font-family: Arial !important;
    font-size: 1.1em;
    box-shadow: white 0px 0px 15px;
  }
  #content ol li{
    color: white;
  }
  #content h2{
    color: red;
  }
  #content p{
    color: black;
    font-size: 1.1em;
  }
  /* Left Panel */
  .moduletable h3,h3{
    padding: 6px;
    margin: 1px;
    color: white;
    font-size: 1.4em;
    font-weight: bold;
    font-family: "Calibri";
    border-radius: 2px;
    background-color: RGBA(255, 57, 54, 0.71);
    text-align: center;
  }
  .content-module{
    background: RGBA(55, 57, 54, 0.71);
    border-radius: 2px;
    padding: 4px;
    margin: 2px;
    text-align: center;
  }
  .submenu li a:hover{
    color: yellow;
    background: RGBA(255, 57, 54, 0.71);
    border-radius: 4px;
    text-align: center;
    font-weight: bold;
    transition: opacity 1s;
  }
  .submenu li{
    background: RGBA(55, 57, 54, 0.71);
    border-radius: 3px;
  }
    .submenu li a{
    border-bottom: 10px;
    border-bottom-color: black;
    border-bottom-width: 2px;
    border-bottom-style: solid;
  }
}
