css:
@-moz-document domain("twitter.com") {
@font-face {
    font-family: Shabnam;
    src: url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2);
    src: url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2#iefix) format('embedded-opentype'), url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2) format('woff'), url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2) format('truetype');
    font-weight: normal;
}
/* Persian fixes */
body {
    font-family: Shabnam, "iranian sans", "Helvetica Neue", "Helvetica Neue", Helvetica, Arial, sans-serif;
}
body.fa,
input,
textarea,
.tweet-content,
.tweet-text,
.ProfileTweet-text,
.TweetTextSize.TweetTextSize--26px,
.pretty-link b,
[lang="fa"] {
    font-family: Shabnam, "iranian sans", Tahoma, sans-serif !important;
    font-size: 12px !important;
    line-height: 18px !important;
    font-weight: 100 !important;
    text-decoration: none !important;
    letter-spacing: 0 !important;
}
h1,
h2,
h3,
a,
button,
div.new-tweets-bar,
div.module,
input.submit,
Strong.fullname-rtl,
txt-size--14,
b{
    font-family: "Droid Arabic Naskh", "B Yekan", Shabnam, "iranian sans", tahoma, sans-serif !important;
    text-decoration: none !important;
}
.trends-container .flex-module-inner {
    text-align: left;
    direction: ltr;
}
.twitter-hashtag,
.twitter-atreply {
    direction: rtl !important;
}
/* Visual Improve */

div.tweet {
    cursor: default !important;
}
.js-tooltip b,
.stream-item-header .translate-button {
    display: none;
}
.new-tweets-bar {
    height: 18px;
}
/* Shadows */

.Gallery-content,
#timeline,
.stream-item.open,
.global-nav-inner,
.permalink {
    box-shadow: 0 0 10px black;
}
.module,
.WhoToFollow,
.flex-module {
    background: whitesmoke !important;
}
/* remove headers */

.content-main,
.dashboard,
#timeline {
    margin-top: -10px;
}
.profile-social-proof,
.tweet-user,
.modal-content,
.content-main,
.module,
.stream-item.open,
.permalink .before-expanded,
.after-expanded:not(.sepration-module),
.Gallery--new .Gallery-media,
.Gallery-content,
.stream-items > .stream-item.open:first-child,
.module .flex-module:first-child,
.module .flex-module:last-child,
.module ul:first-child li:first-child .list-link,
.module ul:last-child li:last-child .list-link,
.with-media-forward .cards-base .media-forward,
.header-inner,
.content-header,
.media-thumbnail,
.WhoToFollow,
.ProfileTweet,
.ProfileTweet-avatar,
.avatar,
.ProfileAvatar,
.ProfileAvatar-image,
.before-expanded,
.permalink,
.permalink-tweet {
    border-radius: 0 !important;
}
.home-tweet-box {
    margin-top: 0;
}
.timeline-tweet-box {
    border: 0 !important;
}
.TwitterCard {
    font-size: 14px;
    font-family: Shabnam, "iranian sans", "Helvetica Neue", Helvetica, Arial, sans-serif;
    line-height: 1.3em;
}
.username,
._timestamp {
    font-family: tahoma, sans-serif;
}
}
