css:
@-moz-document domain("mokum.place") {

div.title h1 {
     opacity: 0;
}

    @font-face {
        font-family: Samim;
        src: url('//rastikerdar.github.io/samim-font/Samim.eot?v=0.6.1');
        src: url('//rastikerdar.github.io/samim-font/Samim.eot?v=0.6.1#iefix') format('embedded-opentype'), url('//rastikerdar.github.io/samim-font/Samim.woff?v=0.6.1') format('woff'), url('//rastikerdar.github.io/samim-font/Samim.ttf?v=0.6.1') format('truetype');
        font-weight: normal;
    }
    @font-face {
        font-family: Samim;
        src: url('//rastikerdar.github.io/samim-font/Samim-Bold.eot?v=0.6.1');
        src: url('//rastikerdar.github.io/samim-font/Samim-Bold.eot?v=0.6.1#iefix') format('embedded-opentype'), url('//rastikerdar.github.io/samim-font/Samim-Bold.woff?v=0.6.1') format('woff'), url('//rastikerdar.github.io/samim-font/Samim-Bold.ttf?v=0.6.1') format('truetype');
        font-weight: bold;
    }
    * {
        font-family: Samim;
        font-size: 14px;
    }                               

                   


.header .title{
    width:228px;
    background-image:url('https://mokum.place/system/attachments/000/047/212/mokum-medium-6f9a4e42450714a9293f908da4d07de7cd092bb3.png');
    background-repeat:no-repeat;
        text-indent: 50px;
        white-space: nowrap;
        margin-top: 25px;  
        display: block;
        height: 55px; 
        
}


body {
    background: #9bb4c5 url("https://mokum.place/system/attachments/000/032/204/mokum-medium-3a1f91658b2a42d00ae1d33948525f0be3d1d37d.png") repeat-x scroll center top;
    background-size: auto;
    background-attachment: fixed;
    direction: rtl;
    font-family: Samim;
   }
.content .box-body {
    background-color: #fff!important;
    background-color: #e0e0e0;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}


.timeline-post-container {
    padding: 14px;
}
.post-body .likes .icon {
    float: right;
}
.comments .comment a.date {
    float: right;
}
.sidebar {
    direction: ltr;
}
.timeline-post-container {
    padding: 14px;
    margin: 10px;
}
.sidebar .box-body {
    padding: 10px 10px 3px 0;
}
.controls {
    direction: ltr;
    text-align: right;
}
.box-header,
.box-header-empty,
.box-header-timeline,
.box-header-friends,
.box-header-groups {
    border: none;
}
.sidebar .box-body {
    background-color: #FFFFFF;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}
div.box-header-timeline {
    font-size: 16px;
}
li.previous a,
li.next a {
    background-color: #d1d1d1;
    color: #000000;
}
.box-header-friends {
    background-image: url("https://mokum.place/system/attachments/000/032/205/mokum-medium-e9361bda18b9b9de647529a440b5415f59bba8b8.png");
    font-weight: bold;
    font-size: 15px;
    background: #289628;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 2px;
}
.box-header-groups {
    background-image: url("https://mokum.place/system/attachments/000/032/212/mokum-medium-872e52be1e9a1391df278c5e389f184b219a98d6.png");
    border: none;
    background: #FF8300;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 2px;
}
div.box-header-timeline {
    background-image: url("https://mokum.place/system/attachments/000/032/213/mokum-medium-958a2f4d2154d22e1c2b86fcf3633b9115cca258.png");
    font-size: 16px;
    background: #5393D8;
    color: white;
    padding: 2px;
    direction: ltr;
    border-radius: 10px 10px 0 0;
    margin-left: 0px !important;
}
.timeline-post-container .avatar {
    float: right;
    margin-left: 15px;
}
}
