css:
@-moz-document domain(theregister.co.uk) {

/* Main page background */
  html {background:none repeat scroll 0% 0% #ddd!important}

/* General links   */
  a {color:#00d!important;}
  a:hover {color:#f00!important;}
  a:visited {color:#800!important;}
  a:visited:hover {color:#f00!important;}
  
/* Small bar above header  */
  #topbar {margin:0px 0px 0px 0px!important;margin-bottom:0px!important;}

/* ALL Adverts */
  #ad-oop-spot, #ad-ldr-spot, #ad-mu1-spot, #ad-mu2-spot, #jobs-promo {display:none!important;}
    
/* Social Media buttons in main header  */
  #follow_btns_top {display:none!important;}

/* Main navigation bar */
  /* Top navigation */
    #top_nav {background-color: #d00!important; border-bottom-color: #900!important;}
    #top_nav a {color: #fff!important; font-weight: bold; padding-left: 7px; padding-right: 7px;}
    #top_nav a:hover {background-color: #b00; color:#fff!important;}
    #top_nav a.on { background-color: #b00!important;} 
  
  /* Popup navigation */
    #nav_pop ul {border-bottom-color: #700!important;}
    #nav_pop ul li a {display: block; color: #500!important; font-weight: bold;}
    #nav_pop ul li a:hover {color: #fff!important;}
    #nav_pop div {background-color: #b00; border-color: #700;}
    #nav_pop div img {border-color: #700!important;}
    /* links */
    #nav_pop div a {color: #500!important;}
    #nav_pop div a:hover {color: #fff!important;}
    #nav_pop div a:visited {color: #500!important;}
    #nav_pop div a:visited:hover {color: #fff!important;}

    /* Comment in/out the following section to enable or disable the sub navigation links
       Not 100% sure how this works, but, after much tinkering, it does but could probably done so more elegantly! */
    .glue #nav_pop > div {height:31px!important}
    #nav_pop > div {height:31px!important}
    .static #nav_pop > div {bottom:-31px!important}
    #nav_pop .story_row {display:none!important}

    /* Comment in/out to Enable/disable empty sub navigation boxes with no content */
    div.bootnotes_nav {display:none!important}
    div.devops_nav {display:none!important}


/* Main content area */
  /* Headline pages */
    .headlines {padding:0px 0px 0px 0px!important;border-right:2px dotted #ddd;} /* Headline area */
     #top_tease, .toprot.large {display:none;}
    .headline {width:190px!important;} /* individual headline box width */
                  /* a.story_link:hover {background-color:#fafafa;} */
    a .standfirst, a:hover .standfirst, a:visited .standfirst, a:visited:hover .standfirst {color:#000!important;} /* Keep subheading text black */
    .time_comments {display:none;} /* Headline timestamp & Comments */
    /* .headlines .author_time_stamp, .headlines .comments .count{display:none!important} */
    .story_grid_img {display:none} /* Headline images */
  
    .dont_miss {width:415px!important;} /* Mid index page "DON'T MISS" box */
    .dont_miss.dcl .story_link .trailer {color:#000!important}
  
    #nearby-index {padding: 10px 15px 10px!important} /* Older stories link */
  
  /* Article Pages */
    /* Content Area */
      #article .article_head {margin-bottom: 12px!important; border-bottom: 1px #ccc solid}
      #article .article_head h2 {padding-bottom: 12px; border-bottom: 1px #eee solid}
      #author_box {display:none!important;}
      .article_img, .img_caption {display:none!important;} /* Article hero image */
      .headlines .author_time_stamp {display:none!important;}
      /* #article .byline {margin:14px 0px 0px!important} */
      #social_btns {top:17px!important; position: inherit; padding-top: 8px;} /* Shrink article share buttons */
      .wptl.btm {display:none;} /* Sponsered link */
       #in_article_forums .heading .post a {color:#fff!important;}
       #article_body_btm .comments a {color: #fff!important; background: #4e4e4e;}
       .promo_article {display:none!important;}
       .article_head {border-bottom:0px!important;}
       .crosshead span {box-shadow: none!important;}  

  
    /* Below articles */
      #read_more_on .story {min-height: 150px!important;} /* Reduce height of Read More boxes */
      #whitepapers {display:none;}
  
  /* Forum Pages */
  body:not(.DYNX) h2 {margin-bottom: 0px!important;}
  #forums_page .heading .post {border-radius: 3px;}
  #forums_page .heading .faq {padding: 0px!important;}
    .reg_btn, #forums_page .heading .post a, #forums-nav .reg_btn.logout, #forums-nav .your_topics .more a, #forums-starred .your_topics .more a {color: #fff!important; border-radius: 3px!important;}

 
/* Right column, ALL pages */
  #right-col {padding:0px 0px 0px 0px;}
  body:not(.DYNX) #hot img {border-width: 0px;}
  body:not(.DYNX) #spotlight .story img {border-width: 0px;}
  #rh_platform, #rhs_promoter, #tags, #rhs_whitepapers, #story_rhs_more, #sponlinks {display:none;}
  
/* Footer */  
  #footer {background-color:#eee;margin:0px!important;padding:15px!important;}
  #footer .circ {background-color:#000!important;}
  #page .subscribe_newsletter_button {background-color:#000!important;}
  #page a.subscribe_newsletter_button {color:#fff!important;}
  #page a.subscribe_newsletter_button:hover {color:#f00!important;}
  #footer .social {margin-top:1.5em!important;}

}
