.pull-left .label-primary {
background:#FE822E;
}

.pull-left .label-primary:hover {
background:#EDA034;
}

.pull-left .label-primary .fa-facebook-square {
background:none;
}

.ipsType_sectionTitle {
	    background: #CC7F0C;
}

.ipsWidget.ipsWidget_vertical .ipsWidget_title {
	background: #CC7F0C;
}

.cPollVoteBar > span {
	background: #DEA72A;
}

a {
	color: #A9720F;
}

a:hover {
	color: #000000;
}

#elSearchNavContainer, #elMobileNav {
	background: #CC7F0C;
	color:white;
}

#ipsLayout_mainNav > li > a {
	        opacity:1.0;
}

#ipsLayout_mainNav > li > a :hover {
	        opacity:0.5;
}

.ipsButtonBar {
	background: #CC7F0C;
	border-top:1px solid rgba(0, 0, 0, 0.15);
}

.ipsTabs {
	background: #CC7F0C;
	border-top:1px solid rgba(0, 0, 0, 0.15);
}

#elLogo img {
	max-width:45%;
}

.ipsApp .ipsSubNavigation {
	background: rgb(212, 157, 75);
}

#elUserNav > li > a {
	    color: #CC7F0C;
	border:black 1px;
}

#ipsLayout_header header {
	    background-color: #805F2F;
}

body {
	    background-color: #C7B793;
}

.ipsBadge_positive, .ipsBadge_style4 {
	background: #DEA72A;
}


.cBestAnswerIndicator_off {
	background:rgba(169, 114, 15, 0.20);
	transition: ease 0.5s;
}

.ipsList_inline {
	font-size:16px;
}

.bim_tthumb_wrap .tthumbimg {
	border-radius:10px;
}

.ipsItemStatus {
border-radius:10px;
}

html ::-webkit-scrollbar {
    width:12px;
}

html ::-webkit-scrollbar-thumb {
    background:rgba(11, 21, 31, 0.800);}

html ::-webkit-scrollbar-thumb:hover {
   background:rgba(11, 21, 31, 100);
}

html ::-webkit-scrollbar-track {
    background:#C7B793;
}

.ipsAreaBackground_dark {
	    background: #CC7F0C;
}

.cCalendar td.cCalendar_nonDate {
background:#C3A26F;
}

.ipsApp .ipsNotificationCount {
    background: #4E3609;
}

.ipsApp .ipsButton_important {
background: #8E5730;
}

.ipsRating .ipsRating_on .fa-star {
color:#CC7F0C;
}

.ipsRating > .ipsRating_hover .fa-star {
color:#D09B4B;
}

.ipsApp .ipsButton_primary {
background: #8E5730;
}

div#elSearchNavContainer {
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
width:99.80%;
margin-right:1px;
}

.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):nth-child(even) {
    background:rgba(199, 183, 147, 0.35);
}

.img-circle {
border-radius:0px
}

.cke_editable {
background:white;
}

html[dir="rtl"] #elSearch input[type="search"] {
    left: 30px;
    color: #000;
}

#elHeaderSubLinks a, .ipsBreadcrumb > ul a {
    color: #000000;

}

.ipsBreadcrumb > ul > li:last-child {
    color: #000000;
}
}
