css:
@-moz-document url-prefix("http://www.ohmydollz.com/forum/") {

    #contenu {
        background: url("http://www.zupimages.net/up/16/06/7w4o.png") no-repeat fixed 50% 0 rgba(0, 0, 0, 0);
        height: 100%;
        margin: 0;
        padding: 3px 0;
        width: 100%;
    }
    #head_forum {
        background-image: url("http://www.zupimages.net/up/16/06/4kvk.png");
        background-repeat: no-repeat;
        height: 300px;
        position: relative;
        width: 950px;
    }

#head_links {
    bottom: 24px;
    left: 475px;
    position: absolute;
}

#head_links a {
    color: #f5a0a5;
    font-size: 20px;
    text-decoration: none;
}

#head_links a:hover {
    color: #d21a32;
}

    #brdmenu {
        background-image: url("http://www.zupimages.net/up/16/06/cos4.png");
        background-repeat: no-repeat;
        height: 30px;
    }
    #footer_box {
        background-image: url("http://www.zupimages.net/up/16/06/vj31.png");
        background-repeat: no-repeat;
        height: 100px;
        width: 950px;
    }
    #brdwelcome {
        background-image: url("http://www.zupimages.net/up/16/06/y0pp.png");
        background-repeat: no-repeat;
        color: #E6E4D9;
        font-weight: bold;
        height: 60px;
        padding: 10px;
        width: 950px;
    }
    .pun H2, #brdmenu {
        background-image: url("http://www.zupimages.net/up/16/06/cos4.png");
        background-repeat: no-repeat;
    }
    .pun {
        color: #072908;
    }
    DIV.box, .pun BLOCKQUOTE, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven {
        background-color: #540907;
    }
    .box {
        border: 1px solid #072908;
    }
    .pun A:link, .pun A:visited {
        color: #f59aa1;
    }
    .pun A:hover {
        color: #DCDDD2;
    }
    DIV.block DIV.inbox, DIV.blockmenu DIV.inbox {
        padding: 0 6px;
        color: #E0DFD5;
    }
    .pun TH {
        background-image: url("http://www.zupimages.net/up/16/06/xce0.png");
        background-repeat: no-repeat;
        color: #E1CEC7;
        font-size: 13px;
        height: 100%;
        text-align: center;
    }
    pun .tcr .byuser {
        color: #E6E4D9;
    }
    TD.tc2, TD.tc3, TD.tcmod, #postpreview, #viewprofile DD, DIV.forminfo, #adminconsole FIELDSET TD, DIV.blockmenu DIV.box, #adstats DD {
        background-color: #540907;
    }
    TD DIV.tclcon {
        color: #7D7DA6;
        float: left;
        margin-left: 10px;
        width: 475px;
    }
    .pun .tcr .byuser {
        color: #E6E4D8;
    }
    .pun .tcr {
        border-right: 1px solid #5B0000;
    }
    .pun {
        color: #E4E1D8;
    }
    DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {
        background-color: #7d7c9d;
    }
    DIV.postright, DIV.postfootright {
        background-color: #5B0000;
    }
    DIV.blockpost DIV.box {
        background-color: #5B0000;
    }
    DIV.postright, DIV.postfootright {
        border-color: #F9EDD5;
        border-left: 18em solid #7d7c9d;
    }
    DIV.postleft DT, DIV.postmsg H4, TD.tcl H3, DIV.forminfo H3, P.postlink, DIV.linkst LI, DIV.linksb LI, DIV.postlinksb LI, .blockmenu LI, #brdtitle H1, .pun SPAN.warntext, .pun P.warntext {
        font-weight: bold;
        color: #EEEAE1;
    }
    .pun A:link, .pun A:visited {
        color: #f59aa1;
    }
    DIV.icon {
        background-image: url("http://www.zupimages.net/up/16/06/dpzn.png") !important;
    }
    DIV.inew {
        background-image: url("http://www.zupimages.net/up/16/06/m6l0.png") !important;
    }
}
