@-moz-document url-prefix("https://dev.scrap.tf"), url-prefix("https://scrap.tf") {
body[id|="pid"] {
    /* Replace this URL with desired image */
    
    background-image: url("/*[[background]]*/");
    /* Replace this URL with desired image */
    
    background-attachment: fixed;
    background-size: 100% 100%;
    background-clip: content-box;
    height: auto
}
.welcome {
    background-color: rgba(0, 0, 0, 0.6);
}
nav {
    opacity: 0.8
}
