css:
@-moz-document domain("rutracker.org") {
.branding #main-nav {
    padding: 0 7px !important;
    background: #CFD4D8 !important;
    border: solid #CFD4D8 !important;
    height: 23px !important;
}

.branding #main-nav div {
    display: block !important;
    height: auto !important;
    padding: 0 !important;
    border: 0 !important;
    background-color: transparent !important;
    opacity: 1 !important;
}

.branding #main-nav a {
    color: #535F62 !important;
}

.branding #main-nav a:hover, 
.branding #main-nav a:active {
    color: #000000 !important;
}

.branding .topmenu {
    background: #EFEFEF !important;
    border: 1px solid #CFD4D8 !important;
    padding: 0 3px !important;
    margin: 3px 10px 0 !important;
    width: calc(100% - 20px) !important;
    z-index: 1 !important;
}

.branding .topmenu.new-pm {
	background: #FFD9B2 !important;
    border-color: #FFB062 !important;
}


.branding .topmenu td:last-child div {
    background-color: transparent !important;
    border: 0 !important;
    color: #000 !important;
    font-weight: normal !important;
}

.branding .topmenu td:last-child div a, 
.branding .topmenu td:last-child div .a-like {
    color: #006699 !important;
}

.branding .topmenu td:last-child div a:hover, 
.branding .topmenu td:last-child div .a-like:hover {
    color: #DD6900 !important;
}

.branding .topmenu td div {
    position: static !important;
    display: block !important;
    padding: 0 !important;
    z-index: auto !important;
}

.branding .topmenu td div.invisible-el {
    display: none !important;
}

.branding .topmenu td:first-child div {
    background-color: transparent !important;
    border: 0 !important;
}

.branding .topmenu td {
    padding: 2px 4px !important;
}

.branding .topmenu .pad_2, 
.branding .topmenu .pad_2 td, 
.branding .topmenu td.pad_2 {
    padding: 2px !important;
}

.branding #search-menu {
    font-size: 12px !important;
}

.branding #search-submit {
    font-size: 12px !important;
    width: 60px !important;
}

.branding .topmenu td:last-child div.new-pm {
    background-color: transparent !important;
    border: 0 !important;
}

.branding .topmenu td:last-child div .new-pm-link {
    color: #993300 !important;
}

.branding .topmenu td:last-child div a.new-pm-link:hover {
    color: #591D00 !important;
}

.branding .topmenu td:last-child div.new-pm {
    background: transparent !important;
}
}
