@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
*[src*='www.google.'][src$='/favicon.ico'],
*[src*='myaccount.google.'][src$='/favicon.ico'],
*[src*='accounts.google.'][src$='/favicon.ico'],
*[src*='support.google.'][src$='/favicon.ico'],
*[src*='books.google.'][src$='/favicon.ico'],
*[src*='www.google.'][src$='/images/branding/product/ico/googleg_lodp.ico'] {
  padding-top: 16px !important;
  background: url(data:image/x-icon;base64,) !important;
}
#main-window *[image*="www.google."][image$="/favicon.ico"] > image, 
#main-window *[image*="myaccount.google."][image$="/favicon.ico"] > image, 
#main-window *[image*="accounts.google."][image$="/favicon.ico"] > image, 
#main-window *[image*="support.google."][image$="/favicon.ico"] > image, 
#main-window *[image*="books.google."][image$="/favicon.ico"] > image, 
#main-window *[image*="www.google."][image$="/images/branding/product/ico/googleg_lodp.ico"] > image {
  padding-right: 16px !important;
}
#main-window *[style^="list-style-image:"][style*='www.google.'][style*='/favicon.ico'],
#main-window *[style^="list-style-image:"][style*='myaccount.google.'][style*='/favicon.ico'],
#main-window *[style^="list-style-image:"][style*='accounts.google.'][style*='/favicon.ico'],
#main-window *[style^="list-style-image:"][style*='support.google.'][style*='/favicon.ico'],
#main-window *[style^="list-style-image:"][style*='books.google.'][style*='/favicon.ico'],
#main-window *[style^="list-style-image:"][style*='www.google.'][style*='/images/branding/product/ico/googleg_lodp.ico']{
  list-style: url(data:image/x-icon;base64,) !important;
}
