css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

:root {
	--toolbarbutton-hover-background: #eaeaea;
	--toolbarbutton-hover-bordercolor: #b4b4b4;
	--toolbarbutton-hover-boxshadow: none;

	--toolbarbutton-active-background: #eaeaea;
	--toolbarbutton-active-bordercolor: #b4b4b4;
	--toolbarbutton-active-boxshadow: none;

	--panel-separator-color: #b4b4b4;

	--urlbar-separator-color: #969696;
	}

#menubar, #TabsToolbar > * {
	background-color: #2d2d2d !important;
	}

toolbar {
	padding:0;
	}

#nav-bar {
	box-shadow: none;
	background: none !important;
	background: #e0e0e0 !important;
	
	--urlbar-border-color: #969696;
	}

#urlbar {
	background: none;
	background: #f9f9f9;
	color: #333333;
	}

#tabbrowser-tabs {
	background: none !important;
	background:	#2d2d2d !important;
	
	padding-top: 1px;
	min-height: 32px;
	}

#TabsToolbar .tab-background {
	margin: 0;
	}

#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
	display: none !important;
	}

/*All Tabs -- Background*/
#TabsToolbar .tab-background-middle {
	margin: 0;
	padding: 0;
	background: #d4d4d4;
	}


/*Selected Tabs -- Background*/
#TabsToolbar .tab-background-middle[selected] {
	background: #e0e0e0;
	}

/*All Tabs -- Font*/
#TabsToolbar .tabbrowser-tab {
	color: #737373;
	}

/*Selected Tabs & Hover Tab -- Border*/
#TabsToolbar .tabbrowser-tab:hover .tab-background,
#TabsToolbar .tabbrowser-tab[selected] .tab-background {
	margin-left:-1px;
	border: 1px solid #9f9f9f;
	
	}

/*All Tabs -- Border*/
#TabsToolbar .tabbrowser-tab .tab-background {
	border-top: 1px solid #cacaca;
	border-bottom: 4px solid #d4d4d4;
	}

/*Hover Tab -- Border*/
#TabsToolbar .tabbrowser-tab:hover .tab-background {
	border-top: 2px solid #d5aaa5;
	border-bottom: 4px solid #cecece;
	}

/*Selected Tabs -- Font*/
#TabsToolbar .tabbrowser-tab[selected] {
	color: #3a3a3a;
	}

/*Selected Tabs -- Border*/
#TabsToolbar .tabbrowser-tab[selected] .tab-background {
	border-top: 2px solid #d64937;
	border-bottom: 0 !important;
	margin-bottom: 1px;
	}

/*All Tabs -- Fix*/
#TabsToolbar .tabbrowser-tab {
	background: none !important;
	}

/*Refresh Button*/
#TabsToolbar .tabs-newtab-button {
	max-height:24px;
	max-width:28px;
	margin:4px;
	padding:0;
	border-radius: 5px;
	
	display:block;
	}

#TabsToolbar .tabs-newtab-button:hover {
	background: none;
	background: #eaeaea;
	border: 1px solid #b4b4b4;
	}

/*Tab Close Button*/
#TabsToolbar .tabbrowser-tab .close-icon {
	background-image: -moz-image-rect(url("chrome://global/skin/icons/close.svg"), 0, 16, 16, 0) !important;
	}

#TabsToolbar .tabbrowser-tab[selected] .close-icon {
	background-image: -moz-image-rect(url("chrome://global/skin/icons/close.svg"), 0, 96, 16, 80) !important;
	}

#TabsToolbar .tabbrowser-tab .close-icon:hover {
	background-image: -moz-image-rect(url("chrome://global/skin/icons/close.svg"), 0, 48, 16, 32) !important;
	}
