css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikiwand.com") {
  
  body.lg_latin.serif {
    font-family: "Charter", "FZNewShuSong-Z10S";
    line-height: 1.3;
  }
  body.lg_latin.sans_serif {
    font-family: "Seravek";
  }
  .mw-headline {
    font-family: "Open Sans Condensed";
    font-weight: 400;
  }
  .article_content h2, .article_content h4, .article_content h5, .article_content h6 {
    line-height: 1;
    margin: 0.3em 0;
  }
  body.lg_latin.serif p {
    margin-bottom: 0;
  }
  p + p {
    text-indent: 1.5em;
  }
  dl {
    margin: 0.3em 0;
  }
  dd {
    text-align: center;
    margin: 0;
  }
  .article_content dd ul {
    margin: 0.3em 2.5em;
    text-align: left;
  }
  code, kbd, pre, samp {
    font-family: "Tsentsiu Mono HG";
    font-size: 0.9em;
    background: #E0D9F7;
  }
  .mw-highlight, .mw-code {
    margin: 1.5em 0;
  }
  .hebrewQuotation, .quotebox, blockquote {
    margin: 1em 0 !important;
    font-size: 100% !important;
    text-indent: 1.5em;
  }
}
