css:
@-moz-document url-prefix("https://discordapp.com/channels/") {
/*Header/background part*/
/*background*/
#app-mount > div > div.app.flex-vertical{
    background: url(http://i.imgur.com/i7EJLgI.png);
}

/*guilds*/
.guilds-wrapper{
	background-color:black;
    background-image: -webkit-linear-gradient(left, rgba(0, 0, 100, 0) 0%, rgba(0, 0, 100, 1) 87%, rgba(0, 0, 180, 1) 92%, rgba(0, 0, 0, 1) 94%, rgba(0, 100, 250, 1) 96%, rgba(0, 0, 255, 1) 98%, rgba(0, 0, 0, 0) 100%);
    opacity:1;
}

/*Title Wrap*/
.theme-dark .chat .title-wrap,
.chat .title-wrap{
    background-image: -webkit-linear-gradient(top, rgba(0, 0, 100, 0) 0%, rgba(0, 0, 100, 1) 87%, rgba(0, 0, 180, 1) 92%, rgba(0, 0, 0, 1) 94%, rgba(0, 100, 250, 1) 96%, rgba(0, 0, 255, 1) 98%, rgba(0, 0, 0, 0) 100%);
    background-color: #003;
    background-repeat: no-repeat;
    background-position: 0;
    background-size: cover;
    border-bottom:none;
}

	/*Channel Name*/
    .chat .title-wrap .channel-name{
    	text-shadow: 3px 3px 2px black;
        color:white;
    }
    
    /*Help box thingy*/
    .help-container{
    	box-shadow:inset 0px 0px 20px 1px black;
        
    	background-color:rgb(0,100,0);
    	background-image: -webkit-linear-gradient(92deg, rgba(0, 0, 0, 0) 93%, rgb(255, 255, 255) 100%), -webkit-linear-gradient(135deg, rgba(0, 0, 0, 0) 86%, rgb(255, 255, 255) 100%), -webkit-linear-gradient(270deg, rgba(0, 0, 0, 0) 90%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(315deg, rgba(0, 0, 0, 0) 80%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(top, rgba(0, 0, 0, .5) 0%, rgba(0, 0, 0, 0) 80%);
    }.help-container:after{
    background: none;
    }
    
    	/*help button*/
        .need-help-button:hover, .need-help-button:hover .btn-help{border-color:white; color:white;}

/*header*/
.guild-header header{
    background-image: -webkit-linear-gradient(top, rgba(0, 0, 100, 0) 0%, rgba(0, 0, 100, 1) 87%, rgba(0, 0, 180, 1) 92%, rgba(0, 0, 0, 1) 94%, rgba(0, 100, 250, 1) 96%, rgba(0, 0, 255, 1) 98%, rgba(0, 0, 0, 0) 100%);
    background-color: #003;
    background-repeat: no-repeat;
    background-position: 0;
    background-size: cover;
    box-shadow: inset 0px 0px 10px 5px rgba(0,0,0,0.25), inset 0px 0px 0px 1px black;
}
.guild-header header:hover{
    background-color:#000;
}


/*the entire chat*/
div.flex-spacer.flex-vertical > div > div > div{
    background-color:transparent;
}

/*chatroom list*/
.flex-vertical.channels-wrap{
	background-color:transparent;
    background-image:none;
}

div.scroller-wrap.fade.dark > div{
    background-color:rgba(0,0,0,0.3);
    box-shadow: inset 0px 0px 20px 10px rgba(0,0,0,0.75);
    background-image:none;
}

	/*chatroom tabs (here comes the BIG part)*/
.guild-channels ul .channel-text.selected{    
    color: #fff;
    border-radius: 0px 0px 15px 2px;
    border: 2px solid #a00;
    border-left:none;
    background: rgb(120, 0, 0);
    background-image: radial-gradient(ellipse 10px 10px at 93% 15%, #ffff00 0%, rgba(250, 200, 0, 1) 20%, rgba(255, 200, 0, .5) 23%, rgba(0, 0, 0, 0) 100%), -webkit-linear-gradient(92deg, rgba(0, 0, 0, 0) 93%, rgb(255, 255, 255) 100%), -webkit-linear-gradient(135deg, rgba(0, 0, 0, 0) 86%, rgb(255, 255, 255) 100%), -webkit-linear-gradient(270deg, rgba(0, 0, 0, 0) 90%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(315deg, rgba(0, 0, 0, 0) 80%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 0%, rgba(100, 0, 0, 0) 100%);
    transition: all .8s;
    background-repeat:no-repeat;
    
    -webkit-animation: EnterOrb .5s ease-out forwards;
}.guild-channels ul .channel-text.selected:before{
    border-left: none;
}

.guild-channels ul .channel-text{
    background-image: -webkit-linear-gradient(135deg, rgba(0, 0, 0, 0) 86%, rgba(255, 255, 255, 0.2) 100%), -webkit-linear-gradient(270deg, rgba(0, 0, 0, 0) 90%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(315deg, rgba(0, 0, 0, 0) 80%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 0%, rgba(100, 0, 0, 0) 100%);
	background-repeat:no-repeat;
    
    border-radius: 0px 0px 15px 2px;
    border:2px solid black;
    border-left:none;
    transition: all .3s;
} 
.guild-channels ul .channel-text:before{
	border-left:none;
}

.guild-channels ul .channel-text:hover:not(.selected){
    background-image: -webkit-linear-gradient(135deg, rgba(0, 0, 0, 0) 86%, rgba(255, 255, 255, 0.3) 100%), -webkit-linear-gradient(270deg, rgba(0, 0, 0, 0) 90%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(315deg, rgba(0, 0, 0, 0) 80%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 0%, rgba(100, 0, 0, 0) 100%);
	background-repeat:no-repeat;
    
    border-radius: 0px 0px 15px 2px;
    border:2px solid black;
    border-left:none;
    background-color: rgba(150,0,0,.3);
} 
.guild-channels ul .channel-text:hover:before{
	border-left:none !important;
}

	/*VOICE channels (more ugly stuffs)*/
.guild-channels ul .channel-voice.audio{    
    color: #fff;
    border-radius: 0px 0px 15px 2px;
    border: 2px solid #070;
    border-left:none;
    background: rgb(0, 100, 0);
    background-image: radial-gradient(ellipse 10px 10px at 93% 15%, #ffff00 0%, rgba(250, 200, 0, 1) 20%, rgba(255, 200, 0, .5) 23%, rgba(0, 0, 0, 0) 100%), -webkit-linear-gradient(92deg, rgba(0, 0, 0, 0) 93%, rgb(255, 255, 255) 100%), -webkit-linear-gradient(135deg, rgba(0, 0, 0, 0) 86%, rgb(255, 255, 255) 100%), -webkit-linear-gradient(270deg, rgba(0, 0, 0, 0) 90%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(315deg, rgba(0, 0, 0, 0) 80%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 0%, rgba(100, 0, 0, 0) 100%);
    transition: all .8s;
    background-repeat:no-repeat;
    
    -webkit-animation: EnterOrb .5s ease-out forwards;
}.guild-channels ul .channel-voice.audio:before{
    border-left: none;
}

.guild-channels ul .channel-voice{
    background-image: -webkit-linear-gradient(135deg, rgba(0, 0, 0, 0) 86%, rgba(255, 255, 255, 0.2) 100%), -webkit-linear-gradient(270deg, rgba(0, 0, 0, 0) 90%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(315deg, rgba(0, 0, 0, 0) 80%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 0%, rgba(100, 0, 0, 0) 100%);
	background-repeat:no-repeat;
    
    border-radius: 0px 0px 15px 2px;
    border:2px solid black;
    border-left:none;
    transition: all .3s;
} 
.guild-channels ul .channel-voice:before{
	border-left:none;
}

.guild-channels ul .channel-voice:hover:not(.selected){
    background-image: -webkit-linear-gradient(135deg, rgba(0, 0, 0, 0) 86%, rgba(255, 255, 255, 0.3) 100%), -webkit-linear-gradient(270deg, rgba(0, 0, 0, 0) 90%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(315deg, rgba(0, 0, 0, 0) 80%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 0%, rgba(100, 0, 0, 0) 100%);
	background-repeat:no-repeat;
    
    border-radius: 0px 0px 15px 2px;
    border:2px solid black;
    border-left:none;
    background-color: rgba(0,100,0,.3);
} 
.guild-channels ul .channel-voice:hover:before{
	border-left:none !important;
}

@keyframes EnterOrb {
    from {background-position: 40px 0,0,0,0,0,0;}
    to {background-position: 0;}
}


/*account*/
div.account{
	background-color:rgba(0,0,0,0.5);
}

/*setting type things*/
.account .btn-group > *{
	background-color: rgb(240, 180, 0);
	background-image: -webkit-linear-gradient(135deg, rgba(0, 0, 0, 0) 86%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(270deg, rgba(0, 0, 0, 0) 90%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(315deg, rgba(0, 0, 0, 0) 80%, rgb(0, 0, 0) 100%), -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 0%, rgba(100, 0, 0, 0) 100%);
    border-color:black;
}
.account .btn-group > *:after{
	-webkit-filter:brightness(0%) blur(1px);
    opacity:1;
    transition: -webkit-filter .4s;
}
.account .btn-group > *:hover:after{
	-webkit-filter:brightness(0%) blur(0px);
    opacity:1;
    transition: -webkit-filter .4s;
}


.links{background-color:black;}

/*chat itself*/
.theme-dark .chat>.content,
.chat>.content{
    background-color:rgba(0,0,0,0.5);
}
	/*new message notif*/
.chat .new-messages-bar button:first-child,
.chat .new-messages-bar button:last-child{
	background-color:rgb(240, 180, 0);
    transition:background-color .3s;
}
.chat .new-messages-bar button:first-child{border-bottom-left-radius:10px;color:black;}
.chat .new-messages-bar button:first-child:hover{color:black;background-color:rgb(200, 140, 0);}

.chat .new-messages-bar button:last-child{border-bottom-right-radius:10px;color:black;}
.chat .new-messages-bar button:last-child:hover{color:black;background-color:rgb(200, 140, 0);}

.chat .new-messages-bar,
.chat .new-messages-bar:hover{
	background-color:black;
}

	/*messages*/
.message-group{
    background-color:rgba(0,0,0,0.3);
}

		/*message headers*/
.message-group h2{
    background-color:rgba(0,0,0,0.8);
    width:50%;
    box-shadow: 0px 0px 6px 3px rgba(0,0,0,.8);
}

		/*avatars*/
.avatar-large{
    left:10px ;
}
	/*dividers*/
.theme-dark .chat .divider,
.theme-dark .chat>.content .messages .divider span,
.chat .divider,
.chat>.content .messages .divider span{
    background-color:black;
}

/*chat entry area*/
div.flex-spacer.flex-vertical > form{background-color:rgba(0,0,0,0.5);}
div.flex-spacer.flex-vertical > form:hover{background-color:black;}

div.flex-spacer.flex-vertical > form,
div.flex-spacer.flex-vertical > form:hover{transition: background-color .4s;}

	/*Chat Entry*/
.channel-textarea-inner,
.channel-textarea-inner:hover,
.channel-textarea-inner textarea,
.theme-dark .channel-textarea-inner,
.theme-dark .channel-textarea-inner:hover,
.theme-dark .channel-textarea-inner textarea{
	border-color:rgba(255, 255, 255, 0.35);
    background-color:transparent;
    width:98%;
    left:5px;
    transition: border-color .2s, background-color .4s;
}
.channel-textarea-inner textarea::-webkit-input-placeholder{color:#ccc;}

	/*typing notif*/
.typing{
    background-color:black !important;
}



/*User List*/
.theme-dark div.content.flex-spacer.flex-horizontal > div.channel-members-wrap > div > div,
div.content.flex-spacer.flex-horizontal > div.channel-members-wrap > div > div{
    background-color:rgba(0,0,0,0.5);
}

	/*users/hover stuff*/
.theme-dark .channel-members .member.popout-open, .theme-dark .channel-members .member,
.channel-members .member.popout-open, .channel-members .member{
    border: 2px solid transparent;
    border-radius:40px;
    transition: all .2s;
    height:30px;
    background-image: -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 55%, transparent 100%) !important;
}
.theme-dark .channel-members .member.popout-open, .theme-dark .channel-members .member:hover
.channel-members .member.popout-open, .channel-members .member:hover{
	background-color: #00a !important;
    border: 2px solid #00a;
    border-radius:40px;
    background-image: -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 55%, transparent 100%) !important;
    transition: all .2s;
}
.theme-dark .channel-members .member.popout-open, .theme-dark .channel-members .member:focus
.channel-members .member.popout-open, .channel-members .member:focus{
	background-color: #a00 !important;
    border: 2px solid #a00;
    border-radius:40px;
    background-image: -webkit-linear-gradient(top, rgba(0, 0, 0, 1) 55%, transparent 100%) !important;
    transition: all .2s;
}

/*Online indicator*/
.theme-dark .channel-members .avatar-small .status,
.channel-members .avatar-small .status{
	border-color:#111;
}

/*misc*/

.scroller.messages::-webkit-scrollbar-track-piece{
	background-color: #2e3136;
    border-color: #36393e;
}

.scroller.messages::-webkit-scrollbar-thumb{
	border:3px solid #36393e;
    border-radius:10px
}


