css:
@-moz-document domain("www.chatwork.com") {
/* 全体 */
.wrapper
{
  background: inherited;
}

/* チャットリストの文字・背景色 */
.sideContent
{
  background-color: #FFDEAD;
  color: #8B4513;
}

/* 選択されたチャットリスト項目の文字・背景色 */
.sideContent .menuListTitleSelected
{
  color: #FAF0E6;
  background-color: rgba(0,0,0,0.4);
}

/* フォーカスされたチャットリスト項目の文字・背景色 */
.sideContent .menuListTitle ._room:hover,
.sideContent .menuListTitleFocus
{
  color: #FAF0E6;
  background-color: rgba(0,0,0,0.2);
}

/* チャットリスト・チャットウィンドウのヘッダ領域の文字・背景色 */
.sideContent .chatFilterMenu,
.mainContent .mainContentHl
{
  color: #FFF;
  background-color: rgba(0,0,0,0.6);
}

/* チャットウィンドウのサブコンテンツ領域の背景色 */
.mainContent .subContent
{
  background-color: #FFDEAD;
}

/* チャットウィンドウのサブコンテンツ領域の文字色 */
.mainContent .subContentArea .chatroomTitleMeta,
.mainContent .subContentArea .subRoomDescription,
.mainContent .subContentArea .subRoomTaskArea .linkStatus
{
  color: #8B4513 !important;
}
}
