css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("groupees.com") {
		.container-main{padding-top:70px !important;}
.about-us,.home-banner,.broadcast-message{display: none !important;}
	
		/* chat */
	div[id*="chat"]{display: none !important;}
	/* subscribe */
	.subscribe-row{display: none !important;}
	/* footer */
	footer{display: none !important;}
}

/* top page */
@-moz-document url("https://groupees.com/") {
	.favorite-bundles .hint-top-label, .default-bundles .hint-top-label{display: none !important;}
	
	.default-bundles,.favorite-bundles{background: black !important;}
	.sloped{display: none !important;}
	.featured-bundle a,.favorite-bundles .tile, .default-bundles .tile{border: 2px solid white !important;}
	.sloped:before{display: none !important;}
	
	.featured-bundle{max-width: 1200px !important;}
	.favorite-bundles{padding-top: 5px !important;}
	.default-bundles{padding-top: 0 !important;}
	.bundle-info{background-color: rgba(0,0,0,0.85) !important;
	z-index: 2 !important;}
	}
