css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ani.gamer.com.tw") {
    .big_content::after {
        white-space: pre;
        content: "\A\A※由於代碼過長，所以動畫瘋已獨立成另一專案。\A請使用者務必刪除當前所使用的任何 Nightly Bahamut，\A　再分別安裝 Nightly Bahamut (基本必裝) 與 Nightly Bahamut: Anime ChuDoku (動畫瘋用)。\A\A　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　-開發者_hollen9";
    }
}

@-moz-document url-prefix("http://home.gamer.com.tw/creationEdit1.php"), url-prefix("http://home.gamer.com.tw/creationNew1.php") {
    
    #BH-master {
        width: 1000px;
    }
    .BH-lbox {
        background-position: center center;
        background-size: 100%;
    }
}



@-moz-document url-prefix("http://forum.gamer.com.tw/search_content.php") {
    /* GOOGLE搜尋頁面 */
    * {
        font-family: "Noto Sans CJK TC";
    }
    .gsc-control-cse {
        border: 0;
        background: transparent;
        
    } .FM-blist td[style*="background-color:#FFF"], .FM-blist td[style*="background-color:#FFF"]:hover {
        background: transparent !important;
    }
    .FM-blist tr:hover, .FM-blist tr:hover a {
        background: unset !important;
        color: unset !important;
    }
    .cse .gsc-webResult.gsc-result, .gsc-webResult.gsc-result, .gsc-webResult.gsc-result.gsc-promotion, .gsc-results .gsc-imageResult-classic, .gsc-results .gsc-imageResult-column, .cse .gsc-webResult.gsc-result:hover, .gsc-webResult.gsc-result:hover, .gsc-webResult.gsc-result.gsc-promotion:hover, .gsc-results .gsc-imageResult-classic:hover, .gsc-results .gsc-imageResult-column:hover {
        border: 0;
    }
    #resInfo-0, .gs-snippet {
        color: #C2C2C2 !important;
    }
    .gs-webResult div.gs-visibleUrl-long, .gs-promotion div.gs-visibleUrl-long {
        color: #626262;
        text-indent: 30px;
    }
    .gstitle {
        display: block;
    }
    .gs-result .gs-title b:first-child {
        color: #D25C42;
    } .gs-result .gs-title b, .gs-result .gs-title {
        text-decoration: none;
        color: #FFF;
    } .gs-result .gs-title:hover {
        color: #D25C42 !important;
    }
    
    .gcsc-branding-text, .gcsc-branding-img-noclear {
        vertical-align: middle !important;
    }
    
    div.gsc-result {
        border-bottom: 1px solid #36393E !important;
    } div.gsc-result:hover {
        background: #494C53;
    } div.gsc-result:hover div.gs-title {
        margin-left: 0px;
        transition: 0.5s ease-in;
    }
    
    td.gsc-table-cell-snippet-close .gs-snippet {
        margin-left: -15px;
        padding-right: 140px;
    }  .gs-image {
        transition: 1s;
        position: relative;
        top: 0;
        left: 350px;
        
    } div.gsc-result:hover .gs-image {
        z-index: 100;
        top: -10px;
        left: 350px;
        transition: 1s;
        transform: scale(2);
        -webkit-transform: scale(2);
    } .gs-image:hover {
        transition: 0.5s;
        transform: scale(4) !important;
        -webkit-transform: scale(4) !important;
    }
}

@-moz-document domain("forum.gamer.com.tw"), domain("ani.gamer.com.tw"), domain("guild.gamer.com.tw"), domain("gc.gamer.com.tw"), domain("acg.gamer.com.tw"), domain("gnn.gamer.com.tw"), url-prefix("http://www.gamer.com.tw/"), url("http://home.gamer.com.tw/"), url-prefix("http://home.gamer.com.tw/index.php"), url-prefix("http://home.gamer.com.tw/indexWallpaperList.php") {
    #BH-wrapper {
        background-color: #2E3136;
        box-shadow: 3px 3px 5px rgba(0, 0, 0, 1);
        border-radius: 0 0 20px 20px;
    }
    #BH-background, #BH_background {
        background-color: #1d1c2e;
        min-height:100vh;
    } .BH_background {
        margin-top: 0;
        border-top: 90px solid transparent;
    }/*動畫瘋:BH_background*/
    
    .BH-rbox a {/* 層級高於其他超連結設定*/
        color: #3498DB;
    }
    .BH-rbox a:hover {/* 層級高於其他超連結設定*/
        color: #3DB1FF;
    }
    
    .BH-lbox {/*進版*/
        background-color: #36393E;
        border: 0px solid #e5e5e5;
        margin-bottom: 10px;
        clear: both;
        overflow: auto;
        border-radius: 20px;
    }
    #BH-slave h5 {
        background-color: #1E2124;position: relative; top: 25px; border-radius: 10px;
        border: 3px solid #36393E;
    }
    
    .BH-rbox {
        background-color: #36393E;
        border-radius: 20px;
        color: #C1C1C1;
        border: 0px solid #e5e5e5;
        margin-bottom: 10px;
        overflow: hidden;
        padding: 25px 8px 8px 8px;
    } /*這也適用於其他網頁，例如討論區看板*/
}

@-moz-document domain("forum.gamer.com.tw") {
    /* 討論區重新分配寬度 (這邊沒有格式設定，只有長寬、留白、邊長等) */
    #BH-slave {
        width: 147px;
        box-sizing: border-box;
        -webkit-box-sizing: border-box;
        /*右邊框框留白*/
        padding-left: 3px;
        padding-right: 3px;
    }

    #BH-master {
        width: 850px;
        padding-left: 3px;
    /*background-color: #36393E;*/
    }

    .FM-abox2A {
        width: 400px;
    } /*這個是分類版的 熱門推薦_文章預覽小框框*/
    .FM-abox2A p {
        width: 270px;/*<=280*/
    }
}

@-moz-document domain("gc.bahamut.com.tw") {
    /*使內嵌框架頁同調*/
    body {
        background-color: #36393E;
        font-family: "Noto Sans CJK TC DemiLight", "MS Gothic";
    }
}

@-moz-document domain("gamer.com.tw") {
    /*
    名稱: Nightly Bahamut
    作者: 好冷酒 (hollen9)
    更新: 請見頁尾 footer
    始於: 2016/02/06
    */
    @keyframes fadeInwithbluring { from { opacity: 0; filter: blur(3px);-webkit-filter: blur(3px);} to { opacity:1; filter: blur(0px); -webkit-filter: blur(0px);} }
    @keyframes fadeIn { from { opacity: 0;} to {opacity:1;} }
    @keyframes fadeInhalf { from { opacity: 0.5;} to {opacity:1;} }
    @keyframes blurIn { from {filter: blur(2px);-webkit-filter: blur(2px);} to {filter: blur(0px);-webkit-filter: blur(0px);} }
    #BH-footer::after, .BA-footer::after, .footer::after {
        visibility: visible;
        content: "NightlyBahamut v0.32 (´・ω・`) Designed by Hollen9 (好冷酒)";
        white-space: pre;
        cursor: default;
    }
    
    /* 隱藏廣告 */
    /*
    #flySalve, #BH-bigbanner, #BH-footer iframe, .BA-billboard, #flyRightBox {
        visibility: hidden !important;
        position: absolute !important;
        top: -800px !important;
        left: 0px !important;
    }*/
    
    /* 首頁廣告酷炫化 */
    #flyRightBox {
        position: fixed !important;
        left: -50px !important;
        top: 30% !important;
        width: 100px;
        transition: 1s;
        overflow: visible;
    } #flyRightBox:hover {
        left: 0px !important;
    } #flyRightBox div {
        width: 100px;
        margin-bottom: 0;
    } #flyRightBox img  {
        width: 100px;
        filter: saturate(0.2);
        -webkit-filter: saturate(0.2);
        transition: 0.5s;
    } #flyRightBox img:hover {
        filter: saturate(1);
        -webkit-filter: saturate(1);
        width: 200px;
    }
    
    .BA-billboard {
        height: 40px;
        overflow: hidden;
        transition: 0.5s;
    } .BA-billboard:hover {
        height: 250px;
        transition: 1.5s;
    } .BA-billboard:hover + script + .BA-main #flyRightBox {
        
        left: -100px !important; 
    }
    
    #BH-bigbanner {
        height: 80px;
        overflow: hidden;
        filter: saturate(0.2);
        -webkit-filter: saturate(0.2);
        transition: 1s;
    } #BH-bigbanner:hover {
        height: 160px;
        filter: saturate(1);
        -webkit-filter: saturate(1);
    }
    
    /* 一般版面的浮動式廣告 */
    #flySalve {
        /*visibility: hidden;*/
        transition: 1s;
        position: fixed !important;
        top: 75px !important;
        left: -60px !important;        
        width: 100px;
    } #flySalve:hover {
        left: 0 !important;
    } #flySalve img {
        width: 100px;
        transition: 0.5s;
        opacity: 0.5;
    } #flySalve img:hover {
        transition: 0.5s;
        width: 200px;
        opacity: 1;
    } #flySalve > ins {
        transition: 0.5s;
        opacity: 0.5;
        -ms-zoom: 0.334;
        -moz-transform: scale(0.334);
        -moz-transform-origin: 0 0;
        -o-transform: scale(0.334);
        -o-transform-origin: 0 0;
        -webkit-transform: scale(0.334);
        -webkit-transform-origin: 0 0;
    } #flySalve > ins:hover {        
        opacity: 1;
        -ms-zoom: 1;
        -moz-transform: scale(1);
        -moz-transform-origin: 0 0;
        -o-transform: scale(1);
        -o-transform-origin: 0 0;
        -webkit-transform: scale(1);
        -webkit-transform-origin: 0 0;
    }

    /***************/
    /** 暗色系開始 **/
    /***************/
    
    /*基本共同設定*/
    
    /*User-defined BG*/
    /*#BH-background {
        background-image: url("https://vgy.me/l0wGs3.png"); 
        background-repeat: no-repeat; 
        background-position: 100% 80%;
        background-size: 14% auto;
        background-attachment:fixed;
    }*/
    
    body {
        /*background-image: url("https://raw.githubusercontent.com/hollen9/GamerBahamutThemes/7efb9ed699c746e3a4173b29220fbbc6cfcfae1b/images/tile_blurblack.png");
        background-color: #1E2124;*/
        
        font-family:"Noto Sans CJK TC DemiLight","MS Gothic", sans-serif;
        color: #C0C1C2;
        /*overflow-x: hidden;*/
    }
    
    .BA-main { overflow-x: hidden; }
    
    a:link, a:visited {
        color: #3BAFFF;
        text-decoration: none;
        transition: 0.2s ease-out;/*連結補間動畫*/
        font-family: "Noto Sans CJK TC Regular", "Noto Sans CJK TC";
    } a:hover {
        color: #A6DBFF;
        text-decoration: underline;
    }
    
    .FM-cbox9 a, .TOP-btn a, .BA-top a {
        transition: none !important;
    } /*連結補間動畫例外名單*/
    
    /*#egg_mask{
        width: 100%;
        height: 2000px;
    }*/
    
    
    input, select, textarea {
        font-family:"Noto Sans CJK TC DemiLight", "MS Gothic", sans-serif !important;
    }
    
    input[type="checkbox"] {
        border-radius: 10px;
        border: 2px solid black;
    }
   
    Button, input[type="button"], input[type="submit"] {
        cursor: pointer;
        transition: 0.5s;
        background: transparent;
        
        border: 2px solid #F16364;
        color: #FFD4D4;
        border-radius: 5px;
        
        font-size: 16px;
        font-family: "Noto Sans CJK TC", "MS Gothic";
        white-space: nowrap;
        min-width:86px;
        min-height:35px;
    } Button:hover, input[type="button"]:hover, input[type="submit"]:hover {
        transition: 0.1s ease-out;
        
        border: 2px solid #FFF;
        color: #FFF;
    } Button:active, input[type="button"]:active, input[type="submit"]:active {
        background: #F16364;
        border-color: #F16364;
        color: #FFD4D4;
    }

    /*暫時拿掉喔#BH-wrapper {
        background-color: #2E3136;
        box-shadow: 3px 3px 5px rgba(0, 0, 0, 1);
        border-radius: 0 0 20px 20px;
    }*/

    /* 全頁對話方塊︵開始 */
    .frame {
        background: #2E3136;
        border-radius: 20px;
        box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.2);
        
    } .frame > h1 {
        background: #1E2124;
        border-top-left-radius: 20px;
        border-top-right-radius: 20px;
    } .frame::after {
        position: fixed;
        z-index: -1;
        display: block;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: #1D1C2E url("https://vgy.me/t4GjGN.png");
        color: #1D1C38;
        cursor: default;
        content: "Nightly Bahamut - Made by Hollen9";
        
    }
    /* 全頁對話方塊︶結束 */
    
    
    /* 首頁︵開始 */
    
    div[class^="BA-cbox"] {
        /*animation*/
        animation:blurIn ease-out 1;
        animation-fill-mode:forwards;
        animation-duration:0.4s;
        animation-delay: 0s;
    }
    div[class^="BA-cbox"] span {
        color: #CECECE;
    }
    
    .BA-logo, .bahago {
        filter: contrast(5) invert(1);
        -webkit-filter: contrast(5) invert(1);
    }
    .BA-topbg {
        /*background:  rgba(45, 46, 56, 0.2);*/
        background: url("https://raw.githubusercontent.com/hollen9/GamerBahamutThemes/master/images/top_bg_dark.png") right no-repeat;
        background-size: 100% 107px;
        border-bottom: 5px solid #1D1C2E;
        margin-bottom: 0px;
    }/*背景圖*/
    ul.BA-menu {
        background-color: hsla(246, 100%, 3%, 0.2);
        border-radius: 10px;
        height: 29px;
        border: none;
    } .BA-menu li {
        transition: 0.6s ease-out;
        margin-top: 3px;
        border-right: 0px dotted #CDD1FF;
    } .BA-menu li:hover {
        border-radius: 5px;
        background-color: hsla(245, 27%, 86%, 0.12);
    } .BA-menu a {
        border-radius: 5px;
        border: none;
        width: 100%;
        height: 100%;
    } .BA-menu a:hover {
        border: none;
    } .BA-menu .more a {
        border: 0;
        padding: 0;
        background: none;
        margin-top: 4px;
        height: 100%;
    } .BA-menu .more {
        width: 40px;
        height: 23px;
    } .BA-menu .more a:hover {
        border: 0;
        background: none;
    } .BA-menu .more a::before {
        font-size: 13px;
        content: "更多";
        white-space: nowrap;
        padding-left: 5px;
    }/*homepage頂部導覽列*/
    
    /*[左邊&右邊]*/
    .BA-lbox {
        padding-bottom: 30px;
    }
    
    
    
    h1.BA-ltitle {
        /*background-color: #2E3136;*/
        background: linear-gradient(0deg, #2E3136 50%, #747A85 100%);
        border-top-left-radius: 10px;
        border-top-right-radius: 10px;
    } h1.BA-ltitle + .BA-lbox {
        background-color: #36393E;
        border: 0;
        border-bottom-left-radius: 10px;
        border-bottom-right-radius: 10px;
    } h1.BA-ltitle + .BA-lbox > ul > li {
        padding: 5px 0;
        border-bottom: 0px dotted #CCCCCC;
    }
    
    .BA-left > div > a > img {
        border-radius: 5px;
    }
    .BA-ltitle:nth-of-type(3) {
        margin-top: 0px;
    } /*學術研究*/
    
    ul.BA-serve {
        border-radius: 10px;
        border: 0;
        background-color: #36393E;
        box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.5);
        padding-top: 10px;
        padding-bottom: 10px;
        margin-bottom: 50px;
        z-index: 200;
    } ul.BA-serve > li {
        border-bottom: 0px dotted #e5e5e5;
        padding: 0;
        margin: 0 5px 0 10px;
    } ul.BA-serve > li:hover {
        /*empty*/
    } ul.BA-serve > li > a {
        display: block;
        border-radius: 10px;
        letter-spacing: 2px;
        height: 30px;
        line-height: 29px;
        color: #BAB5B5;
    } ul.BA-serve > li > a:hover {
        background: linear-gradient(90deg,#0F1217 30%,#36393E);
        /*border-radius: 30px;*/
        text-decoration: none;
        color: #FFFFFF;
    } ul.BA-serve > li > a > img {
        filter: hue-rotate(180deg) saturate(0.5);
        -webkit-filter: hue-rotate(180deg) saturate(0.5); 
        vertical-align: middle;
        margin-left: 10px;
    }/*主頁選單*/
    
    .newanime_img {
        border: solid black;
        border-width: 1px 1px 0 1px;
        border-radius: 5px 5px 0 0;
        width: 173px;
        margin-left: auto;
        margin-right: auto;
    }

    .BA-lbox3 a:nth-child(2n), .BA-lbox6 {
        color: #FFE1FF;
        background-color: transparent;
        border: solid black;
        border-width: 0 1px 1px 1px;
        border-radius: 0 0 5px 5px;
        /*border-bottom: 1px dotted black;*/
        width: 173px;
    }
    .BA-lbox6 {
        border: 0;
        padding: 0 4px 20px 3px;
        background: #36393E;
    } .BA-lbox6 li {
        border: 0;
    }
    
    
    
    /*[中間]*/
    .BA-cbox3 p {
        padding-top: 0;
        display: block;
        height: 23px;
        border: 0;
    } .BA-cbox3 span {
        margin-top: 6px;
    } .BA-cbox3 img {
        float: right;
        position: relative;
        right: 5px;
        top: 6px;
        background-color: #FFF;
        filter: hue-rotate(120deg) saturate(2);
        -webkit-filter: hue-rotate(120deg) saturate(2);
        width: 17px;
    } .BA-cbox3 p a {
        padding-left: 50px;
        display: inline-block;
        margin-left: 10px;
        width: 675px !important;
        height: 30px;
        overflow: hidden;
        position: absolute;
        left: 0;
        line-height: 28px;

    } .BA-cbox3 p:hover {/*0224*/
        background-color: #4D5158;
    }
    
    .BA-cbox6A a.IN-acg5 {
        padding-left: 80px;
        display: inline-block;
        margin-left: 10px;
        width: 447px !important;
        max-width: none !important;
        height: 30px;
        overflow: hidden;
        position: absolute;
        left: 0;
        line-height: 28px;
    } .BA-cbox6A p {
        white-space: nowrap;
        padding: 0px 0;
        border-bottom: 0px dotted #CCCCCC;
        margin-right: 3px;
        height: 30px;
    } .BA-cbox6A p:hover {
        background-color: #4D5158;
    } .BA-cbox6A p span {
        margin-top: 6px;
    }.BA-cbox6C {
        width: 40px;
        padding: 0 0 3px 0;
    }
    
    td#acg_list .BA-cbox6D {
        width: 70px;
    }
    
    #gnn_head > div:nth-child(2), #gnn_head > div:first-child, #LiveBlock > div.BA-cbox9A:first-child, #homeOdata > div:first-child {
        margin-bottom: 0px;
        border-bottom-left-radius: 0;
        border-bottom-right-radius: 0;
    } #gnn_normal, #gnn_head > div:nth-child(2), #LiveBlock > div.BA-cbox9A:nth-child(2), #homeOdata > div:nth-child(2) {
        border-top-left-radius: 0;
        border-top-right-radius: 0;
        padding-bottom: 5px;
    }
    
    .BA-cbox5 span, .BA-cbox8 span {
        background-color: transparent;
        border-radius: 20px;
        border: 2px solid #CE4949;
        color: #FFC5C5;
        font-family: Arial;
    }
    
    .BA-cbox6A p:first-child, .BA-cbox5 tr:first-child, .BA-cbox7 tr:first-child {
        background-color: transparent !important;
    }
    
    .BA-cbox6B {
        background-color: transparent;
    }
    
    .BA-cbox9A div a {
        border-radius: 5px;
        background-color: transparent;
        border: 0;
    }
    
    .BA-cbox {
        border: 0px solid #1C1E21;
        border-radius: 10px;
        margin-bottom: 10px;
        background-color: #36393E;
        box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.5);
        position: relative;
        z-index: 0;    
    }/*中間全部的框框*/
    

    
    .BA-cbox9C, .BA-cbox10B {
        border: 0;
        background: none;
        margin-top: -5px;
    } .BA-cbox9C::before, .BA-cbox10B::before {
        display: none;
    } .BA-cbox9C a::after {
        content: "直播＠實況大廳";
    } .BA-cbox10B a::after {
        content: "作品＠創作大廳";
    }
    
    #BA-center-id {
        margin-top: -7px;
    }
    
    .BA-ctag1 {
        margin-left: 10px;
        overflow-y: visible !important;
        
        margin-top: 7px;
        height: 26px;
        position: relative;
        top: 3px;
        z-index: 0;
    } .BA-ctag1 > li > a{
        border-top-left-radius: 5px;
        border-top-right-radius: 5px;
    }
    
    .BA-ctag1 a {
        background-image: none;
        background-color: #232529;
        border: 0;
        color: #A2A5AE;
        min-width: 50px;
        cursor: default;
        position: relative;
        top: 3px;
    } .BA-ctag1 a:hover {
        background-image: none;
        background-color: #36393E;
        color: #D2D6DC;
        position: relative;
        top: 0px;
    } .BA-ctag1 .BA-ctag1now {
        color: #FFFFFF;
        background-image: none;
        background-image: linear-gradient(0deg, #36393E 50%, #747A85 100%);
        position: relative;
        top: 0px;
        font-weight: normal;
        border: 0 !important;
    } .BA-ctag1 .show, .BA-ctag1 .show:hover {
        background-color: #6D1818;
        background-image: linear-gradient(0deg, #232529 80%, #6D1818 100%);
        color: #D93030;
    }/*標籤TAB*/
    
