    } .sign input[type="text"]:focus, .sign input[type="password"]:focus {
        background-color: rgba(45, 46, 56, 1.0);
        border-color: #FF6767;
        color: #FFB8B8;
    }
    
    .sign li {
        list-style: none;
        margin-bottom: 10px;
        position: relative;
    }
    
    .sign label {
        position: absolute;
        width: 40px;
        height: 50px;
        left: 0;
        top: -4px;
    } .focus .regicon ~ label, .focus .passicon ~ label {
        filter: hue-rotate(180deg) saturate(0.9);
        -webkit-filter: hue-rotate(180deg) saturate(0.9);    
    }
    
    .cancel {
        width: 35px;
        height: 40px;
        right: 5px;
        top: -5px;
    }

    .LN_main1 .login_amend {
        border-top: 2px solid #D19494;
    }
    /*登入頁面︶結束*/
}

