css:
@-moz-document domain("gravitytales.com") {
/*Header*/
#header-text-nav-container {background-color: #232323 !important;}

/*Title Text*/
h1, h2, h3, h4, h5, h6 {color: #FFFFFF !important;}

#content .post .article-content .entry-title a {color: #FFFFFF;}

#content .post .article-content .entry-title a:hover {text-decoration: none;}

.post .entry-title a, .search .entry-title a { color: #FFFFFF; transition: all 0.3s ease 0s;}

a {color: #289dcc;}

/*Main Text*/
body, button, input, select, textarea {color: #BBBBBB !important; }

#primary a:hover {text-decoration: underline;}


/*Body*/
#main {padding-top: 30px; padding-bottom: 20px; background-color: #232323 !important;}
.container.wrap {background-color: #232323 !important;}
.panel-default>.panel-heading {background-color: #232323 !important;}
.chapter.row {background-color: #232323 !important;}
.chapter.row:nth-child(even) {background-color: #303030 !important;}

/*Side div*/
#secondary {background-color: #232323 !important;}

#secondary .widget, .error-404 .widget {background-color: #232323 !important;}
.side-nav {background-color: #232323 !important;}
.side-nav .widget {background-color: #232323 !important;}

/*Bottom elements*/
#sharing_email .sharing_send, .sd-content ul li .option a.share-ustom, .sd-content ul li a.sd-button, .sd-content ul li.advanced a.share-more, .sd-content ul li.preview-item div.option.option-smart-off a, .sd-social-icon .sd-content ul li a.sd-button, .sd-social-icon-text .sd-content ul li a.sd-button, .sd-social-official .sd-content>ul>li .digg_button >a, .sd-social-official .sd-content>ul>li>a.sd-button, .sd-social-text .sd-content ul li a.sd-button {background-color: #232323 !important;}

.wpdiscuz-subscribe-bar {background-color: #232323 !important;}

input[type="email"], input[type="password"], textarea {background-color: #232323 !important;}

#wpcomm .wc-form-wrapper {background-color: #232323 !important;}

input[type="text"], input[type="email"], input[type="password"], textarea {background-color: #232323 !important;}

.wpdiscuz-front-actions {background-color: #232323 !important;}

/*Comments*/
#wpcomm .wc-comment .wc-comment-right {background-color: #232323 !important;}

#wpcomm .wc-comment-text {color: #BBBBBB !important;}

.wc-load-more-submit {background-color: #232323 !important;}

/* Forum changes - under review

.wrap-subhead, .wrap-subhead-main {background-color: #232323;}

*, *:before, *:after { background-color: #232323; color: #FFFFFF}

body {background-color: #232323;}

.postbody {background-color: #232323;}
.postbody {color: #BBBBBB;}
*/
body {background-color: #232323; }
}
