css:
@-moz-document domain("moonbunnycafe.com") {
/*Top Nav*/
#access ul li {background: #444444}

#access ul li.current_page_item, #access ul li.current-menu-item {background: #CCCCCC}

#access a {color: #CCCCCC;}

/*Text*/
body, input, textarea {color: #CCCCCC;}

#content .entry-title a, #content .entry-title, #content h1, #content h2, #content h3, #content h4, #content h5, #content h6 {color: #FFFFFF;}

#content {color: #CCCCCC;}

/*Body Color*/
body {background-color: #000000 !important;}

#main {background: #444444;}
    
.bx-wrapper[style] .bx-viewport[style] {background: #444444 !important;}

.entry-meta {background-color: #444444; border: 1px solid #FFF;}
.entry-meta .comments-link {background: #444444; -webkit-box-shadow: inset 1px 1px 0px #444444;}

.commentlist li.comment {background-color: #444444;}

.comment-body {background-color: #444444;}

#respond .form-submit input#submit {color: #ffffff; background-color: #444444; 	-moz-box-shadow: inset 0px -10px 10px 0px #444444; -webkit-box-shadow: inset 0px -10px 10px 0px #444444; box-shadow: inset 0px -10px 10px 0px #444444;}

blockquote {background-color: #444444;}

/*Sidebar*/
#primary {background-color: #444444;}

.widget-title, #footer-widget-area .widget-title {background-color: #000000;}

input[type="text"], input[type="password"], input[type="email"], textarea {background: #444444;}

select {-webkit-appearance: menulist; color: #ffffff; background-color: #000000;}

/*Bottom Nav*/
.pagination span, .pagination a {background: #000000}
}
