css:
@-moz-document domain("forum.canardpc.com") {
.above_body {
    background-image: none;
    /* url(http://i.imgur.com/Y84JCP3.png) !important;*/
    
    background-repeat: no-repeat;
    background-position: center top;
    background-color: transparent;
    height: auto !important;
}
img[src="images/misc/LogoCPC.png"] {
    content: url("https://i.imgsafe.org/c711342.png") !important;
}
.logo-image {
    padding-left: 40%;
}
#blackband {
    display: none;
}
html {
    background: #212121 none;
}
.navtabs {
    background: rgb(51, 51, 51);
}
.toplinks {
    background: none;
}
.toplinks ul.isuser li a {
    color: #D1D1D1;
}
.toplinks ul.isuser li.welcomelink {
    color: #D1D1D1;
}
.navtabs li a.navtab {
    border-top: 1px solid #D1D1D1;
    border-left: 1px solid #D1D1D1;
    background: none;
    color: #D1D1D1;
}
.navtabs li.selected a.navtab {
    color: #D1D1D1;
    background: none;
    padding-top: 0px;
    top: 0px;
}
.navtabs li:first-child {
    border-left: 1px solid #D1D1D1;
}
.navtabs li {
    float: left;
    border-right: 1px solid #D1D1D1;
}
.globalsearch .textboxcontainer {
    border-left: 1px solid #D1D1D1;
}
.navbar_search input {
    border: 1px solid #D1D1D1;
}
.globalsearch .textboxcontainer span {
    border-top: 1px solid #D1D1D1;
    border-left: 1px solid #D1D1D1;
}
.navbar a {
    color: #D1D1D1;
}
.navtabs ul {
    border-top: 1px solid #D1D1D1;
    border-radius: 0px;
}
.navbar_advanced_search li a {
    color: #D1D1D1;
}
.body_wrapper {
    background: #333333;
    border-radius: 0px;
}
html#vbulletin_html {
    color: #D1D1D1;
}
.forumhead {
    background: #212121 none;
    border-radius: 0px;
    border: none;
}
.forumbit_nopost .forumhead .forumtitle a {
    color: #D1D1D1;
}
.forumbit_nopost .forumhead .forumtitle,
.forumbit_nopost .forumhead span,
.forumbit_nopost .forumhead .collapse,
.forumbit_post .forumhead h2 span {
    color: #D1D1D1;
}
.forumhead + .childforum .L2:first-child .forumrow,
.forumhead + .L2 .forumrow {
    background: #212121;
    _background-image: none;
}
.forumbit_nopost .forumbit_nopost .forumrow,
.forumbit_post .forumrow {
    color: #D1D1D1;
    background: none;
    background-color: #212121;
    border: none;
    box-shadow: none;
}
.forumbit_post .forumrow,
.forumbit_nopost .forumhead,
.forumbit_nopost .forumrow,
.forumbit_post .forumhead,
.forumbits .forumhead {
    box-shadow: none;
}
body a {
    color: #D1D1D1;
    text-decoration: none;
}
body a:hover {
    color: #FFFFFF;
    text-decoration: none;
}
#forums a.username {
    color: #D1D1D1;
}
#forums a.username:hover {
    color: #FFFFFF;
}
.navlinks {
    background: #212121 none;
    border: none;
    color: #D1D1D1;
}
.wgo_block .blockhead {
    color: #D1D1D1;
    background: #212121;
    border: none;
}
.wgo_block .blockbody {
    border: none;
    background: #212121;
}
.wgo_block .section {
    color: #D1D1D1;
}
.wgo_block .blocksubhead {
    color: #D1D1D1;
}
.shade {
    color: #D1D1D1;
}
.below_body .time {
    color: #D1D1D1;
}
.wgo_block .time {
    color: #D1D1D1;
}
body {
    color: #D1D1D1;
}
.wgo_block {
    display: block;
    margin-top: 15px;
    -moz-border-radius-topright: 0px;
    -moz-border-radius-topleft: 0px;
    -webkit-border-top-right-radius: 0px;
    -webkit-border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    box-shadow: none;
}
.footer {
    box-shadow: none;
    background: #212121 none;
    border: none;
}
.footer a {
    color: #D1D1D1;
}
.footer a:hover {
    color: #FFFFFF;
}
.threadlisthead {
    border: none;
    background: #212121;
    color: #D1D1D1;
    box-shadow: none;
}
.threadlisthead a {
    color: #D1D1D1;
}
.threadlisthead a:hover {
    color: #FFFFFF;
}
.threadbit {
    color: #D1D1D1;
    box-shadow: none;
}
.threadbit .nonsticky,
.threadbit .discussionrow {
    background: #212121;
}
.threadbit .nonsticky,
.threadbit .deleted,
.threadbit .discussionrow,
.threadbit .ignored {
    border: none;
}
.threadbit .alt {
    background: #212121;
}
.time {
    color: #D1D1D1;
}
.forum_info .blockhead {
    color: #D1D1D1;
    background: #212121;
    border: none;
    box-shadow: none;
    border-radius: 0px;
}
.forum_info {
    color: #D1D1D1;
}
.forum_info .blockbody {
    border: none;
    background: #212121;
    box-shadow: none;
}
.toolsmenu {
    background: #212121;
    border: none;
}
.toolsmenu .popupgroup a.popupctrl,
.toolsmenu .popupgroup .popupmenu a.popupctrl,
.toolsmenu .popupgroup .popupmenu:hover a.popupctrl,
.toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {
    color: #D1D1D1;
}
.pagination span a.popupctrl {
    background: #212121;
}
.pagination span a {
    border: none;
    background: #212121;
    color: #D1D1D1;
}
.pagination span a:hover {
    border: none;
    background: #000000;
    color: #FFFFFF;
    font-weight: bold;
}
.pagination span.selected a {
    background: #D1D1D1;
    color: #212121;
    border: none;
}
.threadbit .sticky {
    background: #212121;
    border: none;
}
.threadbit .pagination a {
    color: #D1D1D1;
}
.threadbit .pagination a:hover {
    color: #FFFFFF;
}
.newcontent_textcontrol {
    background: #212121;
    border-radius: 0px;
    box-shadow: none;
    color: #D1D1D1;
    border: 0;
}
.postbit .posthead,
.postbitlegacy .posthead,
.eventbit .eventhead {
    background: #212121;
    _background-image: none;
    border: none;
    color: #D1D1D1;
}
.postbitlegacy .posthead .time {
    color: #D1D1D1;
}
.postbitlegacy .postdetails,
.eventbit .eventdetails {
    background: #212121 none;
}
.postbitlegacy .userinfo {
    background: #212121 none;
}
.postbitlegacy .postbody,
.eventbit .eventdetails .eventbody {
    border-left: none;
    background: #616161 none;
}
.postbitlegacy .postrow,
.eventbit .eventrow,
.postbitdeleted .postrow,
.postbitignored .postrow {
    color: #D1D1D1;
}
.postbit,
.postbitlegacy,
.eventbit {
    border: none;
    color: #D1D1D1;
}
.bbcode_container div.bbcode_quote {
    background: #000000 none;
    border-radius: 0px;
    border: 1px solid #D1D1D1;
}
.postbit .postfoot .textcontrols,
.postbitlegacy .postfoot .textcontrols,
.eventbit .eventfoot .eventcontrols {
    background: #616161 none;
}
.postbitlegacy .postfoot .textcontrols a.quickreply,
.postbit .postfoot .textcontrols a.quickreply,
.postbitlegacy .postfoot .textcontrols a.forwardpost,
.postbit .postfoot .textcontrols a.forwardpost {
    background: url(http://forum.canardpc.com/images/buttons/reply_40b.png) no-repeat #616161 left;
    color: #D1D1D1;
}
.postbitlegacy .postfoot .textcontrols a.newreply,
.postbit .postfoot .textcontrols a.newreply {
    background: url(http://forum.canardpc.com/images/buttons/quote_40b.png) no-repeat #616161 left;
    color: #D1D1D1;
}
.postbitlegacy .postfoot .textcontrols a.multiquote,
.postbit .postfoot .textcontrols a.multiquote {
    background: url(http://forum.canardpc.com/images/buttons/multiquote-back_40b.png) no-repeat #616161 left;
}
.postbitlegacy .postfoot .textcontrols a.report,
.postbit .postfoot .textcontrols a.report {
    background: url(http://forum.canardpc.com/images/buttons/report-40b.png) no-repeat #616161 left;
}
.blockhead {
    color: #D1D1D1;
    background: #212121;
    border: none;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
}
.formcontrols .blockrow {
    background: #212121;
    border: none;
}
.formcontrols {
    border: none;
}
.texteditor.forum .cke_skin_kama textarea.cke_source,
.texteditor.forum textarea {
    color: #D1D1D1;
    background: #616161 none;
}
.cke_skin_kama .cke_wrapper.cke_ltr,
.cke_skin_kama .cke_wrapper.cke_rtl {
    background: #212121;
}
.thread_info h4 {
    color: #D1D1D1;
    background: #212121;
    border: none;
    box-shadow: none;
}
.thread_info .blockbody {
    border: none;
    background: #212121;
    box-shadow: none;
}
.thread_info {
    color: #D1D1D1;
}
.above_threadlist .threadpagenav .threadpagestats {
    color: #D1D1D1;
}
li.activitybit {
    color: #D1D1D1;
    background: #616161 none no-repeat;
}
li.activitybit a {
    font-weight: bold;
    color: #D1D1D1;
}
.userprof_headers {
    color: #D1D1D1;
    background-color: #212121;
}
.blockbody {
    background: #616161 none no-repeat;
}
.userprof_content {
    color: #D1D1D1;
    background-color: #616161;
}
.member_content a {
    color: #D1D1D1;
}
.member_content a:hover {
    color: #FFFFFF;
}
dl.stats dt,
#sidebar_container dl.stats,
#sidebar_container .blockbody.userprof_content,
#sidebar_container .userprof_content .time,
.userprof_content .time {
    color: #D1D1D1;
}
.userprof_headers_border {
    border: none;
}
.userprof_content_border {
    border: none;
}
#sidebar_container.member_summary {
    color: #D1D1D1;
    background-color: #616161;
    background-image: none;
    background-repeat: no-repeat;
    border: none;
    box-shadow: none;
}
.moduleinactive_bg {
    background-image: none;
}
#sidebar_container .blocksubhead {
    color: #D1D1D1;
    border: none;
}
#sidebar_container a {
    color: #D1D1D1;
}
#sidebar_container a:hover {
    color: #FFFFFF;
}
#sidebar_container .userprof_content .blockrow {
    color: #D1D1D1;
    background-color: #212121;
    border: none;
}
#sidebar_container .userprof_content a {
    color: #D1D1D1;
}
#sidebar_container .userprof_content a:hover {
    color: #FFFFFF;
}
.above_postlist .pagination_top .pagination,
.above_postlist .pagination_top .postpagestats {
    color: #D1D1D1;
}
.textbox,
textarea,
select {
    color: #FFFFFF;
    background: #616161;
    border: none;
    border-radius: 0px;
}
.lastedited {
    color: #D1D1D1;
}
.navpopupmenu.popupmenu.nohovermenu a.textcontrol,
.navpopupmenu.popupmenu.nohovermenu a.popupctrl {
    color: #D1D1D1;
    background: #212121;
}
.button {
    padding: 2px 2px;
}
.button {
    color: #D1D1D1;
    background: #212121;
}
.cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_ok,
.cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_cancel,
.textcontrols a,
a.textcontrol {
    color: #D1D1D1;
    background: #212121;
}
.polloptionsblock {
    padding-left: 120px;
    background: #212121 url(http://forum.canardpc.com/images/misc/poll_question_mark.png) no-repeat 30px 10px;
}
#pollinfo {
    border-radius: 0px;
    border: none;
}
#pollinfo .polltitle {
    color: #D1D1D1;
}
.postbitdeleted,
.postbitignored {
    color: #D1D1D1;
    border: none;
    background: #616161 none;
}
.postbitdeleted .posthead,
.postbitignored .posthead {
    background: #212121;
    border: none;
    color: #ffffff;
}
.postbitdeleted .nodecontrols,
.postbitignored .nodecontrols {
    background: #616161 none;
}
.postbitdeleted .nodecontrols .textcontrol,
.postbitignored .nodecontrols .textcontrol {
    background: #616161;
    border: 0px solid #a8a8a8;
    font: bold 11px Tahoma, Calibri, Verdana, Geneva, sans-serif;
}
}
