css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
#header {
    background: url(https://writergirlkp.files.wordpress.com/2013/11/black-windows_542931.jpg);
}
.userbar:before {
    background: url(http://i.imgur.com/wgVeUXt.gif);
}
a {
    color: #3498db;
}
body,
.mainmenuwrapper,
.ps-room {
    background: url(http://56.media.tumblr.com/0988858422a85dab6c76be656d5c539c/tumblr_nyq62873NC1uowfp3o4_r1_540.jpg)  no-repeat scroll ;
    background-size: cover;
}
.highlighted {
    background: #ccffff;
}
.message-announce {
    background: #ccffff;
    color: black;
    padding: 1px 4px 2px;
}
.chat.timer {
    color: #e74c3c;
}
#lobby-userlist-users small {
    color: #ecf0f1;
}
.ladder strong,
.ladder em {
    color: #131313 !important;
}
.mainmenufooter small:after {
    content: "";
    text-shadow: 0 1px black;
    position: absolute;
    left: 0px;
    top: -40px;
    width: 200px;
}
.tabbar {
    bottom: 0;
}
.userbar {
    top: 18px;
}
.userbar .username {
    text-shadow: none;
    text-shadow: 1px 1px black;
}
.userbar:before {
    display: block;
    content: "";
    width: 32px;
    height: 32px;
    float: left;
    position: relative;
    top: -16px;
}
.icon-user {
    display: none;
}
.siderooms {
    background: clear;
    height: 56px;
}
.maintabbarbottom {
    background: linear-gradient(to bottom, rgba(0, 0, 0, 1) 0%, rgba(0, 0, 0, 0) 100%);
    border: none;
    box-shadow: none;
    z-index: 2;
}
.header {
    height: 56px;
    background: #1B6AA5;
}
.header .logo {
    float: left;
    margin: 7px 0px 0 2px;
}
.tabbar .closebutton,
.tabbar a.cur+.closebutton {
    top: 19px;
}
.closebutton:hover {
    color: #1b1b1b;
}
.closebutton {
    color: #ecf0f1;
}
.tabbar a.button.notifying {
    background: #3498db;
}
.maintabbar .inner {
    padding-top: 0px;
    height: 56px;
    background: transparent;
}
.tabbar.maintabbar {
    margin-left: 170px;
    margin-right: 0px;
    overflow: hidden;
    height: 60px;
    background-position: right;
    background-repeat: no-repeat;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover,
.tabbar a.button {
    color: #ecf0f1;
    background: white;
    box-shadow: none;
    border: none;
    cursor: default;
    top: 0px;
    border-radius: 5px;
    text-shadow: none;
    margin-left: 5px;
    margin-top: 15px;
}
.tabbar a.button:hover,
.tabbar a.button.cur {
    background: white;
}
.tabbar a.button {
    cursor: pointer;
    text-align: center;
    white-space: nowrap;
    text-shadow: none;
    font-size: 11px;
    font-family: comic sans ms;
}
.ps-popup {
    position: absolute;
    top: auto;
    right: auto;
    left: auto;
    bottom: auto;
    text-align: left;
    padding: 2px 10px;
    z-index: 20;
    border: 1px solid #181818;
    border-radius: 2px;
    box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, .2);
    background: url(http://cdn.squares.im/pony/pic/photo/2013/05/b33cae3db80ae21146ff098f27336f20_1024.jpg);
}
.battle-log-add,
.chat-log-add {
    border-color: #bbb;
    background: #ffb3ff;
    opacity: .7;
    text-shadow: 1px 1px black;
}
.message-pm {
    color: #9b59b6;
}
.userlist {
    background: url(http://www.dynamicevents.ie/wp-content/uploads/2015/08/Bricks-Texture-Black-Background.jpg);
}
.userlist li button:hover {
    background: #330066;
}
.userlist li {
    background: url(http://www.dynamicevents.ie/wp-content/uploads/2015/08/Bricks-Texture-Black-Background.jpg);
    border-bottom: 1px solid #000000;
}
.battle-log {
    background: url(http://i.imgur.com/7VtJQdy.jpg?1);
    color: #ecf0f1;
    border: 3px solid #000000;
}
.chat-log {
    font-weight: normal;
    font-size: 12pt;
    font-family: comic sans ms;
    color: white;
    background: url(http://i.imgur.com/XoKzd8w.png) no-repeat scroll center;
    background-size: cover;
    border-width: 1;
}
.ps-room.ps-room-light.scrollable {
    background: url(http://puu.sh/n5Szx/0cbca3fc5f.png);
    background-size: cover;
}
.chat small {
    font-weight: normal;
    font-size: 8pt;
    color: #000080;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {
    background: #330066;
    border: none;
    color: #FFF;
    box-shadow: none;
    border: none;
    cursor: default;
    top: 0px;
    border-radius: 5px;
    text-shadow: none;
    margin-top: 0px;
}
.tabbar a.button {
    background: #330066;
    color: #FFF;
    border: none;
    margin-left: 20px;
    border-radius: 5px;
}
.username {
    text-shadow: 1px 1px black;
}
.battle-log h2 {
    position: relative;
    border: 0px solid #AAAAAA;
    background: #161616;
    background-color: black!important;
}
.battle-log h2:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/1dbPeSk.png);
    width: 31px;
    height: 18px;
    position: absolute;
    right: 5px;
    top: 5px;
}
.battle-log .chat em {
    padding: 1px 4px 1px 3px;
    color: #ecf0f1;
    font-style: normal;
}
.battle-log .chat strong {
    color: #ecf0f1;
}
.challenge {
    background: #fcd2b3;
    border: 1px solid #2980b9;
    color: rgba(0, 0, 0, .6);
    padding: 1px 5px;
}
.pm-log em {
    color: white;
}
.pm-box .focused {
    background: #1b1b1b;
}
.pm-window.focused h3:hover {
    background: #3498db;
}
.pm-window h3,
.pm-window.focused h3 {
    color: #ecf0f1;
    background: #1b1b1b;
    margin: 0;
    padding: 2px 5px;
    font-size: 11pt;
    border-width: 0;
    cursor: pointer;
    border-radius: 0px;
}
.pm-log {
    min-height: 100px;
    max-height: 300px;
    background: url(http://cdn.squares.im/pony/pic/photo/2013/05/b33cae3db80ae21146ff098f27336f20_1024.jpg) center top scroll;
    color: white;
    font-weight: normal;
    word-wrap: break-word;
    overflow: auto;
    border: 0px solid #000000;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}
.pm-log-add {
    border-width: 0;
    background: black;
    border-radius: 0px;
}
.ladder table {
    background-color: #ecf0f1;
}
.infobox,
.ladder,
.pm-window {
    position: relative;
    border: 1px solid #181818;
    border-radius: 2px;
    box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, .2);
    margin-top: 5px;
    color: #1b1b1b;
    background-color: #ecf0f1;
}
.utilichart {
    position: relative;
    border: 1px solid #181818;
    border-radius: 2px;
    box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, .2);
    background: url(http://i.imgur.com/SHCpB0w.png);
    margin-top: 5px;
    min-height: 33px;
    width: 270px;
    height: 96px;
}
.utilichart:after,
.infobox:after,
.ladder:after,
.pm-window:after,
.ps-popup:after,
#tooltipwrapper .tooltip:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/GCuRjnR.png);
    width: 45px;
    height: 7px;
    position: absolute;
    left: -1px;
    top: -7px;
}
.utilichart .numcol,
.utilichart .movenamecol,
.utilichart .namecol {
    padding: 0;
    margin: 0;
    text-align: left;
    font-weight: bold;
    color: #1b1b1b;
    position: absolute;
    left: 10px;
    top: 2px;
    font-size: 8pt;
}
.utilichart .pokemonnamecol {
    padding: 0;
    margin: 0;
    font-weight: bold;
    color: #1b1b1b;
    position: absolute;
    left: 40px;
    top: 2px;
    font-size: 8pt;
}
.utilichart .typecol {
    width: 100px;
    height: 0;
    padding: 0;
    margin: 0;
    position: absolute;
    left: 10px;
    top: 18px;
}
.utilichart .typecol img {
    opacity: 1;
    margin-right: 10px;
}
.utilichart .iconcol,
.utilichart .itemiconcol {
    position: absolute;
    right: 5px;
    top: 3px;
}
.utilichart .abilitycol,
.utilichart .twoabilitycol {
    float: left;
    color: #1b1b1b;
    margin: 34px 40px 0 0px;
    width: 85px;
    font-size: 8pt;
}
.utilichart .abilitycol em,
.utilichart .statcol em,
.utilichart .bstcol em,
.utilichart .twoabilitycol,
.utilichart .labelcol em,
.utilichart .widelabelcol em,
.utilichart .pplabelcol em {
    color: #1b1b1b;
}
.utilichart .statcol,
.utilichart .bstcol {
    position: relative;
    left: -5px;
    top: 3px;
    text-align: center;
    margin-right: 12px;
}
.utilichart .itemdesccol,
.utilichart .abilitydesccol,
.utilichart .movedesccol {
    white-space: normal;
    padding-top: 7px;
    font-size: 8pt;
    width: 250px;
    height: 50px;
    overflow: visible;
    color: #777777;
    position: absolute;
    top: 28px;
    left: 10px;
}
.utilichart .labelcol,
.utilichart .widelabelcol,
.utilichart .pplabelcol {
    padding-top: 0;
    height: 28px;
    width: 24px;
    text-align: center;
    font-size: 8pt;
    margin: 65px 45px 0 0;
}
.popupmenu h3 {
    margin: 5px 0 0 -2px;
    padding: 1px 0;
    color: black;
}
.popupmenu button {
    color: black;
}
.ps-room.ps-room-opaque {
    background: #333 !important;
    color: black;
}
.tournament-title {
    background: url(http://i.imgur.com/Q8vHT0Y.png)
}
.ps-room.ps-room {
    background: url(http://i.imgur.com/Q8vHT0Y.png);
    position: absolute;
    color: #1a6e9a;
}
.select:disabled.preselected {
    color: whitesmoke;
    background: rgba(0, 0, 0, .55);
    border-color: black;
}
.popupmenu button:hover,
.popupmenu button.sel:hover {
    padding: 0 3px;
    border: 1px solid #000;
    background: #77775c;
}
.mainmenufooter a {
    text-decoration: underline;
    color: #fff;
}
.infobox {
    opacity: 0.9;
    background-color: #e6ccff;
}
a.ilink {
    text-decoration: none;
    color: #5F77A7;
}
.teamchartbox {
    border-top: 0;
}
.ps-room {
    position: absolute;
    display: block;
    top: 56px;
    left: 0;
    right: 0;
    bottom: 0;
    border: 1px solid #bbb;
    border-top: none;
}
.ps-room .battle {
    top: 60px;
    border-width: 0px;
}
.foehint {
    position: relative;
    top: 45px;
}
.battle {
    border: 0px solid #AAAAAA;
    width: 640px;
    height: 360px;
    overflow: visible;
    font-family: Verdana, sans-serif;
    font-size: 10pt;
}
.backdrop:before {
    display: block;
    content: "";
    background: url(http://36.media.tumblr.com/tumblr_m971s6AX2R1rea69mo1_1280.png) left center;
    background-size: cover;
    width: 640px;
    height: 360px;
}
.backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
}
.backdrop:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/zQ6bUvr.png);
    position: absolute;
    top: 0;
    left: 0;
    width: 640px;
    height: 360px;
}
.ps-room .battle-controls {
    top: 430px;
}
.turn {
    position: absolute;
    top: -42px;
    left: 280px !important;
    width: 101px;
    height: 17px;
    font-size: 8pt;
    font-weight: bold;
    margin: 0px;
    padding: 0px;
    padding-top: 35px;
    border: 0;
    border-radius: 0;
    opacity: 1 !important;
    background: url(http://i.imgur.com/Ng0wOlV.png);
    text-align: center;
    color: #FFF;
}
.rightbar {
    position: absolute;
    top: -60px;
    right: 0;
    height: 100px;
    width: 320px;
    background-color: rgba(255, 255, 255, 0);
    border: 0px solid rgba(215, 215, 215, 0.5);
    color: #777777;
    background-image: url(http://i.imgur.com/WEdUzyR.png);
    background-position: 0px -35px;
}
.rightbar .trainer {
    margin-top: 0px;
    text-align: right;
}
.rightbar .trainersprite {
    position: absolute;
    top: 5px;
    left: 40px;
    opacity: 1 !important;
}
.trainer strong {
    display: block;
    font-size: 8pt;
    overflow: hidden;
    padding-top: 45px;
    padding-right: 10px;
    color: #111;
}
.rightbar .trainer .teamicons {
    float: left;
    position: relative;
    top: -42px;
    left: 108px;
}
.leftbar {
    position: absolute;
    top: -60px;
    left: 0;
    height: 100px;
    width: 320px;
    background-color: rgba(255, 255, 255, 0);
    border: 0px solid rgba(215, 215, 215, 0.5);
    color: #777777;
    background-image: url(http://i.imgur.com/Y68Rmc5.png);
    background-position: 0px -35px;
}
.leftbar .trainer {
    margin-top: 0px;
    text-align: left;
}
.leftbar .trainersprite {
    position: absolute;
    top: 5px;
    right: 40px;
    opacity: 1 !important;
}
.leftbar .trainer strong {
    display: block;
    font-size: 8pt;
    overflow: hidden;
    padding-top: 45px;
    padding-left: 10px;
}
.leftbar .trainer .teamicons {
    float: right;
    position: relative;
    top: -42px;
    right: 108px;
}
.weather {
    text-shadow: none;
    color: rgba(0, 0, 0, 0);
}
.weather em small {
    font-size: 7pt;
    color: white;
    text-shadow: 1px 1px black;
    position: absolute;
    left: 0;
    top: 85px;
    width: 95px;
    text-align: center;
}
.sunweather,
.sunnydayweather {
    background: url(http://vignette2.wikia.nocookie.net/gravity-falls/images/b/b3/1400x907_gravity-falls_background.jpg/revision/latest?cb=20120917173111&path-prefix=es) no-repeat scroll left top;
}
.rainweather,
.raindanceweather {
    background: url(https://secure.static.tumblr.com/8286e8b4c0bf2281d3c669fc406294a4/w3m8ykz/vHQnkihk1/tumblr_static_a5l4ujrjclk4g4k0c8kkoogk8_640_v2.jpg) no-repeat scroll left top;
}
.hailweather {
    background: url(http://p1.pichost.me/i/39/1629350.png) no-repeat scroll left top;
}
.sandstormweather {
    background: url(http://i.imgur.com/CbsvtPl.png) no-repeat scroll left top;
}
.pseudoweather {
    background: url(http://38.media.tumblr.com/a03bbbd3bcac8e91488a51a5261107b7/tumblr_inline_ngci6qbi8s1rb6ius.png) no-repeat scroll left top;
}
.desolatelandweather {
    background: url(http://i.imgur.com/ZERrDjt.png) no-repeat scroll left top;
}
.primordialseaweather {
    background: url(http://i.imgur.com/D6p3zZQ.png) no-repeat scroll left top;
}
.deltastreamweather {
    background: url(http://i.imgur.com/11qewQj.png) no-repeat scroll left top;
}
.whatdo {
    width: 424px;
    color: #333 !important;
    position: absolute;
    left: 150px;
    top: -17px;
    cursor: default;
    position: absolute;
    top: -5px;
    width: 624px;
}
.whatdo .healthy {
    position: absolute;
    right: 8px;
}
.movecontrols {
    float: left;
    /*margin-left: 27px;*/
}
.movemenu {
    width: 302px;
}
.movemenu button {
    width: 150px;
    height: 50px;
    border-radius: 25px;
    border-width: 0;
    box-shadow: none;
    color: black;
    font-size: 10pt;
    font-family: Arial, sans-serif;
    padding-top: 0px;
    margin: 0px;
}
.movemenu button:hover {
    top: -2px;
}
.movemenu button small {
    text-align: center;
    width: 62px;
    margin-top: 0px;
}
.movemenu button small.type {
    display: none;
}
.movemenu button small.pp {
    padding: 6px 0px 2px;
    color: white;
    font-size: 8pt;
    text-shadow: none;
}
.movemenu button small.pp:before {
    content: 'PP ';
}
.movemenu button.type-Normal,
.movemenu button.type-Normal:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -50px !important;
}
.movemenu button.type-Fire,
.movemenu button.type-Fire:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -50px !important;
}
.movemenu button.type-Water,
.movemenu button.type-Water:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px -150px !important;
}
.movemenu button.type-Electric,
.movemenu button.type-Electric:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px 0px !important;
}
.movemenu button.type-Grass,
.movemenu button.type-Grass:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px -100px !important;
}
.movemenu button.type-Ice,
.movemenu button.type-Ice:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px 0px !important;
}
.movemenu button.type-Fighting,
.movemenu button.type-Fighting:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -150px !important;
}
.movemenu button.type-Poison,
.movemenu button.type-Poison:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px 0px !important;
}
.movemenu button.type-Ground,
.movemenu button.type-Ground:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -150px !important;
}
.movemenu button.type-Flying,
.movemenu button.type-Flying:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -50px !important;
}
.movemenu button.type-Psychic,
.movemenu button.type-Psychic:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px 0px !important;
}
.movemenu button.type-Bug,
.movemenu button.type-Bug:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -100px !important;
}
.movemenu button.type-Rock,
.movemenu button.type-Rock:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -200px !important;
}
.movemenu button.type-Ghost,
.movemenu button.type-Ghost:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px -50px !important;
}
.movemenu button.type-Dragon,
.movemenu button.type-Dragon:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -150px !important;
}
.movemenu button.type-Dark,
.movemenu button.type-Dark:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -100px !important;
}
.movemenu button.type-Steel,
.movemenu button.type-Steel:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -200px !important;
}
.movemenu button.type-Fairy,
.movemenu button.type-Fairy:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -100px !important;
}
.movemenu button.type-undefined,
.movemenu button.type-undefined:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -200px !important;
}
.movemenu button:disabled {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -200px !important;
    opacity: .5;
}
.movemenu label {
    width: 85px;
    text-align: center;
    font-size: 0;
    position: absolute;
    top: 55px;
    right: 250px;
}
.movemenu label input {
    position: relative;
    top: 1px;
}
.movemenu label:after {
    content: " Mega evolution";
    font-size: 11pt;
    color: #999;
}
.shiftselect {
    float: left;
    padding-left: 13px;
    width: 72px;
}
.switchcontrols {
    float: left;
}
.movecontrols ~ .switchcontrols {
    float: right;
}
/*.movecontrols + .switchcontrols {
        padding-left: 85px;
}*/

.switchselect button {
    color: #384;
}
.switchselect ~ .switchmenu {
    width: 248px;
    margin: 0px;
}
.switchmenu button,
.switchmenu button:hover,
.switchmenu button:disabled {
    border: 1px solid black !important;
    box-shadow: none;
    font-size: 8pt;
    right: 0px;
    color: #111111 !important;
    border-radius: 18px;
    height: 36px;
