css:
@-moz-document domain("segmentfault.com") {
body.qa-question,
body.blog-blog.have-notify,
.post-topheader,
body.qa-index,
body.event-index,
body.user-settings,
body.job-index,
.search-job,
.global-nav,
footer#footer,
.page,
.main,
body.user-note,
body.company-list,
body.blog-write,
body.tag-detail,
.company.edit-body.edit-mode,
.company-benfits.edit-body.edit-mode,
.company-address.edit-body.edit-mode,
body.company-detail,
body.page-about,
header.profile__heading,
.popover,
body.timeline-index,
.editor-preview,
div#noteText,
.wrap,
.sf-header.sf-header__shadow.sf-header__fixed,
.init,
.user-profile__main,
body.note-record,
.sf-typeHelper,
body.user-resume,
body.blog-post,
body.qa-editQuestion,
body.blog-blog,
body.blog-index,
.blog-create,
.widget-invite.mt30,
body.search-index,
body.tag-index,
body.qa-ask {
    background-color: #222 !important;
    background-image: url("http://ww1.sinaimg.cn/thumbnail/e8d7aaadgw1f0xv2ny1y5j202s02s3yb.jpg") !important;
}
.popover {
    border: solid 1px white;
}
.answers.answered.solved {
    background-color: #463FB5 !important;
}
.editor-preview .sf-typeHelper,
.wall.tag__wall,
.panel-heading,
.panel.panel-default,
textarea.post__reply--textarea.form-control,
.panel-footer,
.modal-content,
.opts__item--message,
.publish-footer,
section.tag-info.tag__info {
    background-color: rgba(53, 52, 52, 0) !important;
    background-image: url("http://ww1.sinaimg.cn/thumbnail/e8d7aaadgw1f0xv2ny1y5j202s02s3yb.jpg") !important;
}
.global-nav.shadow-bottom.publish,
.alert.alert-warning.topframe {
    background-color: #CDDC39 !important;
}
div#questionText {
    border: none !important;
}
form.editor-wrap {
    background-color: rgba(255, 255, 255, 0.12) !important;
}
.CodeMirror-gutter.CodeMirror-linenumbers {
    background-color: black !important;
}
.stream-list__item {
    margin: 6px !important;
    border-bottom: 1px solid #005F3C !important;
    padding-top: 15px !important;
}
.stream-list__item .title a {
    color: #0BAD12 !important;
    font-size: 18px !important;
    font-weight: bold !important;
}
.stream-list__item .excerpt {
    color: #CDDC39 !important;
}
.profile-mine__content--title {
    font-size: 15px !important;
    color: #00BCD4 !important;
}
.global-nav .opts__item--message .panel-body ul li {
    border-bottom: 1px solid #4CAF50 !important;
}
.nav-tabs-zen>li>a {
    color: #C4ABAB !important;
}
input#searchBox,
input.input-lg.form-control {
    background-color: rgba(255, 255, 255, 0.18) !important;
}
h4.modal-title {
    color: black !important;
}
.modal-header {
    background-color: #009688 !important;
    border-bottom: #009688 !important;
}
.nav-tabs {
    border-bottom: 1px solid #005F3C !important;
}
.stream-list__item:hover,
section.widget-question:hover {
    -webkit-box-shadow: 0 0 10px #000 !important;
    -moz-box-shadow: 0 0 10px #000 !important;
    -webkit-border-radius: 5px !important;
    -moz-border-radius: 5px !important;
    background-color: rgba(255, 255, 255, 0.2) !important;
    position: relative !important;
}
section.widget-question {
    border-top: solid 1px green !important;
}
.widget-invite {
    border: none !important;
}
.side-ask.alert.alert-warning {
    background-color: #141413 !important;
    color: #2196F3 !important;
    border: none !important;
}
div#fixedTools,
.post-nav.hidden-xs.side-outline.hidden-sm.fixed {
    display: none !important;
}
.widget-action--ver strong {
    color: #3AAF6E !important;
}
input#searchBox:HOVER,
form.editor-wrap:HOVER,
li.bg-warning:HOVER,
.global-nav .opts__item--message .panel-body ul li:HOVER {
    -webkit-transition: border linear .2s, -webkit-box-shadow linear .5s;
    border-color: rgba(5, 248, 46, 1);
    -webkit-box-shadow: 0 0 18px rgba(5, 248, 46, 1);
}
.post-topheader .title a,
.post-topheader .title a:active,
.post-topheader .title a:hover,
.form-control,
output {
    color: #31708f !important;
}
.post-topheader {
    border-bottom: #005F3C solid 5px !important;
}
section.stream-list__item,
th,
code,
textarea#answerEditor {
    background-color: rgba(242, 242, 242, 0) !important;
}
body,
.widget-comments {
    color: rgba(243, 243, 243, 0.75) !important;
}
.fmt pre code {
    font-size: 18px !important;
}
a.tag.tag-sm,
a.tag {
    background-color: rgba(255, 193, 7, 0.92) !important;
    border-radius: 5px !important;
}
.tag[href]:focus,
.tag[href]:hover {
    color: #AA1414 !important;
}
.nav-tabs-zen>li.active>a,
.nav-tabs-zen>li.active>a:focus,
.nav-tabs-zen>li.active>a:hover {
    background-color: rgb(0, 95, 60) !important;
    border-bottom: none !important;
    color: white !important;
}
.post-topheader .title a,
.post-topheader .title a:active,
.post-topheader .title a:hover,
.form-control,
output {
    color: #00BCD4 !important;
}
.job-index .job-name,
.job-list .job-name,
.company-list .company-list .media .media-heading>a,
.company-detail .company-position-item h4>a {
    color: #0EA414!important;
}
pre,
.widget-comments {
    background-color: rgba(255, 255, 255, 0.05)!important;
    color: #E91E63 !important;
    border-radius: 20px !important;
}
.col-xs-12.col-md-9.main {
    width: 100% !important;
}
.mCSB_dragger_bar {
    background-color: #F44336 !important;
}
.col-xs-12.col-md-3.side {
    margin-left: 50px !important;
    width: 90% !important;
    border-top: solid #06A655 5px !important;
}
.widget-box.no-border,
.widget-box:first-child {
    margin-top: 25px !important;
}
.post-title,
.widget-answers__item {
    border-bottom: 2px solid #06A655 !important;
}
#footer {
    border-top: 5px solid #06A655 !important;
}
p,
.wmd,
textarea#myEditor,
input#myTitle,
input#editReason {
    background-color: transparent !important;
}
a:hover {
    /*color: #0000FF ;*/
    
    -webkit-transition-property: color;
    -webkit-transition-duration: 0.0s;
    font-weight: bold
}
img:hover {
    box-shadow: 0px 0px 4px 4px rgba(130, 190, 10, 0.6) !important;
    -webkit-transition-property: box-shadow;
    -webkit-transition-duration: .31s;
}
img {
    -webkit-transition-property: box-shadow;
    -webkit-transition-duration: .31s;
}
.post-topheader__info--title a {
    color: #009a61 !important;
}
.post-topheader__side strong {
    color: #009a61 !important;
}
}
