@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("nagytibor.xyz") {
  body
  {
    background:url('http://i.imgur.com/cn7Dkal.jpg') no-repeat center center fixed;
    background-size:cover;
  }
  #tarolo
  {
    background:url('http://i.imgur.com/7nNXocI.png') no-repeat center center fixed;
    background-size:cover;
    border:none;
    border-radius:0px;
    border-collapse:collapse;
    top:0%;
  }
  ul
  {
    list-style-type:none;
  }
  a, a:link, a:visited, a:hover, a:active
  {
    color:black;
    text-decoration:underline;
  }
  #fejlec
  {
    background:url('http://i.imgur.com/NZutVet.jpg');
    border-radius:0px;
  }
  #robot, #lego
  {
    display:none;
  }
  input[name=felha],input[name=jel]
  {
    text-align:center;
  }
  #loginablak
  {
    margin-top: -67px;
  }
  input[name=submit]
  {
    margin-top: 10px;
  }
  input[name=FELTOLT]
  {
    border-radius:10px;
    padding:2px 5px;
  }
  #menu
  {
    width:459px;
    margin:0px auto 16px auto;
  }
  #menu ul
  {
    background:black;
  }
  #menu ul li:hover
  {
    background:#1275ae;
  }
  #menu ul li, #menu ul li a
  {
    border-right:none;
  }
  .alcim
  {
    text-decoration:underline;
  }
  .lezarthf li
  {
    text-decoration:line-through;
  } 
  #lab
  {
    background-color:black;
    border-radius:0px;
    color:white;
  }
  #ballab
  {
    visibility:hidden;
    width:131px;
    padding-left:10px;
  }
  #ballab:after
  {
    content:'Design: Nature © 2016 AX';
    visibility:visible;
  }
  /*WEBKIT BROWSERS*/
  @supports(-webkit-appearance:none)
  {
    #menu
    {
      width:463px;
    }
    #ballab
    {
      width:132px;
    }
    #loginablak
    {
      margin-top: -64px;
      margin-left: -164px;
    }
  }
}
