css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ldlc.com") {
  
  body {
    background: black !important;
    color: whitesmoke;
  }
    
  /*Pop-up du jour*/
  #cboxContent,
  #cboxOverlay,
  .productasso {
    display: none !important;
  }
  
  /*couleur blanc -> text*/
  .zoomsur dt a,
  #cathead .catlist .cat_finales .title_cat_parent,
  .cbFilter input, .cbFilter label,
  .productListingHeader label,
  .productListing.listing th a,
  .productListing.listing td.designation .nom,
  .productListing.listing td.designation .caract,
  .productanchors a,
  #productParametersList .productParameter,
  .chronoNote,
  .shortcutProduct,
  #footer #ftservices em,
  #ftmodepaiement,
  #ftlinks,
  #fthelp span.h4Ex, 
  .rkw .rkwTitle,
  .rkw,
  #footer {
    color: whitesmoke !important;
  }
  
  /*couleur cyan -> lien*/
  a,
  #filterBloc span.h2ex,
  .public-catalogue-categorypage #cathead .wrapperDesc.resume div.desc #cathead-descriptif span,
  .productanchors li.on a,
  .productanchors a:hover,
  h3,
  .productListing.listinglight .prix {
    color: #81CFE0 !important;
  }
  
  /*Fond sur les titres + Aucun fond*/
  h2.pListTitle,
  h3.pListTitle,
  .homerightcol h2,
  .seealltitle,
  .productListing.listing td.group,
  #productfiche h2,
  #fichetech h2,
  .productpress .wrapper h2,
  .productcomments .wrapper h2,
  .producttops .title,
  .similarproducts .wrapper h2,
  .productpress .itemlist li.odd,
  .productcomments ul li.odd,
  .footerSeparator {
    background: none;
  }
  
  /*Fond categorie active*/
  .topCat .liTab.current a.catName,
  body.worldInfo .topCat .liTab.worldInfo a.catName,
  body.worldImag .topCat .liTab.worldImag a.catName,
  body.worldPhon .topCat .liTab.worldPhon a.catName,
  body.worldJeux .topCat .liTab.worldJeux a.catName,
  body.worldCnso .topCat .liTab.worldCnso a.catName,
  body.worldCnct .topCat .liTab.worldCnct a.catName,
  body.worldLogi .topCat .liTab.worldLogi a.catName,
  #filterBloc #linkedCategories a.roundedbutton {
    background: #252c33;
  }
  
  /*fond categorie inactive*/
  .topCat .liTab a.catName {
    background: black;
  }
  
  .topCat .liTab a.catName {
    border-radius: initial;
  }
  
  .homeleftcol .boutiques,
  .rounded,
  .rounded2,
  .homeleftcol .guideActus,
  .socialHome {
    background: #1c2126;
    box-shadow : initial;
  }
  
  #header .topCat .liTab.selected a.catName,
  .topCat .contentTab .header {
    background: #252c33;
  }
  
  /*Grille principale*/
  #homecontent {
    background: #1c2126;
    color: whitesmoke;
  }
  
  /*Titre Filtre*/
  
  .valFilterList input {
    color: whitesmoke;
    background: #343c45 url(http://www.ldlc.com/v3/img/general/ico-fleche-bas.gif) no-repeat 98% 50%;
}
  
  /*Liste de filtres*/
  .valFilterList .vflFlyout {
    background-color: #343C45;
  }
  
  input, select {
    color: whitesmoke;
    background-color: #343C45;
  }
  
  /*filtre dlider*/
  .slider .ruler {
    background: #07369a;
    border: none;
    border-radius: initial;
  }
  .slider .left, .slider .right {
    background: #0a3ca0;
  }
  .slider .marquee {
    background: #67696B;
}
  
  /*Vignettes*/
  .productListing.topthumbs .productWrapper,
  .productListing.topthumbs .groupWrapper {
    background-color: #252c33;
  }

  .productListing.topthumbs .caract {
    color: wheat;
  }
  
  /*menu*/
  .topCat .contentTab .catList {
    background: #14171a;
  }
  
  .topCat .contentTab .col ul a {
    color: whitesmoke;
  }
  
  /*descriptif categorie*/
  #cathead div.desc {
    background: #14171a;
    color: whitesmoke;
  }
  
  /*sous menu*/  
  #cathead .bloc,
  #cathead div.rounded_b {
    background: #14171a;
  }
  
  #searchhead .searchFilters {
    background-color: #171B1F;
    color: whitesmoke;
  }
  
  /*Pub interne LDLC*/
  #cathead .bloc .catactu_int {
    display: none;
  }
  
  
  /*Rubrique a la noix (sugestion, top des ventes ...)*/
  
  .intermediatecattop,
  .LBOCint,
  .brandsCint,
  #cathead .catactu{
    display: none !important;
  }
  
  #cathead .bloc {
    border-top: none;
  }
  
  #productListingWrapper {
    background: #14171a;
    color: whitesmoke;
  }
  
  /*tableau article impaire*/
  .productListing.listing tr.e1 {
    background: #252c33;
  }
  
  .productListing.listing tr.e0 {
    background: #171b1f;
  }
  
  
  /*Fiche detaillé*/
  #productheader .wrapper, #productfiche .wrapper, #fichetech .wrapper, .productpress .wrapper, .productcomments .wrapper, .producttops .wrapper, .similarproducts .wrapper {
    background: #252c33;
    color: whitesmoke;
  }
  
  #productheader h1 .designation_longue {
    color: wheat
  }
  
  #productshipping {
    background: #14171a;
    color: whitesmoke;
  }
  
  #productshipping .price {
    color: #59ABE3;
  }
  
  #productshipping .deee {
    color: wheat;
  }
  
  #productshipping .productdispo {
    background: #14171a;
    border: none;
  }  
  
  #productinfos .share li {
    background: none;
    /*border: 1px solid #c8c8c8;*/
  }
    
  #productfiche .marque {
    background: #39424d;
  }
  
  #fichetech .texte_critere {
    background: #00288C;
  }
  
  #productParametersList .productParameterValues {
    color: wheat;
  }
  
  #productParametersList tr.odd:hover,
  #productParametersList tr.even:hover {
    background: #14171a;
  }
   
  .productcomments ul li.even {
    background-color: #14171a;
  }
  
  .divChildrenCategories a {
    color: whitesmoke !important;
    background: #14171A !important;
  }
  
  .productcomments ul .date {
    color: wheat;
  }
  
  .shortcutProduct .shortcutList li.scImg a,
  .shortcutProduct .shortcutList li.scDesc a,
  .shortcutProduct .shortcutList li.scFT a,
  .shortcutProduct .shortcutList li.scAvis a,
  .footerPage {
    background: #14171a;
  }
        
}
