css:
@-moz-document domain("soap4.me") {
#social {display: none}
#info .description p {line-height: 1.4em}

body,
#footer,
.loading {
	background-color: #000;
	color: #ccc;
}

a { color: #fff !important; }
a:hover { color: #FF7A00 !important; }
.underline { border-color: #bbb !important; }
.underline:hover { border-color: #999 !important; }



#info,
h2,
span.name,
#episodes { text-shadow: none !important; }

#info,
#episodes { color: #ccc }




/* Шапка */
#header {
	background: #333 !important;
	min-height: 28px;
}
#header a {
	text-shadow: none;
	border-bottom: 1px solid #666 !important;
	color: #ccc !important;
	float: left;
	padding: 0;
	text-transform: capitalize;
	margin: 6px 12px;
}
#header a:hover {
	color: #FF7A00 !important;
	border-color: rgba(255, 122, 0, 0.39) !important;
}
#header a.active {
	box-shadow: none;
	text-shadow: none;
	border-radius: 6px 6px 0 0;
	border-bottom: 0 !important;
	background-color: #000;
	color: #00D8FD !important;
	font-weight: bold;
	margin: 0;
	padding: 6px 12px 8px 12px;
}
#header a:nth-of-type(1) {
	margin-left: 0 !important;
}
#header a.active:nth-of-type(1) {
	margin-left: -12px !important;
}
#header .exit {
	float: right;
	margin: 6px 0;
}

/* Скрываем Идеи, новости и помощь */
#header a:nth-of-type(4),
#header a:nth-of-type(6),
#header a:nth-of-type(7) {
    display: none;
}


/* Шапка: информация о пользователе */
#header .user {
/*
	background: #333;
	color: #ccc;
	font-size: 15px;
	opacity: 1;
	margin: 0;
	padding: 8px 12px 8px 12px;
	border-radius: 0 0 6px 6px;
*/
	background: none;
	position: relative;
	top: -35px;
	left: -50px;
	border: 0;
	opacity: 1;
	color: #ccc;
	box-shadow: none;
}
#header .user .pro {
	border-radius: 4px;
	background-color: #00D8FD;
	color: #000;
	font-weight: bold;
	opacity: 1;
	font-size: 13px;
}
#header .user a {
	color: #fff !important;
	padding: 0;
	float: none;
	position: relative;
	left: 10px;
	margin-right: 10px;
}
#header .user .data {
	opacity: .5;
	margin-left: 6px;
	display: none;
}
#header .user .data span {
	opacity: .6;
}
#header .user .data:hover {
	opacity: 1;
}



/* Шапка: сериалы, сортировка */
#soap-sort li:nth-of-type(1),
#soap-sort li:nth-of-type(1):hover {
	border: 0 !important;
	color: #666 !important;
}
#soap-sort li:nth-of-type(1):first-letter {
	text-transform: uppercase;
}
#soap-sort li {
	border-bottom: 1px solid #666 !important;
	color: #ccc !important;
}
#soap-sort li:hover {
	color: #FF7A00 !important;
	border-color: rgba(255, 122, 0, 0.39) !important;
}
#soap-sort li.active {
	border: 0 !important;
	color: #00D8FD !important;
	font-weight: normal;
}

/* Количество непросмотренных серий */
#soap-sort li.watching div {
	border: 0;
    padding: 3px 5px;
	background: none;
	opacity: 1;
	border-radius: 4px 4px 0 0;
	margin-left: 4px;
	font-size: 13px;
	margin-top: -3px;
	display: none;
}
#soap-sort li.active div {
	border-radius: 4px;
	background-color: #00D8FD;
	color: #000;
	font-weight: bold;
	display: inherit;
}



/* Шапка: фильтры */
#new .switcher:first-letter,
#poster .switcher:first-letter,
#shedule .switcher:first-letter {
	text-transform: uppercase;
}
#new .switcher a,
#poster .switcher a,
#shedule .switcher a {
	border-bottom: 1px solid #666 !important;
	color: #ccc !important;
}
#new .switcher a:hover,
#poster .switcher a:hover,
#shedule .switcher a:hover {
	color: #FF7A00 !important;
	border-color: rgba(255, 122, 0, 0.39) !important;
}
#new .switcher a.active,
#poster .switcher a.active,
#shedule .switcher a.acrive {
	border: 0 !important;
	color: #00D8FD !important;
	font-weight: normal;
}





/* Крупный поиск */
#search {
	position: absolute;
	margin-left: 490px;
    margin-top: 5px;
}



/* Информация о сериале */
#info .tabs .active,
#info .tab {
    background-color: #000;
    color: #666;
}
#info .tabs .active {
    color: #00D8FD;
}
#info .tab a { color: #fff }
#info .tab a:hover { color: #ccc !important }

#info .reviews li {
    border: 0;
}
.pointer {color: #ccc; border-bottom: 1px solid #666 !important;}
.pointer:hover { color: #FF7A00 !important;
    border-color: rgba(255, 122, 0, 0.39) !important; }







span.name,
.season { color: #999 !important }

#soap li a { opacity: .5; }
#soap li:hover a { opacity: 1; }
/*
#soap .new { z-index: 10; visibility: hidden; }
#soap li:hover .new { visibility: visible; }
*/
h2, h4 { color: #666; border-color: #666; border: 0 !important; }
h4 { color: #fff !important; border-bottom: 1px solid #333 !important; }
h2:first-letter {
    text-transform: uppercase;
}
h2 a { color: #666; }
h2 a:hover { color: #999; }




/* Список эпизодов */
#episodes .list li,
#new li {
    border: 0;
    padding: 0;
    margin-bottom: 0px;
}
#new li:nth-child(2n+3) {
	background: inherit;
}
#episodes .list li:hover,
#new li:hover {
    color: #eee;
    background-color: #333;
}

/* Список эпизодов: заголовки колонок */
#episodes li.header {
	display: none;
}
#episodes li.header,
#episodes li.header:hover {
    font-size: 13px !important;
    color: #666;
}
#episodes li.header .watched {
	margin: 0;
}
#episodes li.header .number {
	padding-top: 0;
	margin-right: 10px;
}

/* Список эпизодов: строка с эпизодом */
#episodes .jq_info_link,
#new .jq_info_link {
	margin: 0 0 0 10px;
	padding: 4px 0;
	height: 30px;
}
#episodes .title .en,
#new .title .en {
	opacity: .5;
	font-size: 12px;
}
#episodes li .number,
#new li .number {
	padding-left: 5px;
	width: 25px;
}
#episodes li .translate,
#new li .translate {
	width: 130px;
}

#new li a:nth-of-type(1) {
	margin-top: 4px;
	border-bottom: 1px solid #666 !important;
	color: #ccc !important;
}
#new li a:nth-of-type(1):hover {
	color: #FF7A00 !important;
	border-color: rgba(255, 122, 0, 0.39) !important;
}
#new .new li:first-child {
	display: none;
}
#new .new li:first-child .soap {
	margin: 0;
}
#new li .soap {
	margin-top: 4px;
}
#new .title {
	width: 315px;
}

/* Список эпизодов: информация об эпизоде */
#episodes .info,
#new li .info {
	background: none;
	border: 0;
	padding-left: 78px;
	line-height: 18px;
}
#new li .info {
	padding-left: 179px;
}
#episodes .info div,
#new li .info div {
	font-size: 13px;
}
#episodes li .rating,
#new li .rating {
	display: none
}
#episodes li .info div:nth-of-type(1),
#new li .info div:nth-of-type(1) {
	opacity: .5
}
#episodes .info .text,
#new .info .text {
	opacity: 1;
	font-size: 13px;
	margin-bottom: 20px;
}

/* Статус серии: просмотрена или нет */
#episodes .watched,
#new .watched {
	margin-top: -4px;
	height: 38px;
	width: 28px;
}
#episodes .watched div,
#new .watched div {
	background-position: center;
	background: none;
	height: 38px;
	width: 28px;
	opacity: 1 !important;
	border: 0 !important;
}
#episodes .watched div:after,
#new .watched div:after {
	content: "•";
	font-size: 36px;
	margin-left: 7px;
}
#episodes .watched .no:after,
#new .watched .no:after {
	color: #00D8FD;
}
#episodes .watched .yes:after,
#new .watched .yes:after {
	color: #666;
	opacity: .5;
}
#new .watched .all {
	display: none;
}

/* Ссылка на скачивание .torrent */
a.no-ajaxy { display: none; }

/* Кнопка воспроизведения */
#episodes .play,
#new .play {
	width: 97px;
	height: 36px;
	margin: 0;
	opacity: 1;
	line-height: 30px;
	font-size: 25px;
	border: 0 !important;
}
#new .play {
	width: 77px;
}
#episodes .play:hover,
#new .play:hover {
	background: none;
	color: #FF7A00;
}




/* Фильтр качества и озвучки */
#episodes .switcher .submenu li {
	background-color: #666 !important;
	color: #fff;
	box-shadow: none;
}
#episodes .switcher .submenu li:hover {
	background-color: #FF7A00 !important;
}
#episodes .switcher .submenu em {
	color: #00D8FD;
}




/* Расписание */
#shedule .underline:hover {
	color: #FF7A00 !important;
	border-color: rgba(255, 122, 0, 0.39) !important;
}




/* Подвал */
#footer { height: 160px; border-color: #333; margin-top: 10px; }
#footer .inner {
    font-size: inherit;
    color: #666;
}
#footer .inner ul li:first-letter {
    text-transform: uppercase;
}
#footer li:first-child {
    margin-bottom: 10px;
}
#footer li { line-height: 20px; }
#footer a {
    border-bottom: 1px solid #333;
}
#footer a:hover {
    color: #ccc !important;
    border-bottom: 1px solid #666;
}
#footer .inner .right {
	display: none;
}
}
