css:
@-moz-document domain("forums.ulyssesmod.net") {
/*Scrollbar Restyle*/
::-webkit-scrollbar {
  width: 14px;
  height: 14px;
}
::-webkit-scrollbar-button {
  width: 0px;
  height: 0px;
}
::-webkit-scrollbar-thumb {
  background: #e6a210;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-thumb:hover {
  background: #d2930f;
}
::-webkit-scrollbar-thumb:active {
  background: #d2930f;
}
::-webkit-scrollbar-track {
  background: #323232;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-track:hover {
  background: #464646;
}
::-webkit-scrollbar-track:active {
  background: #464646;
}
::-webkit-scrollbar-corner {
  background: transparent;
}
/*Continue*/
a.new_win:link {
    color: #A07207 !important
}
a.new_win:visited {
    color: #2993AD !important
}
span.smalltext {
    color: #A07207 !important
}
#smflogo {
    display: none !important
}
/*Heading*/

a:visited {
    color: #E6A210 !important
}
a:link {
    color: #2993AD !important
}
a:hover {
    color: #BA891F !important
}
#top_section {
    background: rgb(50, 50, 50) !important;
}
#header {
    background: rgb(50, 50, 50) !important;
}
#header div.frame {
    background: rgb(50, 50, 50) !important;
}
span.firstlevel {
    background: rgb(50, 50, 50) !important
}
.dropmenu li a.active {
    background: rgb(50, 50, 50) !important;
}
a.firstlevel {
    margin-right: 0px !important
}
.navigate_section ul {
    border-top: 3px solid rgb(55, 55, 55) !important;
    background: rgb(50, 50, 50) !important;
}
/*Text Boxes*/

input,
button,
select,
textarea {
    color: #DBA125 !important;
    background: rgb(135, 135, 135) !important;
    border: 1px solid #000000 !important;
}
/*Sections*/

div.cat_bar h3.catbg {
    background: rgb(50, 50, 50) !important
}
div.cat_bar {
    background: rgb(50, 50, 50) !important;
}
/*Sub-Sections*/

tbody.content td.children {
    color: #E6A210 !important;
    background: rgb(55, 55, 55) !important;
}
/*Main Body*/

.navigate_section ul {
    background: rgb(50, 50, 50) !important;
}
#content_section {
    background: rgb(50, 50, 50) !important;
}
table {
    background: rgb(50, 50, 50) !important
}
#content_section div.frame {
    background: rgb(50, 50, 50) !important;
}
table.table_list tbody.content td.icon {
    background: rgb(55, 55, 55) !important
}
tr.windowbg2 td,
tr.approvebg td,
tr.highlight2 td {
    background: rgb(55, 55, 55) !important;
}
/*Text*/

table.table_list p {
    color: rgb(135, 135, 135) !important;
}
/*Background Color*/

body {
    background: rgb(55, 55, 55) !important;
    color: #2993AD !important;
}
/*Info Center*/

.buttonlist ul li a span {
    background: rgb(50, 50, 50) !important;
}
.buttonlist ul li a {
    background: rgb(50, 50, 50) !important;
}
span.upperframe span {
    background: rgb(55, 55, 55) !important;
}
.roundframe {
    background: rgb(55, 55, 55) !important;
    border-left: 0px solid rgb(55, 55, 55) !important;
    border-right: 0px solid rgb(55, 55, 55) !important;
}
span.upperframe {
    background: rgb(55, 55, 55) !important;
}
span.lowerframe span {
    background: rgb(50, 50, 50) !important;
}
span.lowerframe {
    background: rgb(50, 50, 50) !important;
}
#upshrinkHeaderIC p.last {
    border-top: 2px solid rgb(135, 135, 135) !important;
}
h3.catbg a:link,
h3.catbg a:visited,
h4.catbg a:link,
h4.catbg a:visited,
h3.catbg,
.table_list tbody.header td,
.table_list tbody.header td a {
    color: rgb(135, 135, 135) !important;
}
/*Info Center Sections*/

h4.titlebg {
    background: rgb(50, 50, 50) !important;
}
div.title_barIC {
    background: rgb(50, 50, 50) !important;
}
h4.titlebg,
h4.titlebg a {
    color: #E6A210 !important;
}
/*Footer*/

div.frame {
    background: rgb(50, 50, 50) !important;
}
#footer_section {
    background: rgb(50, 50, 50) !important;
}
/*Inside Topic Sections*/

tr.windowbg2 td,
tr.approvebg td,
tr.highlight2 td {
    background: rgb(55, 55, 55) !important;
    color: rgb(135, 135, 135) !important;
}
tr.catbg th.last_th {
    background: rgb(55, 55, 55) !important;
}
.catbg,
.catbg2,
tr.catbg td,
tr.catbg2 td,
tr.catbg th,
tr.catbg2 th {
    color: #E6A210 !important;
    background: rgb(55, 55, 55) !important;
}
.table_frame .table_list td.icon,
.table_frame .table_list td.info,
.table_frame .table_list td.stats {
    border-right: 2px solid rgb(50, 50, 50) !important;
}
.windowbg2 {
    color: #E6A210 !important;
    background-color: rgb(55, 55, 55) !important;
}
.topic_table td.subject p,
.topic_table td.stats {
    background: rgb(55, 55, 55) !important;
    color: #E6A210 !important;
}
.topic_table .icon1,
.topic_table .icon2,
.topic_table .stats {
    background: rgb(55, 55, 55) !important;
}
table.table_grid td {
    border-bottom: 1px solid rgb(50, 50, 50) !important;
    border-right: 1px solid rgb(50, 50, 50) !important;
}
tr.catbg th {
    border-bottom: 1px solid rgb(50, 50, 50) !important;
}
.smalltext,
tr.smalltext th {
    background: rgb(55, 55, 55) !important;
}
#topic_icons .description {
    background: rgb(55, 55, 55) !important;
    border-bottom: 1px solid rgb(50, 50, 50) !important;
    border-top: 1px solid rgb(50, 50, 50) !important;
    border-left: 1px solid rgb(50, 50, 50) !important;
    border-right: 1px solid rgb(50, 50, 50) !important;
}
li {
    background: rgb(135, 135, 135) !important;
}
.inner {
    background: rgb(50, 50, 50) !important;
    color: rgb(135, 135, 135) !important;
}
.smalltext,
tr.smalltext th {
    background: rgb(55, 55, 55) !important;
    color: rgb(135, 135, 135) !important;
}
.postarea div.flow_hidden {
    background: rgb(50, 50, 50) !important;
}
ul.quickbuttons li a {
    background: rgb(55, 55, 55) !important;
}
.postarea,
.moderatorbar {
    background: rgb(50, 50, 50) !important;
}
.windowbg span.topslice span {
    background: rgb(55, 55, 55)!important;
}
.windowbg span.topslice {
    background: rgb(55, 55, 55) !important;
}
.poster {
    background: rgb(50, 50, 50) !important;
}
.post_wrapper {
    background: rgb(50, 50, 50) !important;
    color: #000000 !important;
}
li {
    background: rgb(50, 50, 50) !important;
}
code.bbc_code {
    background: rgb(55, 55, 55) !important;
    color: #A07207 !important;
}
.windowbg2 span.topslice span {
    background: rgb(50, 50, 50) !important;
}
.windowbg2 span.topslice {
    background: rgb(50, 50, 50) !important;
}
.windowbg span.botslice span {
    background: rgb(50, 50, 50) !important;
}
span.botslice {
    background: rgb(50, 50, 50) !important;
}
blockquote.bbc_standard_quote {
    background: rgb(155, 155, 155) !important;
}
.windowbg2 span.botslice span {
    background: rgb(50, 50, 50) !important;
}
#display_jump_to {
    background: rgb(50, 50, 50) !important;
    border-bottom: 1px solid rgb(50, 50, 50) !important;
    border-right: 1px solid rgb(50, 50, 50) !important;
    border-left: 1px solid rgb(50, 50, 50) !important;
    border-top: 1px solid rgb(50, 50, 50) !important;
}
#poll_options p {
    background: rgb(55, 55, 55) !important;
    color: rgb(135, 135, 135) !important;
}
#poll .content {
    background: rgb(55, 55, 55) !important;
    color: rgb(135, 135, 135) !important;
}
/*locked Threads*/

.lockedbg2 {
    background: #E6A210 !important;
    color: rgb(55, 55, 55) !important;
}
.topic_table td.stickybg2 {
    background: #525252 !important;
    background-repeat: no-repeat;
    color: #E6A210 !important;
}
/*Profile*/

#basicinfo h4 {
    background: rgb(55, 55, 55) !important;
    color: #2993AD !important;
}
#basicinfo .windowbg .content {
    background: rgb(55, 55, 55) !important;
    color: #2993AD !important;
}
.windowbg span.topslice span {
    background: rgb(55, 55, 55) !important;
    color: #2993AD !important;
}
table.table_grid td {
    border-bottom: 1px solid rgb(50, 50, 50) !important;
    border-right: 1px solid rgb(50, 50, 50) !important;
    background: rgb(55, 55, 55) !important;
    color: #2993AD !important;
}
/*Messages*/

.windowbg,
#preview_body {
    background-color: rgb(50, 50, 50) !important;
}
.richedit_resize {
    background: rgb(135, 135, 135) !important;
    border: 1px solid rgb(135, 135, 135) !important;
}
/*Editor*/

.rich_editor_frame {
    background: rgb(135, 135, 135) !important;
}
/*Search*/

div.title_bar {
    background: rgb(55, 55, 55) !important;
}

.geshi {
    color: rgb(135,135,135) !important;
    background: rgb(65,65,65) !important;
    border: 1px rgb(65,65,65) !important;
}
}
