css:
@-moz-document url(http://csgocircle.com) {
body {
    color: #fff;
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 14px;
    background: url(http://i.imgur.com/H6QZeVM.jpg) no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    overflow: auto;
}

#circle {
    margin-top: 0;
    background: rgba(0, 0, 0, 0) url("http://i.imgur.com/2qKx1OE.png") no-repeat scroll center top / 94% 100%;
}

.container .masthead nav {
    background: url(http://i.imgur.com/AHUwqZc.png) repeat top;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;
}

.list-box {
    background: url(http://i.imgur.com/d4oPWMa.png) repeat;
    /* max-width: 273px; */
    width: 100%;
    /* min-height: 600px; */
    -webkit-border-radius: 10px;
    -moz-border-radius: 10px;
    border-radius: 10px;
    margin-top: 50px;
    float: left;
    min-height: 164px;
    margin-bottom: 20px;
}

#affix-right #chat .chat-container {
    height: 345px;
    overflow: auto;
    background: rgba(0, 0, 0, 0.40);
    padding: 15px;
    -webkit-border-radius: 15px;
    -moz-border-radius: 15px;
    border-radius: 15px;
}
}
