css:
@-moz-document domain("www.sinister.ly") {
body {
    background: #101010 url(http://images.alphacoders.com/211/211691.jpg) no-repeat; background-attachment: fixed;}
    
#bridge {
    background: rgba(0, 0, 0, 0);
    height: 40px;
    padding: 5px 0px;
    border-bottom: 0px solid #303030;
    box-shadow: 0px 0px 0px rgba(0,0,0,0.7);
    min-width: 1000px;
}
.searchbox_wrap {visibility:hidden;}
.red_alert {visibility:hidden;}
#theme_styler {visibility:hidden;}
#container {
    border: 0px solid #303030;
    border-bottom: 0;
    border-radius: 0px;
	box-shadow: 0 0px 0px rgba(0,0,0,0.7);
    background: rgba(0, 0, 0, 0);}
.menu ul a {
    color:#0AB9D4;
}
.menu ul a:active, .menu ul a:hover {
    background: transparent;
    border-color: #0AB9D4;
}
.tborder {
    /* Opaque bg to content */
	background: rgba(71, 65, 109, 0.63);
    width: 100%;
    /* SUBTITLE */
    color: #FFFFFF;
	margin: auto;
}
.trow1, .trow2 {
    background: transparent;
    border-bottom: 1px solid rgba(26, 26, 26, 0);
    border-top: 1px solid rgba(42, 42, 42, 0);
}
a.user_anchor {
    display: block;
    padding: 5px 8px;
    border: 1px solid #26F007;
    border-radius: 2px;
    color: #26F007;
    font-weight: 900;
    text-align: left;
}
#panel a.circlelink {
    border: 1px solid #26F007;
}
a:link, a:visited {
    /* TITLE */
    color: #00D8FF;
}
#panel a.circlelink:hover {
    border-color: #007511;
    color: #007511!important;
}
#panel a.link {
    color: #26F007;
}
.tcat {
    background: rgb(16, 129, 57); /*#108139*/
    color: #FFFFFF;
}
.thead {
    background: #088FA7;
    color: #206B79;
    }
    .thead a{
        color:#FFFFFF;
    }
    .thead a:hover{
        color:#098197
    }
.bigbutton {
    background: #108139;
    box-shadow: inset 0 1px 0 rgba(255,255,255,.07);
    padding: 8px 12px;
    border-radius: 3px;
    margin-top: 4px;
    position: relative;
    top: 4px;
    color: #fff!important;
    text-transform: uppercase;
    font-size: 11px;
    text-shadow: 0 0px 0 rgba(0,0,0,.71);
    border: none;
}
.postbit_button {
    background: #1784A0 url(https://www.sinister.ly/images/e-red/highlight_faint.png) repeat-x top;
    }
a.postbit_button{
    color:#ffffff;        
    }
    a.postbit_button:hover{
        background-color:#108139;
    }
.navigation {
    margin-bottom: 20px;
    padding: 5px 10px;
    border-radius: 3px;
    background: #108139;
    color: #FFFFFF;
    border: 0px solid #FFFFFF;
    font-size: 12px;
}
.post_userdetails {
    background: rgba(108, 117, 158, 0.47);
    color: #fff;
    padding: 5px 8px;
    margin: -1px;
    border: 0px solid #242424;
    text-align: left;
    font-size: 11px;
}
.codeblock, blockquote {
    background: #002B36;
    border: 0px solid #2b2b2b;
    margin: 5px 0;
    padding: 5px;
    color: #859900F;
}
.codeblock .title, blockquote cite {
    background: rgba(41, 38, 62, 0.62);
    color: #14746C;
    border-bottom: 1px solid #2c2c2c;
    display: block;
    margin: -5px -5px 5px;
    padding: 8px 5px 5px;
    font-style: normal;
    font-weight: 700;
}
.tfoot {
    background: rgba(70, 63, 108, 0.28);
    color: #fff;
    padding: 8px;
    border-top: 0px solid #2e2e2e;
    text-align: right!important;
}
textarea {
    background: rgba(78, 58, 107, 0.61);
    border: 0px solid #FFFFFF;
    padding: 6px;
    min-width: 500px;
    font-family: Arial,Helvetica,Tahoma,Verdana;
    font-size: 12px;
    box-shadow: inset 0 0 10px transparent;
    color: #FFFFFF;
}
textarea:focus {
    background: #383737;
    }
input.button {
    background: #1784A0 url(https://www.sinister.ly/images/e-red/highlight_faint.png) repeat-x top;
    font-size: 11px;
    padding: 5px 8px;
    border-radius: 2px;
    color: #fff;
    border-width: 0px;
    cursor: pointer;
}
#modoptions, #threadoptions {
    background: rgba(77, 63, 108, 0.58);
    padding: 5px;
    border: 0px solid #000;
}
#footer {
    background: rgba(72, 60, 108, 0.74);}
.shadetabs li a.selected {
    position: relative;
    top: 0;
    opacity: 1;
    background: rgba(46, 253, 223, 0.87);
    box-shadow: none;
}
.shadetabs li a:hover {
    background: rgba(0, 223, 212, 0.35);
}
.shadetabs li a {
    background: rgba(12, 255, 225, 0.49);
    color: #FFF;
    padding: 5px 10px;
    position: relative;
    z-index: 1;
    margin-right: 5px;
    border-radius: 2px;
    transition: .3s;
    -o-transition: .3s;
    -ms-transition: .3s;
    -webkit-transition: .3s;
    -moz-transition: .3s;
    font-weight: 700;
    text-decoration: none;
}
.textbox {
    background: rgba(108, 92, 160, 0.58);
    color: #FFFFFF;
    border: 0px solid #363636;
    padding: 8px 10px;
    font-size: 11px;
    outline: 0;
    box-shadow: inset 0 0 0px rgba(0,0,0,.19);
}
.pagination a {
    background: #1784A0;
    color: #FFF;
    border: 0px solid #1a1a1a;}
.pagination .pagination_current {
    background: #1784A0;
    color: #fff;
    border: 0px solid #000;}
.usertabs {background:none;border: 0px solid #2F0000;}
.usertabs li a.selected {
    background: #1784A0;
    color: #fff;
    padding: 5px 10px;
    border-radius: 2px;
    box-shadow: inset 0 0px 0px rgba(0,0,0,.6);
    text-decoration: none;
}
.usertabs li a:hover {
    background: rgba(46, 253, 223, 0.68);
    color: #fff;
    box-shadow: inset 0 0px 0px rgba(0,0,0,.6);
    border-radius: 2px;
    text-decoration: none;
}
.tabcontent {
    background: #202020 !important;;
    border: 0px solid #191919;
    border-bottom: 0px solid #191919;
    display: none;
    padding: 5px;
}
.detailbox {
    background: rgba(71, 64, 108, 0.38) url(http://) bottom left repeat-x;
    border: 0px solid #191919;
    border-bottom: 0px solid #191919;
    padding: 5px;
    margin: 4px;
    font-size: 12px;
}
.reputation_positive {
    color: #2EFDDF;
}
.user_block.active {
    background: rgba(72, 60, 108, 0.5);
    border-radius: 2px 2px 0px 0px!important;
}
.user_menu {
    position: absolute;
    z-index: 9995;
    top: 50px;
    display: none;
    background: rgba(44, 36, 68, 0.83);
    width: 180px;
    padding: 10px 0px;
    list-style: none;
    text-align: center;
    font-size: 12px;
    box-shadow: 0 0px 0px rgba(0,0,0,0.7);
    border: 0px solid #303030;
    border-bottom: 0;
    border-radius: 4px;
}
.user_menu a:hover {
    background: #2EFDDF
}
.user_block:hover a.user_anchor {
    border-color: #007511!important;
    color: #007511!important;
}
select {
    background: rgba(96, 82, 138, 0.76);
    color: #C9C9C9;
    border: 0px solid #3D3D3D;
    padding: 7px;
    font-size: 11px;
}
.reputation_negative {
    color: #FFA283;
}
#logo-img {
    background: url(https://www.sinister.ly/images/sinisterly_v2/logo_red.png) no-repeat;}
#logo-img:hover {
    background: url(http://i.imgur.com/Ssv314p.png) no-repeat;
}
input.button:hover {
    background-color: #76F9E6;
    color: #fff;
    text-decoration: none;
}
div.error {
    background: rgba(71, 62, 107, 0.88);
    color: #1784A0;
    padding: 5px 10px;
    border-top: 2px solid rgba(0, 223, 212, 0.72);
    border-bottom: 2px solid rgba(1, 223, 213, 0.78);
    font-size: 12px;
}
div.error p em {
    font-style: normal;
    font-weight: 700;
    padding-left: 24px;
    display: block;
    color: #00DFD4;
    background: url(https://www.sinister.ly/images/error.gif) no-repeat 0;
}
legend {
    background: rgba(72, 62, 107, 0.55);
    padding: 2px;
    border: 0px solid #121212;
}
fieldset {
    border: 0px solid #121212;
}
}
