css:
@-moz-document domain("bitbucket.org") {

/* limit block elements to a reasonable width */
.wiki-content p,
.wiki-content li,
.wiki-content blockquote {
    max-width: 72ex;
}

/* make paragraphs, and only paragraphs, double-justified */
.wiki-content p {
    text-align: justify;
}

/* preformatted blocks should be as wide as their content,
 * but at least as wide as the standard block width
 */
.wiki-content pre {
    min-width: 80ex;
    width: auto;
    width: intrinsic;
    width: -moz-max-content;
    width: -webkit-max-content;
    width: max-content;
}

/* tables should be as wide as their content,
 * but at least as wide as the standard block width
 */
.wiki-content table {
    min-width: 72ex;
    width: auto;
}

}
