css:
@-moz-document domain("glowing-bear.org") {
#topbar,
#topbar .actions {
    background: #cdcdcd;
    background: -moz-linear-gradient(top, #cdcdcd 0%, #b8b8b8 100%);
    background: -webkit-linear-gradient(top, #cdcdcd 0%, #b8b8b8 100%);
    background: linear-gradient(to bottom, #cdcdcd 0%, #b8b8b8 100%);
    border-bottom: 1px solid #939393;
}
#topbar .actions.vertical-line-left {
    border-left: 0px;
}
#topbar .glyphicon {
    color: #3C3C3C;
}
#sidebar {
    border-right: 1px solid #BABABA;
}
#sidebar,
.nav-pills li:nth-child(2n) {
    background-color: #DAE0E6;
}
#bufferFilter,
.bufferfilter {
    display: none !important;
}
body {
    font-family: "Droid Sans";
}
.modal-content,
.panel {
    background-color: #CECECE;
}
.modal-header {
    background-color: #F4F4F4;
    border-top: 0px;
}
.modal-body,
.modal-footer {
    border-top: 1px solid #939393;
}
}
