css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("weibo.com") {

      .FRAME_main .WB_frame { width: calc(/*[[width]]*/px + 400px) !important; }
      .FRAME_main #plc_main { width: calc(/*[[width]]*/px + 250px) !important; }
      .FRAME_main .WB_main_c { width: /*[[width]]*/px; }
      .FRAME_main .WB_tab_a .tab_box_a .fr_box { width: calc(/*[[width]]*/px - 300px); } 
      .FRAME_main .WB_tab_a .tab_box_a_r2_s .fr_box { width: calc(/*[[width]]*/px - 400px); } 
      .FRAME_main .WB_timeline { margin-left: calc(/*[[width]]*/px / 2 + 10px); }
      .FRAME_main a.W_gotop { margin-left: calc(/*[[width]]*/px / 2 + 200px); }
      @media screen and (max-width: 1006px) {
        .FRAME_main .WB_frame { width: calc(/*[[width]]*/px + 160px) !important; }
        .FRAME_main #plc_main { width: calc(/*[[width]]*/px + 10px) !important; }
        .FRAME_main a.W_gotop { margin-left: calc(/*[[width]]*/px / 2 + 80px); }
      }

      .FRAME_message .WB_frame { width: calc(/*[[width]]*/px + 400px) !important; }
      .FRAME_message #plc_main { width: calc(/*[[width]]*/px + 250px) !important; }
      .FRAME_message .WB_main_c { width: /*[[width]]*/px; }
      .FRAME_message .WB_tab_a .tab_box_a .fr_box { width: calc(/*[[width]]*/px - 300px); } 
      .FRAME_message .WB_tab_a .tab_box_a_r2_s .fr_box { width: calc(/*[[width]]*/px - 400px); } 
      .FRAME_message .WB_timeline { margin-left: calc(/*[[width]]*/px / 2 + 10px); }
      .FRAME_message a.W_gotop { margin-left: calc(/*[[width]]*/px / 2 + 200px); }
      .FRAME_message .private_dialogue_box .private_dialogue_body { width: /*[[width]]*/px; }
      .FRAME_message .webim_chat_window .private_dialogue_box .private_dialogue_body { width: 400px; }
      @media screen and (max-width: 1006px) {
        .FRAME_message .WB_frame { width: calc(/*[[width]]*/px + 160px) !important; }
        .FRAME_message #plc_main { width: calc(/*[[width]]*/px + 10px) !important; }
        .FRAME_message a.W_gotop { margin-left: calc(/*[[width]]*/px / 2 + 80px); }
      }

      .FRAME_page .WB_frame { width: calc(/*[[width]]*/px + 240px) !important; }
      .FRAME_page .WB_frame_a,
      .FRAME_page .WB_frame_a_fix { width: calc(/*[[width]]*/px + 320px); }
      .FRAME_page .WB_frame #plc_main { width: calc(/*[[width]]*/px + 250px) !important; }
      .FRAME_page .DSC_header { width: calc(/*[[width]]*/px + 260px); }
      .FRAME_page .WB_frame_c { width: calc(/*[[width]]*/px); }
      .FRAME_page .WB_tab_a .tab_box_a .fr_box { width: calc(/*[[width]]*/px - 300px); } 
      .FRAME_page .WB_tab_a .tab_box_a_r2_s .fr_box { width: calc(/*[[width]]*/px - 400px); } 
      .FRAME_page a.W_gotop { margin-left: calc(/*[[width]]*/px / 2 + 220px); }
      @media screen and (max-width: 939px) {
        .FRAME_page .WB_frame { width: /*[[width]]*/px !important; }
        .FRAME_page .WB_frame_a,
        .FRAME_page .WB_frame_a_fix { width: /*[[width]]*/px; }
        .FRAME_page .DSC_header { width: calc(/*[[width]]*/px + 20px); }
        .FRAME_page .WB_frame #plc_main { width: calc(/*[[width]]*/px + 10px) !important; }
      }

      .B_page .WB_frame { width: calc(/*[[width]]*/px + 320px) !important; }
      .B_page .WB_frame #plc_main { width: calc(/*[[width]]*/px + 340px) !important; }
      .B_page a.W_gotop { margin-left: calc(/*[[width]]*/px / 2 + 160px); }
      .B_page .WB_timeline { margin-left: calc(/*[[width]]*/px / 2 + 170px); }
      .B_page .send_weibo_simple .input_simple_wrap .inputfunc_simple_wrap { width: calc(/*[[width]]*/px - 96px); }
      @media screen and (max-width: 939px) {
        .B_page .WB_frame { width: calc(/*[[width]]*/px + 0px) !important; }
        .B_page .WB_frame #plc_main { width: calc(/*[[width]]*/px + 20px) !important; }
        .B_page a.W_gotop { margin-left: calc(/*[[width]]*/px / 2 + 120px); }
      }

      .send_weibo { background-size: cover; }
      .WB_ad_tm2015 { display: none !important; }


/*[[fontsize]]*/
  
}

style_setting_options.value:
.WB_info, .WB_text, .WB_info *, .WB_text * { font-size: 16px !important; line-height: 26px !important; }
.WB_feed_expand .WB_info *, .WB_feed_expand .WB_text *, .WB_feed_expand .WB_info, .WB_feed_expand .WB_text { font-size: 14px !important; line-height: 24px !important; }
.WB_text .W_btn_b { height: 20px !important; }
.WB_text .W_btn_b, .WB_text .W_btn_b * { line-height: 20px !important; font-size: 14px !important; }
.WB_feed_expand .WB_text .W_btn_b, .WB_text .W_btn_c, .WB_empty .W_btn_c { height: 18px !important; line-height: 18px !important; }
.WB_feed_expand .WB_text .W_btn_b, .WB_feed_expand .WB_text .W_btn_b *, .WB_text .W_btn_c *, .WB_empty .W_btn_c * { line-height: 18px !important; font-size: 12px !important; }
.W_icon_feedpin, .W_icon_feedhot { height: 16px !important; line-height: 16px !important; }

.WB_info, .WB_text, .WB_info *, .WB_text * { font-size: 21px !important; line-height: 32px !important; }
.WB_feed_expand .WB_info *, .WB_feed_expand .WB_text *, .WB_feed_expand .WB_info, .WB_feed_expand .WB_text { font-size: 18px !important; line-height: 29px !important; }
.WB_text .W_btn_b { height: 25px !important; }
.WB_text .W_btn_b, .WB_text .W_btn_b * { line-height: 25px !important; font-size: 18px !important; }
.WB_feed_expand .WB_text .W_btn_b, .WB_text .W_btn_c, .WB_empty .W_btn_c { height: 23px !important; line-height: 23px !important; }
.WB_feed_expand .WB_text .W_btn_b, .WB_feed_expand .WB_text .W_btn_b *, .WB_text .W_btn_c *, .WB_empty .W_btn_c * { line-height: 23px !important; font-size: 14px !important; }
.W_icon_feedpin, .W_icon_feedhot { height: 16px !important; line-height: 16px !important; }

.WB_info, .WB_text, .WB_info *, .WB_text * { font-size: 28px !important; line-height: 42px !important; }
.WB_feed_expand .WB_info *, .WB_feed_expand .WB_text *, .WB_feed_expand .WB_info, .WB_feed_expand .WB_text { font-size: 24px !important; line-height: 36px !important; }
.WB_text .W_btn_b { height: 33px !important; }
.WB_text .W_btn_b, .WB_text .W_btn_b * { line-height: 33px !important; font-size: 24px !important; }
.WB_feed_expand .WB_text .W_btn_b, .WB_text .W_btn_c, .WB_empty .W_btn_c { height: 29px !important; line-height: 29px !important; }
.WB_feed_expand .WB_text .W_btn_b, .WB_feed_expand .WB_text .W_btn_b *, .WB_text .W_btn_c *, .WB_empty .W_btn_c * { line-height: 29px !important; font-size: 19px !important; }
.W_icon_feedpin, .W_icon_feedhot { height: 16px !important; line-height: 16px !important; }
