/* Charcoal dark reading theme for http://forum.codelyoko.fr/
Version 1.1.1 by LeMinaw, released 28/02/2016
Work under CC-BY-NC-SA licence (see http://creativecommons.org/licenses/by-nc-sa/4.0/ for more informations.
Contact : leminaw@gmail.com */

@-moz-document url-prefix("http://forum.codelyoko.fr/viewtopic.php"), url-prefix("https://forum.codelyoko.fr/viewtopic.php") {
    
    /* Body */
    
    body {
        background-color: #070707 !important;
    }
    
    body > table {
        border: none !important;
    }
    
    /* Up navigation */
    
    .nav img {
        -webkit-filter: hue-rotate(350deg);
        filter: hue-rotate(350deg);
    }
    
    body > table > tbody > tr > td > table:nth-child(3) > tbody > tr:nth-child(2) > td:nth-child(2) > span { /* welcome message */
        color: #f3f3f3 !important; 
    }
    
    /* Forum thread */
    
    body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(8) { /* Ugly baby */
        max-width: 1200px;
    }
    
    .row1, .row2 {
        background-color: #0f0f0f !important; 
    }
    
    /* td.row1_viewforum, td.row2_viewforum {
        opacity: 0.8;
    } */
    
    /* Thread users */
    
    .postdetails {
        color: #d5d5d5;
    }
    
    /* Thread messages */
    
    td:nth-child(2).row1, td:nth-child(2).row2 {
        padding: 15px 50px !important;
    }
    
    span.postbody, span.postbody span, span.postbody ul{
        color: #d5d5d5 !important;
        font-family: "Palatino Linotype", "Book Antiqua", Palatino, serif !important;
        font-size: 14px !important;
        text-align: justify !important;
        display: inline-block; /* To deal with justification in imbricated blocks. CSS3's authors are FUCKING GENIUSES with inline-block ! */
    }
    
    /* BBCodes */
    
    .spoiler {
        background: #0c0c0c none repeat scroll 0 0 !important;
        border: 1px solid #a5a5a5 !important;
    }
    
    div.spoiler span {
        color: #a5a5a5 !important;
        font-size: 14px !important;
        font-weight: bold !important;
    }
    
    div.spoiler input {
        background: #0f0f0f!important;
        color: #d5d5d5!important;
        border: 1px solid #a5a5a5 !important;
    }
    
    .spoiler_visible {
        background: #0c0c0c none repeat scroll 0 0 !important;
        border: 1px solid #a5a5a5 !important;
    }
    
    .code {
        background-color: #0c0c0c !important;
        border: 1px solid #a5a5a5 !important;
        color: #00779e !important;
        font-size: 13px !important;
    }
    
    .quote {
        background-color: #0c0c0c !important;
        border: 1px solid #a5a5a5 !important;
        color: #d5d5d5 !important;
        font-size: 13px !important;
        font-family: "Palatino Linotype", "Book Antiqua", Palatino, serif !important;
    }
    
    a.postlink {
        color: #00779e !important; 
    }
    
    /* General purpose */
    
    .quote span {
        color: #d5d5d5 !important;
        font-size: 12px !important;
        font-family: "Palatino Linotype", "Book Antiqua", Palatino, serif !important;
    }
    
    .genmed, .genmed b {
        color: #a5a5a5 !important;
        font-size: 14px !important;
        font-family: "Palatino Linotype", "Book Antiqua", Palatino, serif !important;
        font-weight: bold !important;
    }
    
    .gensmall {
        color: #a5a5a5;
    }
}
