css:
@-moz-document domain("staemme.ch") {
/*
Background Bild
*/
/*body {
background-image: url('http://img1.goodfon.ru/original/2880x1800/3/54/art-anime-skazka-o-hvoste-fei.jpg');
background-repeat: no-repeat;
background-attachment: fixed;
}*/

/*
Layout
*/
body {
    font-size: 9pt;
    font-family: Comic Sans MS;
    background-color: rgb(0,0,0);
}
#main_layout .bg_left .bg_left {
    width: 25px;
    height: 200px;
    background: rgba(0, 0, 0, 0.0);
}
#main_layout .bg_left {
    width: 25px;
    overflow: hidden;
    vertical-align: top;
    background: rgba(255, 255, 255, 0);
}
#main_layout .bg_right .bg_right {
    width: 25px;
    height: 200px;
    background: rgba(255, 255, 255, 0);
}
#main_layout .bg_right {
    width: 25px;
    overflow: hidden;
    vertical-align: top;
    background: rgba(255, 255, 255, 0);
}
#main_layout .bg_bottomcenter {
    height: 25px;
    background: rgba(255, 255, 255, 0);
}
#main_layout .bg_bottomleft {
    width: 25px;
    background: rgba(255, 255, 255, 0);
}
#main_layout .bg_bottomright {
    width: 25px;
    background: rgba(255, 255, 255, 0);
}
.maincell {
    padding: 0 0 5px;
    vertical-align: top;
    background: rgba(255, 255, 255, 0);
}
.widget table {
    border-spacing: 0px !important;
}
#footer, #footer a {
    color: #00BAFF;
}
#footer {
    background-image: url('https://dsch.innogamescdn.com/8.44/28414/graphic/index/topbar-main.png');
    color: white;
}
#footer .world_button_active, #footer .world_button_inactive {
    background: rgb(145, 32, 17);
    border: 1px solid #000000;
}
.servers-list-top {
    background: rgba(0, 0, 0, 0.55);
    width: 100%;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    border-top: 1px solid #FFFFFF;
    border-left: 1px solid #FFFFFF;
    border-right: 1px solid #FFFFFF;
}
#servers-list-block {
    background: rgba(0, 0, 0, 0.55);
    color: #FFFFFF;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    border-left: 1px solid #FFFFFF;
    border-right: 1px solid #FFFFFF;
}
.servers-list-bottom {
    background: rgba(0, 0, 0, 0.55);
    width: 100%;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    border-bottom: 1px solid #FFFFFF;
    border-left: 1px solid #FFFFFF;
    border-right: 1px solid #FFFFFF;
}
.world_button_active, .world_button_inactive {
	color: #00BAFF!important;
    background: rgb(145, 32, 17)!important;
    border: 1px solid #000000!important;
}
.return Index.submit_login {
	color: #00BAFF!important;
    background: rgb(145, 32, 17)!important;
    border: 1px solid #000000!important;
}
.maincell > .info_box {
    background: rgba(0, 0, 0, 0.55) url('https://dsch.innogamescdn.com/8.42.2/28260/graphic/questionmark.png') no-repeat 4px center;
    color: #FFFFFF;
}
.chat-header {
    background: rgba(0, 0, 0, 0.5) url(http://www.transparenttextures.com/patterns/asfalt-light.png);
    border-bottom: 1px solid #000000;
}
.chat-window {
    border: 1px solid #FFFFFF;
}
.chat-body {
	color: #FFFFFF;
    background: rgba(0, 0, 0, 0.35) url(http://www.transparenttextures.com/patterns/asfalt-light.png);
}
.reportable.chat-row:hover {
    background: transparent
}
.chat-search {
	color: red! important;
}
.chat-header h4 {
    color: white;
}
.chat-footer {
    border-top: 1px solid #FFFFFF;
    background: rgba(255, 255, 255, 0.5);
}
.chat-input {
    background-color: transparent;
    color: black;
}
.chat-contacts .chat-body {
    background: rgba(0, 0, 0, 0.8);
}
.chat-contact:first-child {
    border-top: 1px solid rgb(255, 0, 0);
}
.chat-contact {
    border-bottom: 1px solid #000000;
}
.chat-message.chat-message-other, .chat-message {
    background: linear-gradient(to bottom, #000000 0%,rgba(0, 0, 0, 0.3) 100%);
    border: 1px solid rgba(0, 0, 0, 1);
    background-color: rgba(22, 122, 198, 0.5);
}
.chat-time {
    color: #F2DE04;
}
.chat-row .userimage {
    border: 1px solid #000000;
}
.chat-menu {
    border: 1px solid rgba(0, 0, 0, 0.8);
    background-color: rgba(55, 0, 255, 0.53);
}
.help-link {
    background: url('http://puu.sh/p8vaO/185c254ae5.png') no-repeat;
}

/*
Server Info
*/
.server_info {
    font-size: 7pt;
    margin-top: 0;
    margin-bottom: 0;
    text-align: right;
    color: #FFFFFF;
    margin: 1px 3px -0px 0;
    background: rgba(0, 0, 0, 0.7);
}
#serverTime {
    font-size: 24pt;
    color: #FF0000
}

/*
Schnellleiste
*/
#quickbar_inner .right {
    background: rgba(0, 0, 0, 0.7);
    width: 15px;
    position: static;
}
#quickbar_inner .topborder .main {
    background: rgba(0, 0, 0, 0.7);
    height: 4px;
}
#quickbar_inner .main {
    background: rgba(0, 0, 0, 0.7);
    text-align: center;
}
#quickbar_inner .topborder .right {
    background: rgba(0, 0, 0, 0.7);
    height: 4px;
}
#quickbar_inner .left {
    background: rgba(0, 0, 0, 0.7);
    width: 15px;
}
#quickbar_inner .topborder .left {
    background: rgba(0, 0, 0, 0.7);
    height: 4px;
}
#quickbar_inner .bottomborder .main {
    background: rgba(0, 0, 0, 0.7);
    height: 4px;
}
#quickbar_inner .bottomborder .left {
    background: rgba(0, 0, 0, 0.7);
    height: 4px;
}
#quickbar_inner .bottomborder .right {
    background: rgba(0, 0, 0, 0.7);
    height: 4px; 
}
#quickbar_inner a {
    font-weight: 700;
    text-decoration: none;
    color: #167ac6;
}
#quickbar_outer {
    margin: 0 auto;
    padding: 0;
}

/*
Dorf Anzeige
*/
.maincell table {
    color: #FFFFFF
}
.warn_90 {
    color: #FF0000;
}
.header-border {
    background: transparent url(http://www.transparenttextures.com/patterns/asfalt-light.png) scroll left top repeat-x;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
}
.header-border .firstcell {
    background: rgba(0, 0, 0, 0);
}
.header-border .box {
    background: rgba(0, 0, 0, 0.7);
}
.icon-box {
    background: transparent
}
.box {
    border: 0px solid;

}
.box-item.separate {
    background: transparent url(http://www.transparenttextures.com/patterns/asfalt-light.png) scroll left top no-repeat;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
}
#menu_row2_village a {
    font-weight: 700;
    text-decoration: none;
    color: #167ac6;
}
#content_value table, #units_help table {
    border-spacing: 0px;
    border-collapse: separate;
    empty-cells: show !important;
}
.vis td, .vis_item {
    background: rgba(254, 254, 254, 0.11);
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
}
a {
    font-weight: 700;
    text-decoration: none;
    color: #00BAFF;
}

/*
Dorf Ansicht
*/
.content-border {
    padding: 0;
    margin: 0;
    direction: ltr;
    background: rgba(0, 0, 0, 0.7);
    border-collapse: separate !important;
    border-spacing: 0!important;
    border: 0px solid #000000;
    -webkit-box-shadow: 1px 2px 3px 1px rgba(0, 0, 0, 0.2);
    box-shadow: 1px 2px 3px 1px rgba(0, 0, 0, 0.2);
}
#inner-border {
    padding: 0px 0;
    border: 1px solid #000;
}
#rightcolumn a {
    font-weight: 700;
    text-decoration: none;
    color: #00BAFF;
}
th, .vis > h4 {
    font-size: 9pt;
    text-align: left;
    font-weight: 700;
    color: #FFFFFF;
    background: linear-gradient(to bottom, #000000 0%,rgba(255, 255, 255, 0.3) 100%);
    background-color: rgba(193, 162, 100, 0.02) !important;
}
.grey {
    color: #FFF72B;
}
.btn, .btn-default {
    display: inline-block;
    padding: 3px;
    margin: 0 2px;
    text-align: center;
    font-family: Comic Sans MS;
    font-size: 12px !important;
    font-weight: bold;
    line-height: normal;
    cursor: pointer;
    background: -webkit-linear-gradient(top, #850808 0%,#E90D0D 22%,#D01010 30%,#EC0C0F 100%);
    /* background: linear-gradient(to bottom, #05133E 0%,#1A1887 22%,#1534A9 30%,#2072F3 100%); */
    -webkit-border-radius: 5px;
    border: 0px solid #000;
    color: #fff;
}
.btn:hover, .btn-default:hover {
    background: -webkit-linear-gradient(top, #05133E 0%,#1A1887 22%,#1534A9 30%,#2072F3 100%);
    /* background: linear-gradient(to bottom, #850808 0%,#E90D0D 22%,#D01010 30%,#EC0C0F 100%); */
    color: #fff;
    border: 0px solid #000;
}
div.vis {
    border: 1px solid #FFFFFF;
    margin: 15px 5px;
    -webkit-box-shadow: 1px 1px 2px 1px rgba(60, 30, 0, 0.2);
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
}
input, select {
    font-size: 8pt;
    border: 1px solid #000000;
    padding: 2px;
    word-wrap: break-word;
    font-family: Comic Sans MS;
    color: #000000;
    background-color: rgba(255, 255, 255, 0.5);
}
.popup_helper {
    color: #FFFFFF;
}
#group_id {
    font-size: 8pt;
    border: 1px solid #000000;
    padding: 2px;
    word-wrap: break-word;
    font-family: Comic Sans MS;
    color: #000000;
    background-color: rgba(255, 255, 255, 0.5);
}
.info_box {
    min-height: 26px;
    font-size: 12px;
    background: transparent url('https://dsch.innogamescdn.com/8.42.2/28260/graphic/questionmark.png') no-repeat 4px center;
    border: 1px solid #FFFFFF;
    padding: 3px 3px 3px 28px;
    margin: 5px 0;
    line-height: 24px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}
.hidden {
    color: #9F9696;
}
.inactive, .inactive:hover {
    color: #0084B5;
}

/*
Nachricht
*/
.confirm_abandonment > div {
	background-color: rgba(255, 0, 0, 0.5)! important;
    border: solid 1px black! important;
    padding: 2px! important;
    color: #FFFFFF! important;
}
.text > a {
	color: #00FFAB! important;
}
.post {
    border: 1px solid rgba(0, 0, 0, 1);
    background-color: rgba(22, 122, 198, 0.5);
}
.igmline {
    height: 26px;
    line-height: 26px;
    border-bottom: 1px solid rgba(128, 64, 0, 0.7);
    position: relative;
    overflow: hidden;
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;
    background: rgba(0,0,0,0.60);
    background: linear-gradient(to bottom, #000000 0%,rgba(0, 0, 0, 0.3) 100%);
}
.own .igmline {
    background: rgba(0,0,0,0.60);
    background: linear-gradient(to bottom, #000000 0%,rgba(0, 0, 0, 0.3) 100%);
}
.igmline {
    border-bottom: 1px solid rgb(0, 0, 0);
}
.post.own {
    float: right;
    border: 1px solid rgba(0, 0, 0, 0.99);
}
.selected a {
    color: #C11818;
}
.selected, .selected td {
    background-color: rgba(254, 254, 254, 0.22)! important;
}
#action_row tr {
    background-color: rgba(0, 0, 0, 0.45)! important;
}
#box {
    color: #FFF;
    background-color: rgba(255, 255, 255, 0.5);
}
#bb_color_picker {
    border: 1px solid #000000;
    background-color: rgba(0, 0, 0, 0.75) !important;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
}
#bb_color_picker_tones, #bb_color_picker_colors {
    border-bottom: 2px solid rgba(255, 255, 255, 0.5);
}
#bb_color_picker_preview {
    border: 1px solid #FFFFFF;
    background: rgba(255, 255, 255, 0.1);
}
#igm_to {
    border: 2px solid #03A9F4;
    background-color: rgba(0, 0, 0, 0.5);
    padding: 5px;
}
#igm_to > div {
    background-color: rgba(255, 255, 255, 0)!important;
}
.searchbar a {
    border: 1px solid rgb(255, 255, 255);
    background-color: transparent;
}
.searchbar input[type=search] {
    border: 1px solid rgb(255, 255, 255);
    font-family: Comic Sans MS;
    color: white;
    background-color: transparent;
}
.searchbar input[type=submit] {
    border: 1px solid rgb(255, 255, 255);
    background-color: transparent;
    background-image: url("https://dsch.innogamescdn.com/8.44/28414/graphic/search.png");
    background-image: url("https://dsch.innogamescdn.com/8.44/28414/graphic/search.png"), -webkit-linear-gradient(left top, transparent, transparent);
    background-image: url("https://dsch.innogamescdn.com/8.44/28414/graphic/search.png"), -o-linear-gradient(left top, transparent, transparent);
    background-image: url("https://dsch.innogamescdn.com/8.44/28414/graphic/search.png"), -moz-linear-gradient(left top, transparent, transparent);
    background-image: url("https://dsch.innogamescdn.com/8.44/28414/graphic/search.png"), -ms-linear-gradient(left top, transparent, transparent);
    background-image: url("https://dsch.innogamescdn.com/8.44/28414/graphic/search.png"), linear-gradient(to bottom right, transparent, transparent);
}

/*
Notieze
*/
.popup_menu {
    background-color: rgba(0, 0, 0, 0.7) !important;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    background-repeat: repeat-x;
    border-radius: 8px 8px 0 0;
    cursor: move;
    font-size: 17px;
    font-weight: bold;
    height: 26px;
    padding: 2px 5px;
    width: auto;
    color: #FFFFFF;
}
.popup_style {
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    border-color: #000000 #000000 #000000 #000000;
    background-color: rgba(0, 0, 0, 0.7);
    border-radius: 8px 8px 0 0;
    border-style: solid;
    border-width: 3px;
    z-index: 99999;
}
.popup_content {
    width: auto;
    color: #FFFFFF;
    padding-top: 2px;
    background-color: rgba(0, 0, 0, 0.7) !important;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    background-repeat: repeat-x;
    padding: 10px 5px;
}
#bb_main_div.ui-draggable-dragging {
	transition: none !important;
	background-color: rgba(255, 0, 0, 0.5) !important;
}
textarea {
    border: 1px solid #000000;
    padding: 2px;
    word-wrap: break-word;
    font-family: Comic Sans MS;
    color: #000000;
    background-color: rgba(255, 255, 255, 0.5);
}
h3 {
    font-size: 12pt;
    font-weight: 700;
    color: #FFFFFF;
}
.spoiler div {
    background: rgba(0,0,0,0.6) none repeat scroll 0 0;
}
.bbcodetable {
    background: rgba(0, 0, 0, 0.6);
    border: #FFFFFF 1px solid;
}

/*
Berichte
*/
#attack_spy_buildings_left {
    border: 1px solid #FFFFFF! important;
    background-color: rgba(0, 0, 0, 0)! important;
}
#attack_spy_buildings_right {
    border: 1px solid #FFFFFF! important;
    background-color: rgba(0, 0, 0, 0)! important;
}
.report_transparent_overlay {
    background: rgba( 0, 0, 0, 0.5)! important
}

/*
Charte
*/
#map_popup table {
    border: 1px solid #FF0000!important;
    background-color: rgba(0, 0, 0, 0)! important;
}
#info_content > tbody {
    background-color: rgba(0, 0, 0, 0.8);
}
.map_container, #warplanner {
    background-color: rgba(0, 0, 0, 0.69)!important;
    border: 1px solid #FFFFFF;
    direction: ltr;
}
.map_legend {
    float: none;
    font-size: 13PX;
}
.map-legend-container table {
    border: solid 1px #FFFFFF;
    background-color: rgba(255, 255, 255, 0.1) !important;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    margin-left: 0;
    border-collapse: separate;
    text-align: left;
} 
#tooltip {
    position: absolute;
    border: 1px solid #FFFFFF;
    background: linear-gradient(to bottom, rgb(248, 7, 7) 0%,#FF7000 100%);
}
.tooltip-style {
    position: absolute;
    max-width: 300px;
    z-index: 30000;
    border: 1px solid #FFFFFF;
    padding: 5px;
    background-color: rgba(254, 98, 0, 0.5) !important;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    -webkit-box-shadow: 1px 1px 2px 0px rgba(60, 30, 0, 0.35);
    box-shadow: 1px 1px 2px 0px rgba(60, 30, 0, 0.35);
    -webkit-border-radius: 4px;
    border-radius: 4px;
}
.tooltip-style h3, .tooltip-style div {
    margin: 0;
    color: #FFFFFF;
}
#info_content .center td {
    background-color: rgba(255, 255, 255, 0.1) !important;
}
#info_bonus_image {
    background-image: url(http://www.transparenttextures.com/patterns/asfalt-light.png);
}
hr {
    border-color: #FFFFFF;
}
#centercoords td {
    border-top: solid 1px #FFFFFF!important;
    padding-top: 0px;
}
.village-note {
    background-color: rgba(0,0,0,0.5);
    border: 1px solid #FFFFFF;
}
.village-note-head {
    border-bottom: 1px solid #FFFFFF;
}
#worldmap_image > input {
    background-color: rgba(255, 255, 255, 0.0);
}
#village_colors > table {
    background-color: rgba(255, 255, 255, 0.1)!important;
    border: solid 1px rgb(255, 255, 255)!important;
    background-image: url(http://www.transparenttextures.com/patterns/asfalt-light.png)!important;
}
#own_villages {
    background-color: rgba(0, 0, 0, 0.5)!important;
    border: solid 1px rgb(255, 255, 255)!important;
}
#own_villages > div:nth-child(2) {
    background-color: rgba(255, 255, 255, 0.1)!important;
    background-image: url(http://www.transparenttextures.com/patterns/asfalt-light.png)!important;
}
#color_picker_popup {
    background-image: url('http://www.spreeblick.com/wp-content/uploads/2007/11/angriffsflaeche.gif')!important;
    background-color: rgba(255, 255, 255, 0.15)!important;
}
.autoHideBox {
    background-image: url('http://www.transparenttextures.com/patterns/asfalt-light.png')!important;
    background-color: rgba(0, 0, 0, 0.7)!important;
    border: solid 2px #FFFFFF!important;
    color: #FFFFFF!important;
}

/*
Stamm
*/
.vis .row_a > td, .vis.alternating tr:nth-child(even) td, .vis .row_a > tr > td {
    background: rgba(37, 37, 37, 0.66);
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
}
.vis .row_b > td, .vis.alternating-rows tr:nth-child(odd) td, .vis .row_a > tr > td {
    background: rgba(125, 125, 125, 0.79);
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
}
.forum-content {
    background: rgba(255, 255, 255, 0.12);
    border: 1px solid #000000;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
}
.quote .quote_message {
    background-color: rgba(0, 0, 0, 0.56);
}
.post_thanks {
    border-top: 1px solid #000000;
}
.borderimage .confirmation-box {
    border: 19px solid rgba(255, 255, 255, 0.5);
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png")! Important;
    color: #FFFFFF;
}
.confirmation-box > div {
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png")! Important;
    background-color: rgba(0, 0, 0, 0.77);
    padding: 10px 5px;
}
.post_thanks_who > a {
	color: #000000
}
.shared_forum {
    background-color: rgba(0, 0, 0, 0.2);
    border: 1px solid #FFFFFF
}
.shared_selected {
    background-color: rgba(255, 255, 255, 0.2);
    border: 1px solid #FFFFFF;
}
.forum {
    background-color: rgba(0, 0, 0, 0);
    margin-bottom: 0px;
    border: 1px solid #FFFFFF;
}

/*
Flagge
*/
.flag_box {
    border: 1px solid #000000;
    background-color: rgba(255, 255, 255, 0.24);
}
.flag_box_empty {
    background-image: url("https://dsch.innogamescdn.com/8.43/28352/graphic/flags/flag_disabled.png") !important;
    background-color: rgba(0, 0, 0, 0.24)!important;
}
.flag_count {
    background-color: rgba(252, 0, 0, 0.65) !important;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    background-repeat: repeat-x;
    border-top: 1px solid #000000;
    border-left: 1px solid #000000;
}

/*
Rangliste
*/
.lit2 .lit-item {
    background: rgba(0, 0, 0, 0.5)!important;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png")!important;
}
.lit .lit-item {
    background: rgba(0, 0, 0, 0.5)!important;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png")!important;
}
.progress-bar {
    background-color: rgba(255, 255, 255, 0.5);
}
.progress-bar > div {
    background: linear-gradient(to bottom, #000000 25%,#FF0000 100%);
}

/*
Acc-Manager
*/
#plunder_list_filters {
    background-color: rgba(0, 0, 0, 0.25)! important;
}
.green {
