h2.disc-main-head, .basic-info h2#disc-main-head-serie, h2#disc-main-head-level {
    background: #333;
}

ul.watchlist li.watchname, ul.watchlist li.wantname {
    background: #333;
    color: #FFF;
}

ul.watchlist li.epnr {
    background: #A5A5A5;
}

ul.watchlist li.watchinfo {
    background: #C1C1C1;
}

ul.watchlist:nth-child(even) {
