@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("nagytibor.xyz") {
  #tarolo
  {
    background:url('http://i.imgur.com/pK1rz1w.jpg');
    background-size:100% 100%;
    color:white;
    border:none;
    border-radius:0px;
    border-collapse: collapse;
    top:0%;
  }
  ul
  {
    list-style-type:none;
  }
  a, a:link, a:visited, a:hover, a:active
  {
    color:white;
    text-decoration:underline;
  }
  #fejlec
  {
    background:black;
    border-radius:0px;
  }
  #robot, #lego
  {
    display:none;
  }
  input[name=felha],input[name=jel] 
  {
    text-align:center;
  }
  #loginablak
  {
    margin-top: -67px;
  }
  input[name=submit]
  {
    margin-top:10px;
  }
  input[name=FELTOLT]
  {
    border-radius:10px;
    padding:2px 5px;
  }
  input[name=fajl] button
  {
    border-radius:10px;
    padding:2px 5px;
  }
  #menu
  {
    width:459px;
    margin:0px auto 16px auto;
  }
  #menu ul
  {
    background:black;
  }
  #menu ul li:hover
  {
    background:#1275ae;
  }
  #menu ul li, #menu ul li a
  {
    border-right:none;
  }
  .alcim
  {
    text-decoration:underline;
  }
  .lezarthf li
  {
    text-decoration:line-through;
  } 
  #lab
  {
	  background-color:black;
    border-radius:0px;
  }
  #ballab
  {
    visibility:hidden;
    width:168px;
    padding-left:10px;
  }
  #ballab::after
  {
    content:'Design: Gabe\'n\'Tibi © 2016 AX';
    visibility:visible;
  }
  #baltibi
  {
    position:fixed;
    top:0;
    left:0;
    width:calc((100% - 600px) / 2);
    height:100%;
    background:url('https://media.giphy.com/media/l4KhVCGjXWUnB72fu/giphy.gif');
	background-size:100% 100%;

  }
  #kozeptibi
  {
    position:fixed;
    top:0;
    right:calc((100% - 600px) / 2);
    width:600px;
    height:100%;
    background:url('http://ih1.redbubble.net/image.77105221.9179/flat,800x800,075,f.u3.jpg');
	background-size:100% 100%;
  }
  #jobbtibi
  {
    position:fixed;
    top:0;
    right:0;
    width:calc((100% - 600px) / 2);
    height:100%;
    background:url('https://media.giphy.com/media/l4KhVCGjXWUnB72fu/giphy.gif');
	background-size:100% 100%;
  }
  /*WEBKIT BROWSERS*/
  @supports(-webkit-appearance:none)
  {
    #menu
    {
      width:463px;
    }
    #ballab
    {
      width:168px;
    }
  }
}
