css:
@-moz-document domain("nagytibor.xyz") {
#robot, #lego
{
	display:none;
}
body
{
	background-image:none;
    background-color:#221155;
}
a, a:link, a:visited
{
	color:#6633cc;
}
a:hover
{
	color:#000033;
}
#tarolo
{
    position:Relative;
	width:800px;
    border-radius:0px;
    left:0px;
    right:0px;
    margin:auto;
    margin-top:-5px;
    border-width:0px;
    padding:0px;
    background-color:white;
}
#fejlec
{
	background-image:none;
    background-color:#ddccff;
    border-radius:0px;
    height:28px;
}
#fejlec span:before
{
    font-family:Arial;
    text-shadow:none;
    text-align:left;
    color:#221155;
    font-weight:bold;
    font-size:13px;
    line-height:26px;
    height:26px;
    float:left;
    padding-left:5px;
    visibility:visible;
    content:'Házi feladat dashboard';
}
#fejlec span
{
	visibility:hidden;
    width:2px;
    height:2px;
    line-height:2px;
}
#menu ul li:last-child
{
	position:absolute;
    top:-35px;
    right:0px;
    margin:0px;
    padding:0px;
    border-width:0px;
    background-image:none;
    background-color:transparent;
    border-radius:0px;
    visibility:visible;
}
#menu ul li:last-child a
{
    color:#221155;
    text-transform:none;
}
#menu ul li:last-child:after
{
	display:none;
}
#menu ul li:last-child a:hover
{
	background-color:rgba(0,0,0,.1);
}
#menu ul li:first-child
{
    visibility:visible;
	position:absolute;
    top:-35px;
    left:0px;
    width:200px;
    margin:0px;
    padding:0px;
    border-width:0px;
    background-image:none;
    background-color:transparent;
    border-radius:0px;
}
#menu ul li a
{
	border-width:0px;
}
#menu ul li:first-child:after
{
	display:none;
}
#menu ul li:first-child a
{
    color:transparent;
}
#tarolo tr:nth-child(2) td, #tarolo tr:nth-child(2)
{
	visibility:hidden;
    line-height:1px;
    height:1px;
}
#tarolo tr:nth-child(2) td ul
{
    line-height:1px;
    height:1px;
}

#tartalom
{
	position: relative;
    top: -40px;
    padding-left:20px;
    padding-right:20px;
    min-height:350px;
    display:block;
}
.alcim:before
{
	display:block;
    text-align:left;
    font-family:Arial;
    font-size:18px;
    color:#666666;
    margin-top:5px;
    margin-bottom:10px;
}
.aktivhf, .lezarthf
{
	margin-top:0px;
    margin-left:0px;
    padding-left:0px;
    font-family:Arial;
    font-size:14px;
    line-height:32px;
}
.aktivhf li, .lezarthf li
{
    position:relative;
	display:block;
    min-height:32px;
}
.aktivhf li strong
{
	display:inline-block;
    width:100px;
    font-weight:normal;
    color:#999999;
}
.aktivhf .hiba
{
    display:inline-block;
	width:50px;
    font-weight:bold;
}
.aktivhf form
{
	display:inline-block;
    text-align:right;
    right: 20px;
    position: absolute;
    height:24px;
    top:4px;
}
.aktivhf input[type='file']
{
    display:inline-block;
	width: 250px;
}
.aktivhf input[name='FELTOLT']
{
margin-left:25px;
}
.aktivhf ul li
{
	display:none;
}
.lezarthf
{
margin-top:-20px;
}
.lezarthf ul
{
	display:none;
}
.aktivhf
{
	margin-bottom:40px;
}
#lab, #lablec
{
	width:100%;
    border-radius:0px;
    background-color:#ddccff;
}
#ballab:before
{
    font-family:Arial;
    color:rgba(0,0,0,.4);
    font-size:13px;
    content:'Sablon: ';
    font-weight:bold;
    line-height:22px;
}
#ballab
{
width:400px;
}
#ballab:after
{
    font-family:Arial;
    color:rgba(0,0,0,.4);
    font-size:13px;
    content:'nagytibor MMU by Edemmester Gamer Blog';
    font-weight:normal;
    line-height:22px;
}
#jobblab, #nevip
{
    line-height:22px;
	float:right;
    font-family:Arial;
    color:rgba(0,0,0,.4);
    font-size:13px;
}
#loginablak
{
    width: 800px;
    left: 40px;
    right: 0px;
    margin: auto;
    top: 55px;
    background-color:white;
    color:transparent;
    background-image:url('http://edemmester.hu/nagytibormmu/login.png');
    height:350px;
}
input[name="felha"]
{
	position:Relative;
    top:31px;
    left:-130px;
    font-family:Arial;
    border-radius:0px;
    border-color:#dddddd;
    border-style:solid;
    border-width:1px;
    padding:3px;
}
input[name="jel"]
{
	position:Relative;
    top:-39px;
    left:-130px;
    font-family:Arial;
    border-radius:0px;
    border-color:#dddddd;
    border-style:solid;
    border-width:1px;
    padding:3px;
}
#loginablak *
{
	border-radius:0px !important;
}
#loginablak input[type="submit"]
{
	position:Relative;
    top:-105px;
    left:-130px;
}
input[type="submit"]
{
	background-color:#221155 !important;
    color:white;
    font-family:Arial;
    font-size:13px;
    border-width:0px;
    padding-top:3px;
    padding-bottom:3px;
    padding-left:10px;
    padding-right:10px;
    text-align:center;
}

}

@-moz-document url("http://nagytibor.xyz/index.php?p=1") {
#tartalom .alcim:nth-child(1):before
{
	content:'Függőben lévő feladatok';
    visibility:visible;
    display:block;
}
#tartalom .alcim:nth-child(3):before
{
	content:'Lezárt feladatok';
    visibility:visible;
    display:block;
}
.alcim
{
	visibility:hidden;
    width:0px;
    height:0px;
}
}

@-moz-document url("http://nagytibor.xyz/") {
#tartalom:before
{
	font-weight:bold;
    content:url('http://edemmester.hu/nagytibormmu/info.png');
}

}
