css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("workflowy.com") {
/* userstyles from(Workflowy.com - Misc CSS Changes) added */
/* Set main font for bullet points and editor */
BODY, TEXTAREA, DIV.content {
    /*font-family: 'Segoe UI Semibold' !important;*/
    /*font-family: 'Segoe UI' !important;*/
    font-size: 12px !important;
}   

/* set main font for notes and notes editor */
DIV.noteEditor TEXTAREA,
DIV.notes DIV.content {
    /*font-family: 'Segoe UI' !important;*/
    font-size: 10px !important;
    font-weight: normal;
}

/* set main font color and margin/padding for notes and notes editor */
/* hide full notes when not editing */
DIV.notes DIV.content {
    margin: 0 0 10px 6px !important;
    padding: 0 0 2px 6px !important;
    color: #000ddd !important;
    overflow: auto!important;

    border-left: 1px dashed #D6D6D6 !important;
    width: 100% !important;
}

DIV.noteEditor TEXTAREA {
    margin: 0 0 0 0 !important;
    padding: 1px 0 0 7px !important;
}

DIV.nameEditor TEXTAREA {
    margin: 0 0 0 0 !important;
    padding: 1px 0 0 0px !important;
}

/* Show full notes */
DIV.notes DIV.content {
    height: auto !important;
    overflow: auto !important;
}

/* hide share buttons */
#share_buttons,
#helpButton,
#leftGradient,
#rightGradient {
    display:none;
}

/* Outer border, set to none */
#workflowy > DIV.children {
    border-left:none !important;
}

/* border of each child... I've set to none, but you can add this back in */
DIV.children {
    border-left:none !important;
}

/* adjust margins */
#visible {
    margin:10px auto !important;
}

/* Re-style the tags to look more tag-like */
.contentTag {
    background-color: #46A546 !important;
    border-radius: 3px 3px 3px 3px !important;
    color: #FFFFFF !important;
    font-size: 8px !important;
    /*font-weight: bold !important;*/
    padding: 2px 3px 2px !important;
    white-space: nowrap !important;
}

/* hide the tags when editing */
.content.editing .contentTag {
    visibility: hidden;
}

/* remove the underline from tags */
.content .contentTagText {
    text-decoration: none !important;
}

/* end of MISC CSS */


.noted>.notes>.content {
    min-height: 15px !important;
    line-height: 12px !important;
}



.contentTag[title*="@"] {
    font-weight: normal;
    background-color: #dba4d5 !important;
    color: white !important;
}
.contentTag[title*="#"] {
    background-color: #d1ebd1 !important;
    color: black !important;
}
.contentTag[title*="#Had"],
.contentTag[title*="#Sent"],
.contentTag[title*="#Recieved"],
.contentTag[title*="Attended-Event"],
.contentTag[title*="#Went-to-visit"],
.contentTag[title*="#Received"] {
    background-color: #fcf8c2 !important;
    color: black !important;
}
.contentTag[title*="#urgent"] {
    background-color: #f74545 !important;
    color: white !important;
}

.contentTag[title*="#today"] {
    background-color: #e8caf9 !important; /* #59a7f9 */
    color: black !important;
}

.contentTag[title*="#fix"] {
    background-color: #ff7b00 !important;
    color: white !important;
}

.contentTag[title*="#this-week"] {
    background-color: #cd69f4 !important;
    color: white !important;
}
.contentTag[title*="#unmined"],
.contentTag[title*="#unfinished"]{
    background-color: #424242 !important;
    color: white !important;
}
.contentTag[title*="@P__"] {
    background-color: #0ab24b !important;
    color: white !important;
}
.contentTag[title*="@06:"], 
.contentTag[title*="@07:"], 
.contentTag[title*="@08:"], 
.contentTag[title*="@09:"], 
.contentTag[title*="@10:"], 
.contentTag[title*="@11:"], 
.contentTag[title*="@12:"], 
.contentTag[title*="@13:"], 
.contentTag[title*="@14:"], 
.contentTag[title*="@15:"], 
.contentTag[title*="@16:"], 
.contentTag[title*="@17:"], 
.contentTag[title*="@18:"], 
.contentTag[title*="@19:"], 
.contentTag[title*="@20:"], 
.contentTag[title*="@21:"], 
.contentTag[title*="@22:"] {
    background-color: #db10f2 !important;
    color: white !important;
}
.contentTag[title$="_"] {
    background-color: #66CCFF !important;
    color: white !important;
}

.contentTag[title*="#R___"] {
    background-color: salmon !important;
    color: salmon !important;
}
.contentTag[title*="#high"] {
    background-color: red !important;
    font-weight: 500;
    color: white !important;
}
.contentTag[title*="#med"] {
    background-color: orange !important;
    color: white !important;
}
.contentTag[title*="#low"] {
    background-color: peru !important;
    color: white !important;
}
}
