css:
@-moz-document url-prefix("https://hootsuite.com/dashboard"), domain("hootsuite.com") {
.rc-LoadingModal .-modalDialog
{
    background-color:#333;
    color:#eee;
}

.prc-ModalBase .-modal, .rc-ModalBase .-modal
{
    background-color:#333;
    color:#eee;
}

#header
{
    background-color:#555;
}

#dashboard
{
    background-color:#555;
}

.x-link
{
    color: #2cb7ff
}

.rc-Metadata
{
    color:#bbb;
}

.rc-MessageContext
{
    background-color:#555;
}

#dashboardTabs
{
    background-color:#444;

}
#dashboardTabs .tab
{
    border-right:1px solid #444;

}

#dashboardTabs .tab:hover
{
    background-color:#333;
    color:#eee;
}
 
#dashboardTabs .tab .text
{
    color:#ddd;
}
 
#dashboardTabs .tab.active
{
    background-color:#666;
}
 
#dashboardTabs .tab.active .text
{
    color:#eee;
}










.rc-CategoryHeading
{
    color:#ddd;
}



.rc-Composer
{
    background-color:#555;
}

.rc-ComposerHeader
{
    background-color:#444;
    color:#eee;
    border: 0px
}

.rc-ComposerHeader .-controls
{
    background-color:#666;
}

.rc-ComposerHeader .-controls .-minimizeButton
{
    border: 0px
}

.rc-ComposerHeader .-controls .-closeButton
{
    border: 0px
}

.rc-MessageEditText > .-header > .-title
{
    color:#eee;
}

.-header .-title
{
    color:#eee;
}

.-message
{
    color:#000;
}

.rc-MessagePreviewArea .-previewArea
{
    background-color:#666;
    color:#eee;
}

.rc-MediaLibrary
{
    background-color:#666;
    color:#eee;
}

.-publishHeader
{
    color:#eee;
}

.rc-ComposerFooter
{
    background-color:#444;
    color:#eee;
    border: 0px
}








.hs-app-notification-center .rc-NotificationTray .-trayContent .-trayHeader
{
    background-color:#666;
    color:#eee;
}

.hs-app-notification-center .rc-NotificationTray .-trayContent .-trayBody
{
    background-color:#666;
    color:#eee;
}

.hs-app-notification-center .rc-NotificationTray .-trayContent .-trayFooter
{
    background-color:#666;
    color:#eee;
}

.hs-app-notification-center .rc-BaseNotification .-itemContentContainer .-itemContent
{
    color:#eee;
}

.hs-app-notification-center .rc-BaseNotification:hover 
{
    background-color:#888;
}

.hs-app-notification-center .rc-settings
{
    color: #2cb7ff
}












#streamTabInfo
{
    background-color:#444;
}

#streamsContainer
{
    background-color:#444;

}

.stream .header
{
    background-color:#555;
    border-bottom:1px solid #444;
}
.stream .header .streamName
{
    color:#fff;
}
.stream .stream-scroll,.stream .stream-iframe
{
    background-color:#555;
}
.stream .message,.stream .noMessage,.originalMessage .message
{
    background-color:#555;
    border-color:transparent transparent #666;
    color:#fff;
}
 
.message .actions
{
    background-color:#666;
    border:1px solid #444;

}

.message .networkAvatar
{
    border-color:#555;

}
 
.message .networkName,.message .creatorName
{
    color:#fff;

}
.message p
{
    color:#eee;

}
.stream .ui-input, .stream .ui-textarea {
    background: #333;
}
.message .messageComments .commentEntry textarea.focus {
    background: #333;
    color: #fff;
}
.message-comments textarea.focus 
{
    color: #fff;
}
.stream .ui-input
{
    border:1px solid #222;
    box-shadow: none;
} 
.message p a
{
    color:#bbb;
}
.stream .stream-scroll .messageOptions
{
    background-color:#555;

}
.message .postAttachment.x-attachmentBox
{

    background-color:#5e5e5e;
    border:1px solid #4e4e4e;

}
.message .postAttachment.x-attachmentBox .title a
{
    color:#fff;
}
.message .messageComments
{
    color:#ddd;
}
.message .messageComments .commentList
{
    background-color:#444;
    border:1px solid #3f3f3f;
}
.message .messageComments .commentCount
{
    color:#ccc;
} 
.message .messageComments .comment .networkName
{
    color:#fff;
}
.message .messageComments .commentEntry textarea
{
    background-color:#333;
}

.message .previousMessages .comment 
{
    background-color: #444;
    border-color: #555;
}
.message .previousMessages .comment.originalComment 
{
    background-color: #333;
    border-color: #333;
}
.mediaGallery .-attachedMedia
{
    background-color:#515151;

}

.stream:hover ::-webkit-scrollbar-thumb
{
    background-color:#222;
}
::-webkit-scrollbar-thumb
{
 -webkit-box-shadow: none;
}
.stream .stream-scroll
{
    border-right:none;
}
.stream .message,.stream .noMessage,.originalMessage .message
{
    border-top:1px solid #555;
}


a, .link {
    color: #BBB;
}

.rc-Author a {
    color: #DDD;
}

.streamsContainer {
	background-color: #111;
}

.rc-MessageList {
	background-color: #333;
}

.rc-MessageContext {
	background-color: #555;
	color: #EEE;
}

.rc-MessageListLoadingIndicator {
    background-color: #111;
    color: #EEE;
}

.stream.update .stream-scroll {
    background-color: #888;
}
}
