css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cowlevel.net") {
  body,.setting-list,.comment-list-sub-box{
    background-color: /*[[bgcolor]]*/ !important;
  }
  .box-shadow-0-8-16{
     box-shadow: #000 0 8px 8px;
  }
  .header-nav{
    background-color: /*[[bgcolor]]*/ !important;
    opacity: 0.6;
    color: #112;
    transition: all 0.25s ease;
  }
  .header-nav:hover{
    opacity: 1;
  }
  .profile .nav-list li a{
    color: #112 !important;
  }
  .main,.v2-main,.v3-main{
    color: #224 !important;
  }
  .header-nav .logo a{
    color: #224 !important;
  }
  .beta{
    color: #224 !important;
  }
  .text-shadow3{
    text-shadow: #111 1px 1px 1px;
  }
  .quick-href a{
    color: #112 !important;
  }
  .grey{
    color: #666 !important;
  }
  .grey5{
    color: #112 !important;
  }
  .grey6{
    color: #222 !important;
  }
  .gl-user-card-box .grey6,.gl-user-card-box .white{
    color: #fff !important;
    text-shadow: 1px 1px 1px #000;
  }
  .grey8{
    color: #224 !important;
  }
  #search-result-box{
    background-color: /*[[bgcolor]]*/ !important;
  }
  #search-result-box a{
    color: #112 !important;
  }
  .search-game-result li:hover{
    background-color: #d1d1d1 !important;
  }
  .title-grey{
    color: #224 !important;
  }
  .white,.white2,.item-tit-a{
    color: #112 !important;
  }
  .part-right .item-tit-a{
    color: #fff !important;
  }
  .card-list .item-tit{
    color: #fff !important;
  }
  .gl-hover-text-3:hover{
    color: #448 !important;
  }
  .tab-left .vote-btn-v2{
    color: #112 !important;
  }
  .vote-btn-v2:hover{
    background-color:rgba(255,66,95,.4) !important;
  }
  .gl-load-more-btn{
    background-color:rgba(17,17,34,.2) !important;
  }
  .line,.line2,.line3{
    background: #112 !important;
    opacity: 0.3;
  }
  .h-tag li{
    border: 1px solid rgba(17,17,34,.3) !important;
  }
  .h-tag li.on, .h-tag li:hover{
    box-shadow: 1px 2px 3px #666 !important;
  }
  .nav-list li::after{
    background: #f6f6f6 !important;
  }
  .notice-tip-window{
    background: /*[[bgcolor]]*/ !important;
  }
  .notice-tab-list li.on i{
    color: rgb(255,66,95) !important;
  }
  .notice-tab-list li.on .agree-tag{
    border-bottom: 14px solid rgba(255,66,95,.9) !important
  }
  .notice-tab{
    color: #112 !important;
  }
  .notice-tab li.weidu{
    background-color: #ddd !important;
  }
  .star-level-box .fa, .clock-level-box .fa,.star-color,.star-list{
    color: #fa0 !important;
  }
  .header-nav input{
    background-color: rgba(39,40,57,1) !important;
    box-shadow: inset 0px 2px 4px 0px rgba(0,0,0,0.50) !important;
  }
  input::-moz-placeholder{
    color: /*[[bgcolor]]*/ !important;
  }
  .global-search-box i{
    color: #fff !important;
  }
  .global-search-box input{
    padding: 3px 12px 3px 35px;
  }
  .quick-href.on{
    background: rgba(0,0,0,.1) !important;
  }
  .repassword-box h2,.notify-box h2{
    border-bottom: 1px solid #224 !important;
  }
  .bg-gradient,.bg-gradient-2{
    background: -moz-linear-gradient(top, rgba(241,241,241,.1), rgba(241,241,241,1)) !important;
    background: -webkit-linear-gradient(top, rgba(241,241,241,.1), rgba(241,241,241,1)) !important;
  }
  .vote-btn-v3 .agree-tag{
    border-bottom: 14px solid #224 !important;
  }
  .grey3{
    color: #112 !important;
  }
  .gl-hover-text{
    color: #224 !important;
  }
  .achievement-list span{
    color: #112 !important;
  }
  .tip-window::after{
    border-bottom: 16px solid rgba(241,241,241,1) !important;
  }
  .tip-window{
    border: 1px solid rgba(255,66,95,.9);
    box-shadow: 1px 4px 5px rgba(0,0,0,.5);
  }
  .wangEditor-container .wangEditor-btn-container-btn-default{
    color: #224;
  }
  .wangEditor-container .wangEditor-btn-container, .wangEditor-container .wangEditor-textarea, .wangEditor-container .wangEditor-drop-menu, .wangEditor-container .wangEditor-drop-menu a, .wangEditor-modal-container .wangEditor-modal{
    background: /*[[bgcolor]]*/ !important;
    color: #224;
  }
  .wangEditor-container .wangEditor-btn-container-group{
    border-right: 1px solid rgba(0,0,0,.1);
  }
  .wangEditor-modal-container .wangEditor-modal-header-line{
    border-top: 1px solid rgba(0,0,0,.1);
  }
  .wangEditor-modal-container .wangEditor-modal input[type="text"]{
    color: #224;
    padding: 7px;
  }
}

@-moz-document regexp("^[^\.]*cowlevel.net/about") {
  .main{
    color: #fff !important;
  }
}

@-moz-document url("https://cowlevel.net/") {
  .grey5{
    color: rgba(255,255,255,.7) !important;
  }
}
