css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(https://happyride.se/) {

.anp1 {
    height: 0;
}

.navbar-default .navbar-brand {
    visibility: hidden;
    width: 0px;
}

.p-nav-smallLogo {
    width: 0px;
    padding: 0px;
    visibility: hidden;
}

.p-body-inner {
    padding: 0px;
}

.p-body-header {
    margin-top: 10px;
}

.p-title {
    margin-bottom: -5px;
}

.p-description {
    visibility: hidden;
    height: 0;
    font-size: 12px;
    color: #484438;
}

.p-title-value {
    font-family: verdana;
    font-weight: bold;
    margin: 0 0 0 0;
    margin-right: auto;
}

.p-title-pageAction {
    margin-bottom: -15px;
}

.message-avatar {
    height: 0;
}

.message-avatar-wrapper .message-avatar-online {
    bottom: -2em;
}

.message-cell.message-cell--user, .message-cell.message-cell--action {
    position: unset;
}

.reaction.reaction--small, .reaction.reaction--medium {
    /*visibility: hidden;*/
    padding: 0px;
    display: none
}

.message-userTitle {
    visibility: hidden;
    height: 0;
}

.pageNav-page.pageNav-page--current {
    font-family: verdana;
    font-size: 11px;
    background: transparent;
    color: #484438;
    border: 0px;
    font-weight: bold;
}

.pageNav-page {
    font-family: verdana;
    font-size: 11px;
    background: transparent;
    color: #484438;
    border: 0px;   
}

.pageNav-jump {
    font-family: verdana;
    font-size: 11px;
    background: transparent;
    color: #484438;
    border: 0px;    
    font-weight: bold;
    padding: 0px 0px;
}

.p-breadcrumbs {
    background-color: transparent;
    border: 0px;
    padding: 0px 0px;
    margin: 0 0 0 0;
}

.p-breadcrumbs a {
    color: #484438;
    font-size: 11px;
}

.bbCodeBlock {
    border-left: 3px solid #484438;
}

.button, a.button {
    font-family: verdana;
    font-size: 12px;
    color: #484438;
    font-weight: bold;
    background: transparent;
    border: 0px;
}

.button.button--cta, a.button.button--cta {
    font-family: verdana;
    font-size: 11px;
    color: #484438;
    font-weight: bold;
    background: transparent;
    border: 0px;
}

.block-container {
    font-family: verdana;
    border-width: 0px;
}

.block-filterBar {
    padding: 0px 5px;
    font-family: verdana;
    font-size: 11px;
}

.filterBar-menuTrigger {
    color: #bdb99a;
}

.structItem.structItem--note .structItem-cell {
    padding-top: 2px;
    padding-bottom: 2px;
    font-size: 11px;
    text-align: center;
}

.hideavatar .title .new-flag {  
    background-color: transparent;
    color: #e1312d;
    padding: 2px 2px;
    text-transform: none;
    font-weight: normal;
}

.hideavatar .title .structItem-pageJump a {
    color: #006fa4;
    background: transparent;
    border: 0px;
    border-radius: 0px;
    padding: 0 2px;
    opacity: 1;
    font-weight: normal;
}

.hideavatar .title {
    font-size: 11px;
    padding: 5px;
    vertical-align: middle;
}

.hideavatar .reply_count {
    font-size: 11px;
    padding: 5px;
    vertical-align: middle;
}

.hideavatar .thread_username {
    font-size: 11px;
    padding: 5px;
    height: 28px;
    vertical-align: middle;
}

.hideavatar .reply_username {
    font-size: 11px;
    padding: 5px;
    vertical-align: middle;
}

.message-inner {
    font-size: 11px;  
}

.message-userTitle {
    font-size: 11px;
}

.message-attribution {
    font-size: 9px;
    padding-bottom: 0px;
    border-bottom: 1px solid #c0bfa9;
}

.message-body, .p-body-inner {
    font-family: verdana;
    font-size: 11px;
    line-height: 1.5;
}

.bbCodeBlock-title {
    padding: 6px 10px;
    font-size: 11px;
    color: #006fa4;
    background: #e9e7d7;
}

.bbCodeBlock.bbCodeBlock--quote .bbCodeBlock-content {
    font-size: 11px;
}

.message-newIndicator {
    font-size: 11px;
    color: #e1312d;
    background: transparent;
    border-radius: 0px;
    padding: 0px;
}

.message-actionBar .actionBar-set {
    margin-top: 0px;
    font-size: 9px;
}

.message .reactionsBar {
    margin-top: 0px;
    padding: 0px;
    visibility: collapse;
    height: 0px;
}

.message-signature {
    margin-top: 10px;
    font-size: 9px;
    color: #262626;    
    border-top: 0px;
}

.message-content {
    padding-bottom: 5px;
}

.message-cell {
    padding-top: 3px;
    padding-bottom: 0px;
}

.message-footer {
    border-top: 1px solid #a4a38d;
}

.button.button--link, a.button.button--link {
    font-size: 11px;
    background: transparent;
    color: #484438;
    font-weight: bold;
    border:0px;
}

.tagItem {
    font-size: 11px;    
    background: transparent;
    color: #484438;
    font-weight: bold;
    border: 0px;
}

.block-outer {
    padding-bottom: 0px;
}

.p-body-main--withSidebar {
    width: 880px;
}

.p-body-sidebar {
    visibility: hidden;
    width: 0px;
}

.block-outer.block-outer--after {
    padding-top: 0px;
}

/* Köp och sälj */

.form-inline {
    margin: 0 0 5px;
}

input[type="text"], input[type="tel"], input[type="email"], input[type="url"], input[type="password"], input[type="search"], textarea {
    font-size: 11px;
    height: 26px;
    font-family: verdana;
}

select.form-control {
    padding: 0 40px 0 8px;
    float: none;
    font-size: 11px;
    font-family: verdana;
    height: 26px;
}

.btn {
	padding: 0px 5px 0px 5px;
    height: 26px;
    letter-spacing: 0px;
    font-family: verdana;
    text-transform: lowercase;
    font-size: 11px;
    font-weight: normal;
}

.alert-info {
    visibility: hidden;
    height: 0px;
    padding: 0px;
    border: 0px;
    margin: 0px;
}

.inner-block {
	margin-right: 0px;
}  

.container-fluid {
	max-width: 900px;
} 

.fixed-col {
    display: none !important;
} 


.theme3 .breadcrumb {
    background-color: #7d796e;
}

.breadcrumb li {
    font-size: 11px;
    margin: -7px -7px -7px 7px;
}

.breadcrumb {
    font-weight: bold;
    padding: 0 0px;
    margin: 0 0 0px;
}

.breadcrumb a {
    color: #313131;
}

.breadcrumb > li + li:before {
    height: 0px;
}

.table.sales-table td {
    vertical-align: top;
    font-size: 11px;
}

.pagination {
    font-family: verdana;
    font-size: 11px;
    text-transform: lowercase;
    font-weight: normal;
    letter-spacing: 0px;
    text-align: center;
}

.pagination > li > a, .pagination > li > span {
    position: relative;
    float: left;
    padding: 0px 0px;
    line-height: 1;
    text-decoration: none;
    color: #333;
    background-color: #fff;
    border: none;
    margin-left: -6px;
}

.pagination > li > a {
    border-width: 0px;
    min-width: 15px;
    height: 20px;
    line-height: 20px;    
    background: none;
}

.pagination > li {
    padding: 0 5px;
    display: inline-block;
}

.table > tbody > tr > td {
    padding: 5px;
}

.table > thead > tr > th {
    background-color: #484438;
    font-size: 11px;
    color: #e0ddcc;
}

h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    font-family: verdana;
    font-weight: bold;
    line-height: 1.1;
    color: inherit;
}

h1, .h1 {
    font-size: 14px;
}

h4 {
    font-size: 12px;
}


body {
    font-family: verdana;
    font-size: 11px;
    background-color: #e0ddcc;
}


.actionBar-set.actionBar-set--internal {
    float: right;
}

}
