css:
@-moz-document url-prefix("https://epic-newdevices.dev.vivifyhealth.com/CaregiverPortal/") {
/* General */

.navbar-default {
    overflow: auto;
}

.col-auto {
    display: block;
}
body {
    font-size: 14px;
}
body.fixed-navbar {}
.navbar-default {
    background-color: #303030;
}
.navbar-default li:nth-child(2) {
    -webkit-border-top-right-radius: 8px;
    -moz-border-radius-topright: 8px;
    border-top-right-radius: 8px;
}
#side-menu>li {
    border-bottom: solid 1px #505050;
}
#side-menu.nav li>a {
    color: #FFFFFF;
    font-size: 14px;
}
#side-menu.nav li.active {
    border-left: 0px;
    background: #333550;
}
#side-menu.nav .badge-info,
#side-menu.nav .label-info {
    background-color: #FFFFFF;
    color: #3E5884;
}
.navbar-default .nav>li>a:focus,
.navbar-default .nav>li>a:hover {
    background-color: #505050;
}
#side-menu.nav .nav-second-level li:last-child {
    margin-bottom: 0px;
}
.mini-navbar .nav-second-level {
    background-color: #505050;
    width: 200px;
    -webkit-transition: .25s ease-in-out;
    -moz-transition: .25s ease-in-out;
    -o-transition: .25s ease-in-out;
    transition: .25s ease-in-out;
}
.collapse-bar {
    background-color: transparent;
    left: 255px;
    -webkit-transition: .25s ease-in-out;
    -moz-transition: .25s ease-in-out;
    -o-transition: .25s ease-in-out;
    transition: .25s ease-in-out;
}
body.mini-navbar .collapse-bar {
    left: 105px;
}
.collapse-bar a {
    color: #727272;
}
.collapse-bar-flex {
    position: absolute;
    height: auto;
    top: 60px;
    bottom: 0px;
    display: block;
    border-top: solid 1px #D4D4D4;
}
.collapse-bar-flex collapse-menu {
    position: absolute;
    top: 42%;
    left: 0px;
}
/* Top Nav Bar */

body .navbar-fixed-top {
    left: 70px;
}
body:not(.mini-navbar) .navbar-fixed-top {
    left: 215px;
}
.navbar.border-bottom {
    border-bottom: solid 1px #D4D4D4 !important;
}
.navbar-fixed-top,
.navbar-static-top {
    background: #ffffff;
}
.nav.navbar-right>li>a {
    color: #2B72C0;
}
#navigation .breadcrumbs-pull-bottom {
    height: 18px;
    margin-bottom: -9px;
    margin-left: 20px;
    top: 35%;
    position: absolute;
}
.breadcrumb {
    background-color: transparent;
    color: #999999;
}
/* Logo */

.nav .nav-header {
    padding: 0;
    background-image: none;
    background-color: #FFF;
    height: 61px;
    border-bottom: solid 1px #D4D4D4 !important;
}
body.mini-navbar .nav-header {
    background-color: #FFF;
    padding: 0;
}
.profile-element {
    width: 240px;
    height: 60px;
    background-image: url("http://vivifyhealth.com/content/themes/caregiver/2016/images/vivify-logo-onlight.svg");
    background-position: 28px 2px;
    background-repeat: no-repeat;
    background-size: 175px 60px;
}
.profile-element img {
    display: none;
}
.logo-element {
    width: 240px;
    height: 60px;
    color: rgba(255, 255, 255, .0);
    background-image: url("http://vivifyhealth.com/content/themes/caregiver/2016/images/vivify-bug-onlight.svg");
    background-position: 16px 5px;
    background-repeat: no-repeat;
    padding: 0px;
    background-size: 50px 50px;
}
/* Page Wrapper */

body.mini-navbar #page-wrapper {
    margin-left: 70px;
}
#page-wrapper {
    margin-left: 220px;
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover !important;
    background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/background-diamonds.jpg') !important;
}
/* Content */

.ibox-title {
    color: #3E5884;
    background-color: rgba(191, 196, 202, .15);
    -webkit-border-top-left-radius: 8px;
    -webkit-border-top-right-radius: 8px;
    -webkit-border-bottom-right-radius: 0px;
    -webkit-border-bottom-left-radius: 0px;
    -moz-border-radius-topleft: 8px;
    -moz-border-radius-topright: 8px;
    -moz-border-radius-bottomright: 0px;
    -moz-border-radius-bottomleft: 0px;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    border-top: 0px;
}
.ibox-content {
    background-color: rgba(255, 255, 255, .7);
    -webkit-border-top-left-radius: 0px;
    -webkit-border-top-right-radius: 0px;
    -webkit-border-bottom-right-radius: 8px;
    -webkit-border-bottom-left-radius: 8px;
    -moz-border-radius-topleft: 0px;
    -moz-border-radius-topright: 0px;
    -moz-border-radius-bottomright: 8px;
    -moz-border-radius-bottomleft: 8px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 8px;
    border-bottom-left-radius: 8px;
    border: solid 1px rgba(191, 196, 202, .3);
    z-index: 1;
}
.ibox-content h5 {
    color: #061871;
}
.ibox-content:after {
    content: "";
    margin-top: 17px;
    background-image: url(http://www.vivifyhealth.com/content/themes/caregiver/2016/images/shadow.png);
    height: 75px;
    width: 100%;
    float: left;
    z-index: 0;
    background-repeat: no-repeat;
    background-size: 100%;
    opacity: .4;
}
.ibox-content>.ibox-content {
    background-color: transparent;
    border: none;
    padding: 0;
    margin: 0;
    background-image: none;
}
.ibox-content>.ibox-content:after {
    background-color: transparent;
    border: none;
    padding: 0;
    margin: 0;
    background-image: none;
}
/* Buttons */

.btn-primary {
    background-color: #2B72C0;
    border-color: #2B72C0;
    color: #FFF;
}
.btn-primary:hover,
.btn-primary:active {
    background-color: #AAAAAA;
    border-color: #AAAAAA;
    color: #FFF;
}
.btn-default {
    background-color: #3E5884;
    border-color: #3E5884;
    color: #FFF;
}
.btn-default:hover,
.btn-default:active {
    background-color: #AAAAAA;
    border-color: #AAAAAA;
    color: #FFF;
}
.btn {
    border-radius: 6px;
}
.btn-primary.active[disabled],
.btn-primary.disabled,
.btn-primary.disabled.active,
.btn-primary.disabled:active,
.btn-primary.disabled:focus,
.btn-primary.disabled:hover,
.btn-primary[disabled],
.btn-primary[disabled]:active,
.btn-primary[disabled]:focus,
.btn-primary[disabled]:hover,
fieldset[disabled] .btn-primary,
fieldset[disabled] .btn-primary.active,
fieldset[disabled] .btn-primary:active,
fieldset[disabled] .btn-primary:focus,
fieldset[disabled] .btn-primary:hover {
    background-color: #DDDDDD;
    border-color: #DDDDDD;
}
/* Forms */

.form-control:focus,
.has-success .form-control {
    border-color: #2B72C0;
}
/* Specific */

#kit-logistics-move-kits .kits {
    margin: 0;
    width: 100%;
    clear: both;
}
}
