css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("apps.rackspace.com") {
  
  body{
    background-image: url(http://ddf912383141a8d7bbe4-e053e711fc85de3290f121ef0f0e3a1f.r87.cf1.rackcdn.com/rackspace-email-hosting-logo.png);
    background-repeat: no-repeat;
    background-position: 20% -150%;
    background-color: #fff;
    font-family: arial;
  }
  
  .page .header, #wm_core_header{
    background-color: /*[[baseColor]]*/;
  }
  
  input.user, input.pwd {
    width: 300px;
  }
  
  div.page{
    width:500px;
    float: right;
  }
  
  div.form div.left {
    width: 300px;
  }
  
  .form .right, .base{
    display: none;
  }
  
  div.form_wrapper {
    margin-top:50px;
    position: absolute;
    bottom: 0px;
    top: 0px;
    right: 0px;
    z-index: -1;
    box-shadow: -3px 2px 12px #AFAAAA;
    border:none;
  }
  /*Mail Page*/
  #wm_core_navlinks{
    width: 250px;
    margin-right: 10px;
    float: right;
    margin-top: 7px;
  }
  #wm_core_search{
    width: 250px;
    float: right;
  }
  #wm_core_header table {
    height: 42px;
  }
  #wm_core_header {
    min-height: 0px;
  }
  #wm_core_content a {
    color: #009100;
  }
  .Webmail_Controls_Dialog_TitleBar .bar {
    background-color: /*[[baseColor]]*/;
  }
  .mce-content-body {
    background-image: none !important;;
  }  
}
