css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("happyride.se") {

/*
Happyride.se brownie theme

Based upon style by "pbeijer".
https://userstyles.org/styles/124361/happyride-se-brunt

Removed footer and changed button-hover-color from green to white

*/

#header {
    background: #313131;
    color: #fff;
    position: relative;
    height: 35px;
    margin: 0 0 3px;
    z-index: 1000;
}
#header .container-fluid {
    padding: 0 !important;
    max-width: 900px;
}
#footer {
    display: none !important;
}
.forum-block .table-holder {
    margin: -5px -20px;
}
.anp1 {
    height: 0px;
}
element.style {
    border: 0px;
    vertical-align: bottom;
    width: 0px;
}
.table > thead > tr > th {
    vertical-align: bottom;
    border-bottom: 0px solid #f4f4f4;
}
.table.forum-table > thead > tr > th:nth-child(2) {
    padding-left: 6px;
    padding-top: 5px;
}
.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus {
    color: #e0ddcc;
}
.navbar-default .navbar-nav li a:hover {
    border-color: #fff !important;
}
.table > thead > tr > th {
    background-color: #484438;
    padding: 0px 0 5px 0px;
    font-size: 11px;
    color: #e0ddcc;
}
.navbar-brand {
    padding: 0;
    margin: 0;
    height: 0px;
    width: 0px;
    background-color: white;
}
.search-form {
    width: 180px;
}
.navbar-default .navbar-nav > li > a {
    padding: 3px 2px 0px;
    border-bottom: 4px solid transparent;
    color: #e0ddcc;
}
.navbar {
    min-height: 30px;
}
.navbar-header {
    height: 35px;
    overflow: hidden;
}
.search-form:before {
    content: "";
    position: absolute;
    width: auto;
    height: auto;
    left: 5px;
    top: 5px;
    font-family: "FontAwesome";
    font-size: 15px;
    line-height: 15px;
    color: #e0ddcc;
}
.navbar-default .navbar-nav > li {
    padding: 0 -1px 5px;
    font-size: 12px;
    text-transform: lowercase;
    font-family: verdana;
}
.navbar-default .navbar-nav > li.active > a {
    border-color: #e0ddcc;
}
.search-form input[type="search"] {
    height: 25px;
}
.user-block .user-bar {
    padding: 0 8px 0 15px;
    line-height: 35px;
}
.user-block .btn-mail {
    line-height: 35px;
}
.user-block .btn-mail .fa {
    font-size: 15px;
    color: #e0ddcc;
}
.user-block .user-bar .opener {
    font-size: 15px;
    color: #e0ddcc;
}
.post-password-form input, .search-form input {
    float: left;
    text-transform: lowercase;
    font-size: 12px;
    font-family: verdana;
}
.search-form {
    margin: 5px 0 0;
}
.inner-block {
	margin-right: 0px;
}  
.anp1-container {
    margin: 0px 0px 0px 0px;
}
.container-fluid {
	max-width: 900px;
}  
.fixed-col {
    display: none !important;
} 
.subgroups {
    float: right;
    padding-bottom: 0px;
    clear: both;
}
.breadcrumb li {
    font-size: 11px;
    margin: -7px -7px -7px 7px;
}
.breadcrumb {
    font-weight: normal;
    padding: 0 0px;
    margin: 0 0 0px;
}
.breadcrumb a {
    color: #313131;
}
.breadcrumb > li + li:before {
    height: 0px;
}
.table > thead > tr > th {
    font-weight: bold;
    font-size: 11px;
    padding: 0px 0 2px 6px;
}
.table.forum-table tbody > tr > td .pages {
	font-size: 9px;
    font-weight: normal;
    color: #4c4c4c;
    font-style: italic;
    padding: 0 20px 0 10px;
}
.table.forum-table tbody > tr > td.col-1 {
    width: 55%;
    padding: 5px 0 5px 0px;
}
.label {
    text-transform: lowercase;
    font-size: 11px;
    font-weight: normal;
}
.forum-block {
    background: #fff;
    padding: 5px 20px 5px;
    margin: 0 0 27px;
    line-height: 20px;
}
.table.forum-table tbody > tr > td {
    border: none;
    font-size: 11px;
    line-height: 12px;
    padding: 0px 0 0px 16px;
    vertical-align: middle;
}
.forum-block {
    background: #fff;
    padding: 5px 20px 5px;
    margin: 0 0 27px;
    line-height: 18px;
}
.forum-block .pagination {
    margin: 3px 0 0;
}
.forum-post .signature {
    font-size: 9px;
    line-height: 14px;
}
.forum-post .edited {
    font-size: 9px;
    font-style: italic;
    padding-top: 10px;
}
h2 {
    font-size: 15px;
}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    font-family: verdana;
    font-weight: bold;
    line-height: 1.1;
    color: inherit;
}
body {
    font-family: verdana;
    font-size: 12px;
    line-height: 1.9;
    color: #000000;
    background-color: #ececec;
}
blockquote {
    padding: 0 11px;
    margin: 0 0 20px 10px;
    font-size: 11px;
}
.forum-post hr {
    margin-top: 5px;
    margin-bottom: -2px;
}
.forum-list li.forum-post {
    padding: 12px 12px 7px;
}
.buttons-group {
    margin: 0px -10px -7px -5px;
}
.forum-list li.forum-post:nth-child(2n+2) {
    background: #e0ddcc;
    border: none;
}
.pagination {
    font-family: verdana;
    font-size: 11px;
    text-transform: lowercase;
    font-weight: normal;
    letter-spacing: 0px;
    text-align: center;
}
.pagination > li > a, .pagination > li > span {
    position: relative;
    float: left;
    padding: 0px 0px;
    line-height: 1;
    text-decoration: none;
    color: #333;
    background-color: #fff;
    border: none;
    margin-left: -6px;
}
.pagination > li > a {
    border-width: 0px;
    min-width: 15px;
    height: 20px;
    line-height: 20px;    
    background: none;
}
.pagination > li {
    padding: 0 5px;
    display: inline-block;
}
.btn {
	padding: 0px 5px 0px 5px;
    height: 26px;
    letter-spacing: 0px;
    font-family: verdana;
    text-transform: lowercase;
    font-size: 11px;
    font-weight: normal;
}
#main .breadcrumb {
    background-color: #7d796e;
}
}
