css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("w3schools.com") {
  .topnav{
    background-color: #343F48;
  }
  #leftmenu a:hover {
    background-color: #343F48;
    color: #F1F1F1;
  }
  #right a:hover {
    text-decoration: none;
  }
  a.w3-btn:hover, a.w3-btn:active {
    background-color: #343F48;
    color: #f1f1f1;
    box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2), 0px 6px 20px 0px rgba(0, 0, 0, 0.19);
  }
  .table-responsive th, .w3-table-all th{
    background-color: #343F48;
    color: #F1F1F1;
  }
  .bottomad, #mainLeaderboard, #skyscraper,  #tryitform p,  #tryitLeaderboard{
    display: none;
  }
  
}
