css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("csgofunpot.com") {
body {
  height: 100%;
  margin: 0;
  background: #202020 none repeat scroll 0% 0%;
  font: 14px/15px 'Exo 2', sans-serif;
  min-height:100%; 
  
}
    /*topbar*/
    
    #topbar{
background-color:#383838;
height:54px;
color:white;
top:0px;
width:100%;
box-shadow:0px 0px 0px #000000;
margin-bottom:5px;
        
}
    .text{
        display: none
}
    #topbar img{
        display: none
}
   #topbar div a{
        color: white
        
}
    .game .box .number {
    display:none;
}
    .game .box {
    background-color: rgba(0, 0, 0, 0.68);
    overflow: hidden;
    clear: both;
    padding: 0px 0px 0px 0px;
}
        /*body*/
    .game .btwn_game {
    font: 700 25px/50px "Roboto",sans-serif;
    margin: 0px 365px 0px ;
    width: 35%;
    height: 50px;
    color: white;
    text-decoration: blueviolet;
    text-align: center;
    display: block;
    border-radius: 0px;
    border-bottom: 0px solid #000;
    background: #383838;
    transition: all 0.5s ease 0s;
    float: right;
    opacity: 1;
}
    #placed {
    width: 20%;
    float: left;
    margin-left: 1%;
    margin-right: 1%;
    border: 0px solid #000;
    box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0);
    background-color: rgba(98, 98, 98, 0.00);
}
    h2 {
        display:none
    
}
    .promo-cover ul {
    list-style: outside none none;
    color: rgb(255, 255, 255) !important; 
    font-size: 15px;
    line-height: 18px;
}
    .depostinfo {
    font-size: 12px;
    color: rgb(255, 255, 255);
}
    .chat header {
    padding: 1px 1px;
    color: #FFF;
    font-size: 14px;
    background-color: rgba(98, 98, 98, 0.00);
}
    body div#wrapper div#main div.sidebar div.chat header img#emots {
        display:none
    }
    #muteSound {
    opacity: 1;
    color: #FFF;
    text-decoration: none;
    float: right;
    margin-right: 1px;
    background: rgba(0, 0, 0, 0.0);
    border-radius: 0px;
    padding: 0px 0px;
    transition: all 0.5s ease 0s;
    display: inline;
}
    .sidebar .online {
    margin-top: 0px;
    background-color: rgba(98, 98, 98, 0.00) !important;
    padding: 0px 0px 0px;
    text-align: center;
    border: 0px solid #000;
    box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0.73);
}
    #sendie {
    background: #393939 none repeat scroll 0% 0%;
    color: #FFF;
    height: 50%;
    width: 94%;
    padding: 5px;
    font: 14px "Lucida Grande",sans-serif;
    float: right;
    margin-top: 0px;
    margin-right: 0px;
    margin-bottom: 0px;
    outline: none !important;
    border: 0px
}
    .sidebar {
    float: right;
    width: 20%;
    margin-right: 1%;
    margin-top: 2px;
    background-color: rgba(98, 98, 98, 0.00)    
}
    .chat {
    right: 5px;
    bottom: 0px;
    width: 100%;
    border: 0px solid #000;
    box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0);
    color: #FFF
}
    #chat-area {
    height: 600px;
    width: auto;
    overflow: auto;
    padding: 15px;
    background-color: rgba(98, 98, 98, 0.00);
    
}
    .game {
border: 0px solid #000;
    box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0.73);
    }    
    li table tbody tr td strong div.depostinfo span.nickname center {
    color: #FFF !important;
    }
    html body div#wrapper div#main div#placed div.stuffs.promo-cover ul#game-sts div.roundplayers li table tbody tr td strong div.depostinfo span.nickname center span {
    color: #FFF !important;
    }
    html body div#wrapper div#main div.sidebar div.chat div#page-wrap div#chat-wrap div#chat-area div p a {
    color: #FFF !important;
    }
    html body div#wrapper div#main div.sidebar div.chat div#page-wrap div#chat-wrap div#chat-area div p span {
    color: #FFFFFF !important;
    }
    html body div#wrapper div#main div.content div.game {
    border: 0px !important;
    box-shadow: 0 0 0 0 !important;
    }
    html body div#wrapper div#main div.content div.game div.progress {
    background-color: rgba(0, 0, 0, 0.00);
overflow: hidden;
padding: 5px 0;    
    }
    .game .box {
  background-color: rgba(0, 0, 0, 0.0);
  clear: both;
  overflow: hidden;
  padding: 0;
}
 .sidebar .online .box {
  background-color: rgba(255, 255, 255, 0.0);
  padding: 3px 0 7px;
}
    .players-percent {
  background-color: rgba(255, 255, 255, 0.0);
  padding-top: 15px;
  position: relative;
  width: 100%;
}
    #otpsoob {
  background-color: rgba(0, 0, 0, 0.0);
  height: 59px;
}
   html body div#wrapper div#main div.sidebar div.online div.box span#usersOnline span#on_now a img{
   border: 0px solid white !important;
height: 45px!important;
margin: 0px!important;;
width: 45px!important;;    
   
   }
html body div#wrapper div#main div.content div.game div.progress div.amount span#bonusitems center div {
    border: 0px!important;
}
    html body div#wrapper div#main div.content div.game div.progress div.amount font {
        display:none !important;
    }
    html body div#wrapper div#main div.content div.game div#rouletboxelement.style {
  background-color: rgba(0, 0, 0, 0.0) !important;;
  height: 150px;
  width: 100%;
}
}
