css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://publib.boulder.ibm.com/bpcsamp/"), url-prefix("http://publib.boulder.ibm.com/bpcsamp/") {
	p, ul, ol, td, th, div {
		font-size: 16px;
		line-height: 1.5em;
	}

	.heading {
		font-size: 16px;
		line-height: 1.5em;
	}
  
	* {
		font-family: 'Proxima Nova', 'Helvetica Nenu', Helvetica, Verdana, Arial, sans-serif;
	}
  
  	h1, h2, h3, h4, h5 {
		font-family: 'Proxima Nova', 'Helvetica Nenu', Helvetica, Verdana, Arial, sans-serif;
    }

	.codeSection {
		font-family: Inconsolata, "Courier New", Courier, mono;
		font-size: 16px;
		background-color: #CCCCCC;
		padding: 10px;
	}
}
