css:
@-moz-document url("http://www.diskusjon.no/index.php?showforum=276"), url-prefix("http://www.diskusjon.no") {
#menuTop {
    display: block;
    background: #777;
    box-sizing: border-box;
    width: 100%;
    min-width: 1000px;
    margin: 0 auto;
}

body.network-tek {
    background-color: #777777;
}

#content {
    background: #777;
    padding: 10px 10px;
    line-height: 120%;
    -webkit-box-shadow: 0 5px 9px rgba(0,0,0,0.1);
    -moz-box-shadow: 0 5px 9px rgba(0,0,0,0.1);
    box-shadow: 0 5px 9px rgba(0,0,0,0.1);
}

.site-tek-no #header_bar {
    background: #717171 !important;
}

.network-promo {
    background: #777777;
    margin: -22px -22px 15px;
    padding: 15px 22px 15px;
}

#LiveFeedContent table td, #LiveFeedContent table th {
    border-bottom: 0;
    padding: 4px 5px;
    background-color: rgba(0,0,0,0.2);
}



/* Underforum */
/*
.ipsType_small {
    font-size: 12px;
    background-color: #777777;
}
*/

table.ipb_table td {
    padding-top: 8px;
    padding-bottom: 8px;
    background-color: #777777;
}


.desc, .desc.blend_links a, p.posted_info {
    font-size: 12px;
    color: #333;
}

table.ipb_table .subforums li.unread {
    font-weight: bold;
    background-color: #777777;
}

.network-tek .ipsBox {
    background: #929090;
}

#header_bar {
    background: #545353 !important;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    padding: 0;
    position: relative;
}
  
.left-content-column {
    width: 180px;
    position: absolute;
    right: 100%;
    left: auto;
    top: 0;
    background: #777;
    border: 1px solid #1F1F1F;
}

#forumList li div {
    background-color: #9E9E9E;
}

#forumList li div {
    background-color: #9E9E9E;
}

#forumList li div {
    border-bottom: 1px solid #6D6D6D;
    list-style: none;
    padding: 6px 6px 0px 6px;
    overflow: hidden;
    text-overflow: ellipsis;
}

}
