css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(happyride.se) {
/*

Happyride White Imageless Theme
by Sambal Oelek

Based upon my "Happyride White Theme" version 18 feb 2016

*/
.container-fluid {
	background: #bbb !important;
}
.main-block {
	background: #fff !important;
}
.inner-block {
	margin-right: 0px !important;
}    
#body {
	background: #dcdcdc !important;
}
.forum-block {
	background: #fff !important;
    color: #000 !important;
    margin: 0 0 0px !important;
}
.forum-block a {
    color: #555 !important;
}
.main-block .table.forum-table tbody > tr:nth-of-type(2n+1), .main-block .forum-list li.forum-post:nth-child(2n+2) {
	background: #eee none repeat scroll 0% 0% !important;
	border-color: #eee !important;
}
.forum-list li.forum-post {
	background: #fff !important;
	border-right: #dcdcdc solid 0px !important;
    border-left: #dcdcdc solid 0px !important;
}
#main .forum-list li.forum-post blockquote, #main blockquote {
	border-left-color: #999;
}
#header {
	background: #dcdcdc !important;
}
#footer {
    display: none !important;
}
.breadcrumb {
    display: none !important;
}
.active {
    background: #bbb !important;
}
.search-form {
	background: #bbb !important;
    max-width: 90px !important;
}
.search-form::before {
	color: #666 !important;
}
.post-password-form input, .search-form input {
    text-transform: none !important;
	font-family: "Arial" !important;
} 
.btn {
    color: #555 !important;
	background: #eee !important;
    border-color: #666 !important;
    font-family: "Arial" !important;
    text-transform: none !important;
    font-weight: normal !important;
    letter-spacing: 0px !important;
}
#phorum_textarea {
    border-color: #dcdcdc !important;
}
.fixed-col {
    display: none !important;
} 
#main hr {
    border-color: #000 !important;
}
#main b {
    color: #000 !important;
}
.navbar-brand {
    display: none !important;
}
.navbar-default {
    min-width: 800px !important;
    max-width: 800px !important;
    background: #bbb !important;
}
.navbar-default .navbar-nav > li > a {
    text-transform: none !important;
    color: #666 !important;
    font-family: "Arial" !important;
    font-size: 14px !important;
    letter-spacing: 0px !important;
}    
.navbar-default .navbar-nav .active a {
    color: #000 !important;
    border-color: #000 !important;
}
.navbar-default .navbar-nav li a:hover {
    border-color: #000 !important;
}
.anp1-container {
    display: none !important;
}
.main-block .pagination > li > a:hover, .main-block .pagination > li > a:focus, .main-block .pagination > li > span:hover, .main-block .pagination > li > span:focus, .main-block .pagination > .active > a, .main-block .pagination > .active > a:hover, .main-block .pagination > .active > a:focus, .main-block .pagination > .active > span, .main-block .pagination > .active > span:hover, .main-block .pagination > .active > span:focus {
	border-color: #333 !important;
	color: #666 !important;
	background-color: #dcdcdc !important;
}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    font-family: "Arial" !important;
    font-weight: normal !important;
    line-height: 1.1 !important;
    color: inherit !important;
}
h2 {
    font-size: 25px !important;
}
.container-fluid {
    max-width: 800px !important;
}
.col-lg-12 {
	padding-top: 2px !important;
	padding-left: 2px !important;
	padding-right: 2px !important;
	padding-bottom: 2px !important;
	background-color: #999 !important;
}
.main-block .pagination > li > a, .main-block .pagination > li > span {
    color: #000 !important;
	border-color: #000 !important;
    background-color: transparent !important;
}
.main-block .pagination > li > a, .main-block .pagination > .active {
    color: #333 !important;
	border-color: #dcdcdc !important;
    background-color: transparent !important;
}
.main-block .btn-default.btn:hover {
    color: white !important;
    border-color: #666 !important;
    background-color: #666 !important;
}
.main-block .btn-primary.btn:hover {
    color: white !important;
    border-color: #666 !important;
    background-color: #666 !important;
}
.pagination {
    font-family: "Arial" !important;
    font-weight: normal !important;
	font-size: 12px !important;
	text-transform: none !important;
	letter-spacing: 0px !important;
	text-align: center !important;
}
.table > thead > tr > th {
	font-weight: normal !important;
}
img {
    width: 50px;
    height: 50px;
}
.list-articles img {
    width: 50px;
    height: 50px;
}
.carousel img {
    width: auto;
}
.article .article-content .wp-caption img {
    width: 50px;
    height: 50px;
}
.article {
    color: #333 !important;
    border-color: #ddd !important;
    background-color: #fff !important;
}
}
