  src: local('Ubuntu Light'), local('Ubuntu-Light'), url(https://fonts.gstatic.com/s/ubuntu/v9/_aijTyevf54tkVDLy-dlnJBw1xU1rKptJj_0jans920.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
* {
    font-family: 'Ubuntu', sans-serif!important
}


/*Set the color of the menubar items on hover*/

ul.dropdown>*:hover,
.menu-item.hover>a {
    background: 0 0!important;
    background-color: #2ecc71!important;
    color: #fff!important
}

.sub-menu>* {
    background-color: rgba(236, 240, 241, .85)!important;
    /*    Line-height helps the menu items fit better on the screen*/
    line-height: .9!important
}


/*Set the background image of the body to a picture from SubtlePatterns, because a gray background is boring.*/

body {
    left: 0!important;
    background: url("http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/zwartevilt.png") !important
}

#menuarea {
    background: #27ae60!important;
    border: none!important
}

.menu-item>a {
    color: #fff!important
}


/*If I don't set this to have a width of 100%, then when I re-add the information, it won't be properly centered.*/

#imglogo {
    width: 100%
}


/*Hide the default header image, because it contains a lot of unneccessary design elements*/

#imglogo img {
    display: none
}


/*Re-create all of the information that was in the header image (Next 3 bits)*/

#imglogo a:before {
    	color: #f1c40f;

    content: "Mountain Vista High School";
    padding-top: 40px;
    font-size: 2em!important;
    font-family: 'Lato', sans-serif
}

#imglogo a:before,
#imglogo:after {
    display: block;
    width: 100%;
    text-align: center
}

#imglogo:after {
    content: "Highlands Ranch, CO • (303) 387-1500 • Attendance (303) 387-1502";
    font-size: .5em;
    margin: 15px
}


/*A better background color for the page, taken from FlatUIColors.com. A lot of the colors come from there.*/

#contentbox,
#content {
    background: #ecf0f1!important
}


/*Shadows make things look pretty, there's no doubt about it.*/

#content {
    -webkit-box-shadow: 0 6px 20px 10px rgba(0, 0, 0, .75);
    top: 15px
}

#menuarea {
    -webkit-box-shadow: 0 3px 6px 2px rgba(0, 0, 0, .75)
}

.sub-menu {
    -webkit-box-shadow: 0 0 17px 2px rgba(0, 0, 0, .75);
    /*    Scale X so that second-level menus overlap their parent a little bit*/
    -webkit-transform: scaleX(1.1);
    width: auto!important;
    border: 1px solid #000!important;
    border-top: 0!important
}

.menu-item .hover>a {
    -webkit-box-shadow: 0 0 17px 1px rgba(0, 0, 0, .75)
}

.spine {
    -webkit-transition: background-color .3s, color .3s;
    border: 1px solid #555
}

.sub-menu .sub-menu {
    overflow-x: hidden;
    overflow-y: auto;
    max-height: 80vh;
    /*    We want second-level menus to have a border on the top*/
    border-top: 1px solid #000!important
}

.dropdown li {
    border-bottom: 1px solid transparent!important
}

h2 {
    color: #3498db;
    /*    Open Sans would be nice too.*/
    font-family: 'Lato', sans-serif!important
}


/*Can't remember what this does, but I'm sure it's important*/

.slidedeck-overlays {
    display: none!important
}


/*Since we can't put a scrollbar on the first-level menus without weird things happening when you try to go to a second level menu, changing the padding by a little bit makes all the options fit on a computer screen better*/

ul.dropdown ul li a {
    /*    The original style document already has this rule with !important; it only works because Styler adds the stylesheet after the original one, so the rule is overwritten.*/
    padding: 6px 0 7px 6px!important
}


/*There's an annoying little green box at the bottom of the page, and this is the only way I can target it and not the other tables.*/

#content>div>table {
    border: none!important
}


/*Without this the "Click here for all legal issues." link is scraping up against the side of its container.*/

#content>a {
    margin-left: 30px !important;
}

#layout::after {
  content: "Styles by Ekyl. (CC BY-NC-SA) 2016. tiny.cc/bettermvhs";
  color: white;
  margin: 40px auto 20px;
  text-align: center;
  display: block;
}
}
